"use client";

import React, { useState, useEffect } from "react";
import { useParams } from "next/navigation";
import { motion, AnimatePresence } from "framer-motion";
import { createClientComponentClient } from "@supabase/auth-helpers-nextjs";
import { Mail, Briefcase, Calendar, CheckCircle, Clock, AlertCircle, Plus, Send, MoreVertical, Trash2 } from "lucide-react";
import { toast } from "sonner";
import { cn } from "@/lib/utils";

// Types
interface Task {
    id: string;
    title: string;
    description: string;
    status: 'todo' | 'in_progress' | 'review' | 'done';
    priority: 'low' | 'medium' | 'high' | 'urgent';
    due_date: string;
}

interface Message {
    id: string;
    content: string;
    created_at: string;
    sender_type: 'owner' | 'employee';
}

interface Employee {
    id: string;
    name: string;
    email: string;
    role: string;
    status: string;
    avatar_url?: string;
}

export default function EmployeeProfilePage() {
    const { id } = useParams();
    const [employee, setEmployee] = useState<Employee | null>(null);
    const [tasks, setTasks] = useState<Task[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [activeTab, setActiveTab] = useState<'tasks' | 'messages'>('tasks');
    const [newMessage, setNewMessage] = useState("");
    const [showTaskModal, setShowTaskModal] = useState(false);
    const supabase = createClientComponentClient();

    useEffect(() => {
        fetchEmployeeDetails();
        fetchTasks();
        fetchMessages();

        // Subscribe to realtime updates
        const channel = supabase
            .channel(`employee_${id}`)
            .on('postgres_changes', { event: '*', schema: 'public', filter: `assigned_to=eq.${id}`, table: 'employee_tasks' }, fetchTasks)
            .on('postgres_changes', { event: '*', schema: 'public', filter: `recipient_employee_id=eq.${id}`, table: 'employee_messages' }, fetchMessages)
            .subscribe();

        return () => { supabase.removeChannel(channel); };
    }, [id]);

    const fetchEmployeeDetails = async () => {
        const { data } = await supabase.from('employees').select('*').eq('id', id).single();
        if (data) setEmployee(data);
    };

    const fetchTasks = async () => {
        const { data } = await supabase.from('employee_tasks').select('*').eq('assigned_to', id).order('created_at', { ascending: false });
        if (data) setTasks(data);
    };

    const fetchMessages = async () => {
        // In a real app, filtering by recipient + sender logic would be more complex
        const { data } = await supabase.from('employee_messages').select('*').eq('recipient_employee_id', id).order('created_at', { ascending: true });
        if (data) setMessages(data);
    };

    const handleAddTask = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const formData = new FormData(e.currentTarget);
        const newTask = {
            title: formData.get('title'),
            description: formData.get('description'),
            priority: formData.get('priority'),
            due_date: formData.get('due_date'),
            owner_id: (await supabase.auth.getUser()).data.user?.id,
            assigned_to: id,
            status: 'todo'
        };

        const { error } = await supabase.from('employee_tasks').insert(newTask);
        if (error) toast.error("Failed to assign task");
        else {
            toast.success("Task assigned successfully");
            setShowTaskModal(false);
        }
    };

    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!newMessage.trim()) return;

        const { error } = await supabase.from('employee_messages').insert({
            content: newMessage,
            recipient_employee_id: id,
            owner_id: (await supabase.auth.getUser()).data.user?.id,
            sender_type: 'owner'
        });

        if (error) toast.error("Failed to send message");
        else setNewMessage("");
    };

    if (!employee) return <div className="p-8"><div className="w-8 h-8 rounded-full border-2 border-violet-500 border-t-transparent animate-spin" /></div>;

    return (
        <div className="p-6 md:p-8 max-w-7xl mx-auto min-h-screen">
            {/* Header Profile */}
            <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/10 rounded-3xl p-8 mb-8 flex flex-col md:flex-row items-start md:items-center justify-between gap-6 shadow-sm">
                <div className="flex items-center gap-6">
                    <div className="w-20 h-20 rounded-3xl bg-gradient-to-br from-violet-500 to-fuchsia-500 flex items-center justify-center text-white font-bold text-3xl shadow-lg shadow-violet-500/20">
                        {employee.name.charAt(0)}
                    </div>
                    <div>
                        <h1 className="text-3xl font-bold mb-1">{employee.name}</h1>
                        <div className="flex items-center gap-3 text-sm text-muted-foreground">
                            <span className="flex items-center gap-1"><Mail className="w-3 h-3" /> {employee.email}</span>
                            <span className="flex items-center gap-1 bg-black/5 dark:bg-white/5 px-2 py-0.5 rounded-full text-xs font-medium text-foreground"><Briefcase className="w-3 h-3" /> {employee.role}</span>
                        </div>
                    </div>
                </div>
                <div className="flex gap-3">
                    <button className="px-4 py-2 border border-red-500/20 text-red-500 hover:bg-red-500/10 rounded-xl text-sm font-medium flex items-center gap-2 transition-colors">
                        <Trash2 className="w-4 h-4" /> Terminate
                    </button>
                    <button
                        onClick={() => setShowTaskModal(true)}
                        className="px-4 py-2 bg-violet-600 hover:bg-violet-700 text-white rounded-xl text-sm font-medium flex items-center gap-2 shadow-lg shadow-violet-500/20 transition-colors"
                    >
                        <Plus className="w-4 h-4" /> Assign Task
                    </button>
                </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Left Column: Stats & Tasks */}
                <div className="lg:col-span-2 space-y-8">
                    {/* Stats Row */}
                    <div className="grid grid-cols-3 gap-4">
                        <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/5 p-6 rounded-3xl">
                            <div className="flex items-center gap-2 text-muted-foreground mb-2 text-sm">
                                <CheckCircle className="w-4 h-4 text-emerald-500" /> Completed
                            </div>
                            <div className="text-3xl font-bold">{tasks.filter(t => t.status === 'done').length}</div>
                        </div>
                        <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/5 p-6 rounded-3xl">
                            <div className="flex items-center gap-2 text-muted-foreground mb-2 text-sm">
                                <Clock className="w-4 h-4 text-amber-500" /> Pending
                            </div>
                            <div className="text-3xl font-bold">{tasks.filter(t => t.status !== 'done').length}</div>
                        </div>
                        <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/5 p-6 rounded-3xl">
                            <div className="flex items-center gap-2 text-muted-foreground mb-2 text-sm">
                                <AlertCircle className="w-4 h-4 text-red-500" /> Overdue
                            </div>
                            <div className="text-3xl font-bold">0</div>
                        </div>
                    </div>

                    {/* Tasks List */}
                    <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/5 rounded-3xl p-8 min-h-[500px]">
                        <h2 className="text-xl font-bold mb-6 flex items-center gap-2">
                            <Briefcase className="w-5 h-5 text-violet-500" /> Assigned Tasks
                        </h2>
                        <div className="space-y-4">
                            {tasks.length === 0 ? (
                                <div className="text-center py-20 text-muted-foreground">No active tasks assigned.</div>
                            ) : (
                                tasks.map(task => (
                                    <div key={task.id} className="p-4 rounded-2xl bg-black/5 dark:bg-white/[0.02] border border-black/5 dark:border-white/5 flex items-start justify-between group hover:border-violet-500/20 transition-colors">
                                        <div>
                                            <h3 className="font-semibold mb-1">{task.title}</h3>
                                            <p className="text-sm text-muted-foreground mb-3">{task.description}</p>
                                            <div className="flex gap-2">
                                                <span className={cn(
                                                    "text-[10px] font-bold px-2 py-0.5 rounded-full uppercase tracking-wider",
                                                    task.priority === 'urgent' ? 'bg-red-500/10 text-red-500' :
                                                        task.priority === 'high' ? 'bg-orange-500/10 text-orange-500' :
                                                            'bg-blue-500/10 text-blue-500'
                                                )}>
                                                    {task.priority}
                                                </span>
                                                <span className="text-xs text-muted-foreground bg-black/5 dark:bg-white/5 px-2 py-0.5 rounded-full flex items-center gap-1">
                                                    <Calendar className="w-3 h-3" /> {new Date(task.due_date).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="flex flex-col items-end gap-2">
                                            <span className={cn(
                                                "text-xs font-medium px-2 py-1 rounded-lg",
                                                task.status === 'done' ? 'bg-emerald-500/10 text-emerald-500' : 'bg-amber-500/10 text-amber-500'
                                            )}>
                                                {task.status.replace('_', ' ')}
                                            </span>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                </div>

                {/* Right Column: Messages */}
                <div className="bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/5 rounded-3xl p-6 flex flex-col h-[calc(100vh-200px)] sticky top-8">
                    <div className="flex items-center justify-between mb-6 pb-4 border-b border-black/5 dark:border-white/5">
                        <h2 className="text-lg font-bold flex items-center gap-2">
                            <Mail className="w-5 h-5 text-violet-500" /> Messages
                        </h2>
                        <span className="text-xs text-muted-foreground">Direct Chat</span>
                    </div>

                    <div className="flex-1 overflow-y-auto space-y-4 mb-4 pr-2 custom-scrollbar">
                        {messages.length === 0 ? (
                            <div className="text-center text-sm text-muted-foreground py-10">Start a conversation with {employee.name.split(' ')[0]}</div>
                        ) : (
                            messages.map(msg => (
                                <div key={msg.id} className={cn(
                                    "max-w-[85%] p-3 rounded-2xl text-sm",
                                    msg.sender_type === 'owner'
                                        ? "bg-violet-600 text-white ml-auto rounded-br-none"
                                        : "bg-black/5 dark:bg-white/10 rounded-bl-none"
                                )}>
                                    {msg.content}
                                </div>
                            ))
                        )}
                    </div>

                    <form onSubmit={handleSendMessage} className="relative">
                        <input
                            value={newMessage}
                            onChange={(e) => setNewMessage(e.target.value)}
                            placeholder="Type a message..."
                            className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl pl-4 pr-12 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500 text-sm"
                        />
                        <button type="submit" className="absolute right-2 top-2 p-1.5 bg-violet-600 text-white rounded-lg hover:bg-violet-700 transition-colors">
                            <Send className="w-4 h-4" />
                        </button>
                    </form>
                </div>
            </div>

            {/* Task Modal */}
            <AnimatePresence>
                {showTaskModal && (
                    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm">
                        <motion.div
                            initial={{ scale: 0.9, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            exit={{ scale: 0.9, opacity: 0 }}
                            className="bg-white dark:bg-[#0a0a0f] border border-white/10 rounded-3xl p-8 max-w-md w-full shadow-2xl"
                        >
                            <h2 className="text-2xl font-bold mb-6">Assign New Task</h2>
                            <form onSubmit={handleAddTask} className="space-y-4">
                                <div>
                                    <label className="text-sm font-medium mb-1 block">Title</label>
                                    <input name="title" required className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500" placeholder="e.g. Fix Bot Navigation Error" />
                                </div>
                                <div>
                                    <label className="text-sm font-medium mb-1 block">Description</label>
                                    <textarea name="description" rows={3} className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500" placeholder="Details about the task..." />
                                </div>

                                <div className="grid grid-cols-2 gap-4">
                                    <div>
                                        <label className="text-sm font-medium mb-1 block">Due Date</label>
                                        <input name="due_date" type="date" required className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500" />
                                    </div>
                                    <div>
                                        <label className="text-sm font-medium mb-1 block">Priority</label>
                                        <select name="priority" className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500">
                                            <option value="medium">Medium</option>
                                            <option value="high">High</option>
                                            <option value="urgent">Urgent</option>
                                            <option value="low">Low</option>
                                        </select>
                                    </div>
                                </div>

                                <div className="flex gap-3 mt-8">
                                    <button type="button" onClick={() => setShowTaskModal(false)} className="flex-1 py-3 rounded-xl hover:bg-black/5 dark:hover:bg-white/5 font-medium">Cancel</button>
                                    <button type="submit" className="flex-1 py-3 rounded-xl bg-violet-600 hover:bg-violet-700 text-white font-medium shadow-lg shadow-violet-500/20">Assign</button>
                                </div>
                            </form>
                        </motion.div>
                    </div>
                )}
            </AnimatePresence>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Plus, Search, MoreVertical, Mail, Calendar, User, Briefcase, Trash2, Edit2 } from "lucide-react";
import { createClientComponentClient } from "@supabase/auth-helpers-nextjs";
import { cn } from "@/lib/utils";
import Link from "next/link";
import { toast } from "sonner";

// Layout wrapper would be imported here in a real app, assuming this renders inside DashboardLayout

interface Employee {
    id: string;
    name: string;
    email: string;
    role: string;
    status: 'active' | 'inactive' | 'on_leave';
    avatar_url?: string;
    skills?: string[];
}

export default function EmployeesPage() {
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");
    const [showAddModal, setShowAddModal] = useState(false);
    const supabase = createClientComponentClient();

    useEffect(() => {
        fetchEmployees();

        // Subscribe to realtime changes
        const channel = supabase
            .channel('employees_changes')
            .on('postgres_changes', { event: '*', schema: 'public', table: 'employees' }, () => {
                fetchEmployees();
            })
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, []);

    const fetchEmployees = async () => {
        try {
            const { data, error } = await supabase
                .from('employees')
                .select('*')
                .order('created_at', { ascending: false });

            if (error) throw error;
            setEmployees(data || []);
        } catch (error) {
            console.error('Error fetching employees:', error);
            toast.error("Failed to load employees");
        } finally {
            setIsLoading(false);
        }
    };

    const handleAddEmployee = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const formData = new FormData(e.currentTarget);
        const newEmployee = {
            name: formData.get('name') as string,
            email: formData.get('email') as string,
            role: formData.get('role') as string,
            status: 'active',
            owner_id: (await supabase.auth.getUser()).data.user?.id
        };

        try {
            const { error } = await supabase.from('employees').insert(newEmployee);
            if (error) throw error;
            toast.success("Employee added successfully");
            setShowAddModal(false);
        } catch (error) {
            console.error('Error adding employee:', error);
            toast.error("Failed to add employee");
        }
    };

    const filteredEmployees = employees.filter(emp =>
        emp.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emp.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emp.role.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="p-6 md:p-8 max-w-7xl mx-auto min-h-screen">
            <div className="flex flex-col md:flex-row md:items-center justify-between mb-8 gap-4">
                <div>
                    <h1 className="text-3xl font-bold tracking-tight mb-2">My Workforce</h1>
                    <p className="text-muted-foreground">Manage your team of experts and AI handlers.</p>
                </div>
                <button
                    onClick={() => setShowAddModal(true)}
                    className="flex items-center gap-2 px-4 py-2 bg-violet-600 hover:bg-violet-700 text-white rounded-xl font-medium transition-colors shadow-lg shadow-violet-500/20"
                >
                    <Plus className="w-5 h-5" />
                    Hire Employee
                </button>
            </div>

            {/* Search & Filters */}
            <div className="flex items-center gap-4 mb-8 bg-white dark:bg-white/5 p-2 rounded-2xl border border-black/5 dark:border-white/5">
                <Search className="w-5 h-5 text-muted-foreground ml-3" />
                <input
                    type="text"
                    placeholder="Search by name, role, or email..."
                    className="flex-1 bg-transparent border-none focus:outline-none p-2"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
            </div>

            {/* Employees Grid */}
            {isLoading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {[1, 2, 3].map(i => (
                        <div key={i} className="h-64 rounded-3xl bg-black/5 dark:bg-white/5 animate-pulse" />
                    ))}
                </div>
            ) : filteredEmployees.length === 0 ? (
                <div className="text-center py-20 bg-white/5 rounded-3xl border border-dashed border-white/10">
                    <div className="w-16 h-16 rounded-full bg-violet-500/10 flex items-center justify-center mx-auto mb-4">
                        <User className="w-8 h-8 text-violet-500" />
                    </div>
                    <h3 className="text-xl font-semibold mb-2">No employees yet</h3>
                    <p className="text-muted-foreground mb-6">Start building your dream team to manage your bots.</p>
                    <button
                        onClick={() => setShowAddModal(true)}
                        className="text-violet-500 hover:text-violet-400 font-medium"
                    >
                        Add your first employee
                    </button>
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredEmployees.map((emp) => (
                        <Link href={`/dashboard/employees/${emp.id}`} key={emp.id}>
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                whileHover={{ y: -4 }}
                                className="group relative bg-white dark:bg-[#0a0a0f] border border-black/5 dark:border-white/10 rounded-3xl p-6 hover:shadow-xl hover:shadow-black/5 dark:hover:shadow-white/5 transition-all cursor-pointer"
                            >
                                <div className="flex items-start justify-between mb-6">
                                    <div className="flex items-center gap-4">
                                        <div className="w-12 h-12 rounded-2xl bg-gradient-to-br from-violet-500 to-fuchsia-500 flex items-center justify-center text-white font-bold text-lg">
                                            {emp.name.charAt(0)}
                                        </div>
                                        <div>
                                            <h3 className="font-semibold text-lg">{emp.name}</h3>
                                            <span className="text-xs font-medium px-2 py-0.5 rounded-full bg-violet-500/10 text-violet-600 dark:text-violet-400 border border-violet-500/20">
                                                {emp.role}
                                            </span>
                                        </div>
                                    </div>
                                    <div className={`w-3 h-3 rounded-full ${emp.status === 'active' ? 'bg-emerald-500' : 'bg-gray-500'
                                        }`} />
                                </div>

                                <div className="space-y-3 mb-6">
                                    <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                        <Mail className="w-4 h-4" />
                                        {emp.email}
                                    </div>
                                    <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                        <Briefcase className="w-4 h-4" />
                                        <span>0 Active Tasks</span>
                                    </div>
                                </div>

                                <div className="pt-4 border-t border-black/5 dark:border-white/5 flex items-center justify-between">
                                    <span className="text-xs text-muted-foreground">View Profile</span>
                                    <div className="w-8 h-8 rounded-full bg-black/5 dark:bg-white/5 flex items-center justify-center group-hover:bg-violet-500 group-hover:text-white transition-colors">
                                        <MoreVertical className="w-4 h-4" />
                                    </div>
                                </div>
                            </motion.div>
                        </Link>
                    ))}
                </div>
            )}

            {/* Add Employee Modal */}
            <AnimatePresence>
                {showAddModal && (
                    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm">
                        <motion.div
                            initial={{ scale: 0.9, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            exit={{ scale: 0.9, opacity: 0 }}
                            className="bg-white dark:bg-[#0a0a0f] border border-white/10 rounded-3xl p-8 max-w-md w-full shadow-2xl"
                        >
                            <h2 className="text-2xl font-bold mb-6">Hire Employee</h2>
                            <form onSubmit={handleAddEmployee} className="space-y-4">
                                <div>
                                    <label className="text-sm font-medium mb-1 block">Full Name</label>
                                    <input name="name" required className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500" placeholder="e.g. Alex Smith" />
                                </div>
                                <div>
                                    <label className="text-sm font-medium mb-1 block">Email Address</label>
                                    <input name="email" type="email" required className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500" placeholder="alex@bothive.io" />
                                </div>
                                <div>
                                    <label className="text-sm font-medium mb-1 block">Role</label>
                                    <select name="role" className="w-full bg-black/5 dark:bg-white/5 border border-black/10 dark:border-white/10 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-violet-500">
                                        <option value="Developer">Bot Developer</option>
                                        <option value="Manager">Project Manager</option>
                                        <option value="Support">Support Agent</option>
                                        <option value="Analyst">Data Analyst</option>
                                    </select>
                                </div>

                                <div className="flex gap-3 mt-8">
                                    <button type="button" onClick={() => setShowAddModal(false)} className="flex-1 py-3 rounded-xl hover:bg-black/5 dark:hover:bg-white/5 font-medium">Cancel</button>
                                    <button type="submit" className="flex-1 py-3 rounded-xl bg-violet-600 hover:bg-violet-700 text-white font-medium shadow-lg shadow-violet-500/20">Hire Now</button>
                                </div>
                            </form>
                        </motion.div>
                    </div>
                )}
            </AnimatePresence>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useAppSession } from "@/lib/app-session-context";
import { supabase } from "@/lib/supabase";
import {
  Bot,
  Users,
  BarChart3,
  Activity,
  TrendingUp,
  Clock,
  DollarSign,
  Settings,
  Plus,
  MoreHorizontal,
  Zap,
  ArrowUpRight,
  ArrowDownRight
} from "lucide-react";

interface QuickStat {
  title: string;
  value: string;
  change: number;
  icon: React.ElementType;
  color: string;
}

interface RecentActivity {
  id: string;
  type: string;
  description: string;
  timestamp: string;
  status: "success" | "error" | "pending";
}

export default function DashboardHome() {
  const { profile } = useAppSession();
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState<QuickStat[]>([]);
  const [recentActivity, setRecentActivity] = useState<RecentActivity[]>([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch user's bots
      const { data: bots } = await supabase
        .from("bots")
        .select("*")
        .eq("user_id", profile?.id)
        .order("created_at", { ascending: false });

      // Fetch workspaces
      const { data: workspaces } = await supabase
        .from("workspaces")
        .select("*")
        .eq("owner_id", profile?.id);

      // Fetch analytics
      const { data: analytics } = await supabase
        .from("bot_analytics")
        .select("*")
        .eq("user_id", profile?.id)
        .order("execution_time", { ascending: false })
        .limit(5);

      // Mock stats for now
      setStats([
        {
          title: "Total Bots",
          value: (bots?.length || 0).toString(),
          change: 12,
          icon: Bot,
          color: "from-purple-600 to-purple-800"
        },
        {
          title: "Team Members",
          value: "24",
          change: -3,
          icon: Users,
          color: "from-blue-600 to-blue-800"
        },
        {
          title: "API Calls",
          value: "1.2K",
          change: 8,
          icon: Activity,
          color: "from-green-600 to-green-800"
        },
        {
          title: "Monthly Cost",
          value: "$29",
          change: 0,
          icon: DollarSign,
          color: "from-gray-600 to-gray-800"
        }
      ]);

      // Mock recent activity
      setRecentActivity([
        {
          id: "1",
          type: "bot_execution",
          description: "Customer Support Bot executed successfully",
          timestamp: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
          status: "success"
        },
        {
          id: "2",
          type: "workspace_created",
          description: "New workspace 'Marketing Team' created",
          timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
          status: "success"
        },
        {
          id: "3",
          type: "bot_execution",
          description: "Data Analysis Bot failed to execute",
          timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
          status: "error"
        },
        {
          id: "4",
          type: "member_invited",
          description: "Invited john@example.com to workspace",
          timestamp: new Date(Date.now() - 45 * 60 * 1000).toISOString(),
          status: "pending"
        }
      ]);

    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / 60000);
    
    if (diffMins < 1) return "Just now";
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffMins < 1440) return `${Math.floor(diffMins / 60)}h ago`;
    return `${Math.floor(diffMins / 1440)}d ago`;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "success": return "text-green-600 bg-green-50 border-green-200";
      case "error": return "text-red-600 bg-red-50 border-red-200";
      case "pending": return "text-yellow-600 bg-yellow-50 border-yellow-200";
      default: return "text-gray-600 bg-gray-50 border-gray-200";
    }
  };

  if (loading) {
    return (
      <div className="w-full p-6 max-w-full">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="bg-gray-50 rounded-2xl h-32 animate-pulse" />
          ))}
        </div>
        <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
          <div className="xl:col-span-2 bg-gray-50 rounded-2xl h-96 animate-pulse" />
          <div className="bg-gray-50 rounded-2xl h-96 animate-pulse" />
        </div>
      </div>
    );
  }

  return (
    <div className="w-full p-6 max-w-full">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-black mb-2">Welcome back</h1>
        <p className="text-gray-500">Here's what's happening with your bots today</p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        {stats.map((stat, index) => (
          <motion.div
            key={stat.title}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="bg-white border border-gray-200 rounded-2xl p-4 sm:p-6 hover:shadow-lg transition-shadow"
          >
            <div className="flex items-center justify-between mb-4">
              <div className={`p-3 bg-linear-to-r ${stat.color} rounded-xl text-white`}>
                <stat.icon className="w-6 h-6" />
              </div>
              <div className={`flex items-center gap-1 text-sm font-medium ${
                stat.change > 0 ? "text-green-600" : stat.change < 0 ? "text-red-600" : "text-gray-600"
              }`}>
                {stat.change !== 0 && (
                  <>
                    {stat.change > 0 ? <ArrowUpRight className="w-4 h-4" /> : <ArrowDownRight className="w-4 h-4" />}
                    {Math.abs(stat.change)}%
                  </>
                )}
              </div>
            </div>
            <div className="text-2xl font-bold text-black mb-1">{stat.value}</div>
            <div className="text-gray-500 text-sm">{stat.title}</div>
          </motion.div>
        ))}
      </div>

      {/* Main Content */}
      <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
        {/* Recent Activity */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="xl:col-span-2 bg-white border border-gray-200 rounded-2xl p-4 sm:p-6"
        >
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-semibold text-black">Recent Activity</h2>
            <button className="text-sm text-purple-600 hover:text-purple-700 font-medium">
              View All
            </button>
          </div>
          
          <div className="space-y-4">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="flex items-center justify-between p-3 sm:p-4 bg-gray-50 rounded-xl">
                <div className="flex items-center gap-3">
                  <div className={`p-2 rounded-lg ${getStatusColor(activity.status)}`}>
                    <Activity className="w-4 h-4" />
                  </div>
                  <div>
                    <div className="font-medium text-black">{activity.description}</div>
                    <div className="text-sm text-gray-500">{formatTimestamp(activity.timestamp)}</div>
                  </div>
                </div>
                <button className="p-2 hover:bg-gray-200 rounded-lg transition-colors">
                  <MoreHorizontal className="w-4 h-4 text-gray-600" />
                </button>
              </div>
            ))}
          </div>
        </motion.div>

        {/* Quick Actions */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="space-y-6"
        >
          {/* Create Bot */}
          <div className="bg-white border border-gray-200 rounded-2xl p-6">
            <h3 className="text-lg font-semibold text-black mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="w-full flex items-center gap-3 p-4 bg-purple-50 text-purple-600 rounded-xl font-medium hover:bg-purple-100 transition-colors"
              >
                <Plus className="w-5 h-5" />
                Create New Bot
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="w-full flex items-center gap-3 p-4 bg-gray-50 text-black rounded-xl font-medium hover:bg-gray-100 transition-colors"
              >
                <Users className="w-5 h-5" />
                Invite Team Member
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="w-full flex items-center gap-3 p-4 bg-gray-50 text-black rounded-xl font-medium hover:bg-gray-100 transition-colors"
              >
                <BarChart3 className="w-5 h-5" />
                View Analytics
              </motion.button>
            </div>
          </div>

          {/* System Status */}
          <div className="bg-white border border-gray-200 rounded-2xl p-6">
            <h3 className="text-lg font-semibold text-black mb-4">System Status</h3>
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-gray-600">API Status</span>
                <span className="px-2 py-1 bg-green-50 text-green-600 text-xs font-medium rounded-full">
                  Operational
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Bot Runtime</span>
                <span className="px-2 py-1 bg-green-50 text-green-600 text-xs font-medium rounded-full">
                  Healthy
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Database</span>
                <span className="px-2 py-1 bg-green-50 text-green-600 text-xs font-medium rounded-full">
                  Connected
                </span>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    useNodesState,
    useEdgesState,
    addEdge,
    Connection,
    Edge,
    Node,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { Loader2, Database, FileText, Share2 } from 'lucide-react';
import { useTheme } from 'next-themes';

// Node Types
const NODE_TYPES = {
    kb: ({ data }: any) => (
        <div className="px-4 py-2 shadow-xl rounded-xl bg-violet-500/10 border-2 border-violet-500 backdrop-blur-md min-w-[150px] text-center">
            <div className="flex justify-center mb-1">
                <Database className="w-6 h-6 text-violet-500" />
            </div>
            <div className="font-bold text-sm text-foreground">{data.label}</div>
            <div className="text-[10px] text-muted-foreground">{data.count} docs</div>
        </div>
    ),
    doc: ({ data }: any) => (
        <div className="px-3 py-2 shadow-sm rounded-lg bg-card border border-border min-w-[120px] flex items-center gap-2">
            <FileText className="w-4 h-4 text-muted-foreground" />
            <div className="text-xs font-medium truncate max-w-[100px]">{data.label}</div>
        </div>
    ),
};

export default function KnowledgeGraphPage() {
    const { theme } = useTheme();
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [loading, setLoading] = useState(true);

    const onConnect = useCallback(
        (params: Connection) => setEdges((eds) => addEdge(params, eds)),
        [setEdges]
    );

    useEffect(() => {
        fetchGraphData();
    }, []);

    const fetchGraphData = async () => {
        try {
            const res = await fetch('/api/knowledge/bases'); // Reuse existing API
            const data = await res.json();

            if (!data.kbs) return;

            const newNodes: Node[] = [];
            const newEdges: Edge[] = [];
            let yOffset = 0;

            // Simple layout algorithm
            data.kbs.forEach((kb: any, i: number) => {
                const kbId = `kb-${kb.id}`;
                const xPos = 250 * i;

                // KB Node
                newNodes.push({
                    id: kbId,
                    type: 'kb',
                    position: { x: xPos, y: 0 },
                    data: { label: kb.name, count: kb.documents?.[0]?.count || 0 }
                });

                // Simulate Document Nodes (Since standard API might not return all docs detail yet)
                // In a real scenario, we'd fetch docs for each KB
                const docCount = kb.documents?.[0]?.count || 0;
                for (let j = 0; j < Math.min(docCount, 5); j++) {
                    const docId = `doc-${kb.id}-${j}`;
                    newNodes.push({
                        id: docId,
                        type: 'doc',
                        position: { x: xPos + (j % 2 === 0 ? -50 : 50), y: 150 + (j * 60) },
                        data: { label: `Document ${j + 1}` }
                    });

                    newEdges.push({
                        id: `e-${kbId}-${docId}`,
                        source: kbId,
                        target: docId,
                        animated: true,
                        style: { stroke: '#8b5cf6' }
                    });
                }
            });

            setNodes(newNodes);
            setEdges(newEdges);
        } catch (e) {
            console.error(e);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="h-screen w-full bg-background relative overflow-hidden">
            <div className="absolute top-6 left-6 z-10 pointer-events-none">
                <h1 className="text-2xl font-bold flex items-center gap-2">
                    <Share2 className="w-6 h-6 text-violet-500" /> Knowledge Graph
                </h1>
                <p className="text-muted-foreground text-sm">Visualizing your HiveMind's neural pathways.</p>
            </div>

            {loading ? (
                <div className="flex items-center justify-center h-full">
                    <Loader2 className="w-8 h-8 animate-spin text-violet-500" />
                </div>
            ) : (
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    nodeTypes={NODE_TYPES}
                    fitView
                    attributionPosition="bottom-right"
                    className="bg-background"
                >
                    <Background color={theme === 'dark' ? '#333' : '#ddd'} gap={20} />
                    <Controls className="bg-card border-border fill-foreground" />
                    <MiniMap
                        nodeStrokeColor={(n) => {
                            if (n.type === 'kb') return '#8b5cf6';
                            return '#eee';
                        }}
                        nodeColor={(n) => {
                            if (n.type === 'kb') return '#8b5cf6';
                            return '#fff';
                        }}
                        className="bg-card border border-border"
                    />
                </ReactFlow>
            )}
        </div>
    );
}

