];

// Shooting Star Component
function ShootingStar({ delay }: { delay: number }) {
  const [key, setKey] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setKey(k => k + 1);
    }, 6000 + delay * 1000);
    return () => clearInterval(interval);
  }, [delay]);

  return (
    <motion.div
      key={key}
      className="absolute w-[2px] h-[2px] bg-white rounded-full"
      style={{
        left: `${20 + Math.random() * 60}%`,
        top: `${10 + Math.random() * 30}%`,
        boxShadow: '0 0 6px 1px rgba(255,255,255,0.6)',
      }}
      initial={{
        opacity: 0,
        x: 0,
        y: 0,
      }}
      animate={{
        opacity: [0, 1, 1, 0],
        x: 150,
        y: 150,
      }}
      transition={{
        duration: 1.2,
        delay: delay,
        ease: "easeOut",
      }}
    >
      {/* Trail */}
      <div
        className="absolute top-0 right-0 h-[1px] w-[60px] origin-right"
        style={{
          background: 'linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.8) 100%)',
          transform: 'rotate(-45deg) translateX(30px)',
        }}
      />
    </motion.div>
  );
}

// Stars background
function StarsBackground() {
  const stars = Array.from({ length: 50 }, (_, i) => ({
    id: i,
    x: Math.random() * 100,
    y: Math.random() * 100,
    size: 1 + Math.random() * 1.5,
    opacity: 0.2 + Math.random() * 0.4,
  }));

  return (
    <div className="absolute inset-0 overflow-hidden">
      {stars.map(star => (
        <motion.div
          key={star.id}
          className="absolute rounded-full"
          style={{
            left: `${star.x}%`,
            top: `${star.y}%`,
            width: star.size,
            height: star.size,
            background: star.id % 3 === 0 ? 'rgba(167, 139, 250, 0.8)' : 'white', // Some stars are purple
          }}
          animate={{
            opacity: [star.opacity, star.opacity * 0.3, star.opacity],
          }}
          transition={{
            duration: 2 + Math.random() * 3,
            repeat: Infinity,
            delay: Math.random() * 2,
          }}
        />
      ))}

      {/* Shooting stars */}
      <ShootingStar delay={0} />
      <ShootingStar delay={2} />
      <ShootingStar delay={4} />
    </div>
  );
}

