    onBack?: () => void;
}

export default function VisualWorkflowBuilder({ workflowId, onBack }: VisualWorkflowBuilderProps) {
    return (
        <ReactFlowProvider>
            <WorkflowBuilderInner workflowId={workflowId} onBack={onBack} />
        </ReactFlowProvider>
    );
}

function WorkflowBuilderInner({ workflowId, onBack }: VisualWorkflowBuilderProps) {
    const { theme } = useTheme();
    const isDark = theme === "dark";
    const reactFlowInstance = useReactFlow();
    const wrapperRef = useRef<HTMLDivElement>(null);

    // State
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [sidebarOpen, setSidebarOpen] = useState(true);
    const [workflowName, setWorkflowName] = useState('Untitled Swarm');
    const [workflowDescription, setWorkflowDescription] = useState('');
    const [saving, setSaving] = useState(false);
    const [loading, setLoading] = useState(false);
    const [currentWorkflowId, setCurrentWorkflowId] = useState<string | null>(workflowId || null);
    const [activeCategory, setActiveCategory] = useState(nodeCategories[0].id);
    const [searchQuery, setSearchQuery] = useState('');
    const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
    const [connectedIntegrations, setConnectedIntegrations] = useState<string[]>([]);
    const [realUserBots, setRealUserBots] = useState<any[]>([]);

    // Load available data (Integrations + Bots)
    useEffect(() => {
        const fetchData = async () => {
            try {
                // 1. Integrations
                const intRes = await fetch('/api/integrations/status');
                if (intRes.ok) {
                    const data = await intRes.json();
                    setConnectedIntegrations(data.connected || []);
                }

                // 2. Real Bots (for "AI" category)
                const botsRes = await fetch('/api/bots');
                if (botsRes.ok) {
                    const data = await botsRes.json();
                    if (data.bots) {
                        setRealUserBots(data.bots);
                    }
                }
            } catch (err) {
                console.error('Failed to init dashboard data', err);
            }
        };
        fetchData();
    }, []);

    // Merge real bots into categories
    const dynamicNodeCategories = useMemo(() => {
        return nodeCategories.map(category => {
            // Replace Mock AI nodes with Real Bots
            if (category.id === 'ai') {
                // Create nodes for user's bots
                const botNodes = realUserBots.map(bot => ({
                    id: bot.id, // Real UUID
                    label: bot.name,
                    description: bot.description || 'User created bot',
                    icon: 'bot',
                    gradient: 'from-violet-500 to-purple-600',
                    glowColor: 'bg-violet-500',
                    data: { ...bot } // Store full bot data
                }));

                // Fallback if no bots exist yet
                if (botNodes.length === 0) {
                    return {
                        ...category,
                        nodes: [
                            { id: 'create_new_bot', label: 'Create New Bot...', description: 'Go to Marketplace', icon: 'zap', gradient: 'from-gray-500 to-gray-600', glowColor: 'bg-gray-500' },
                            ...category.nodes
                        ]
                    };
                }

                return {
                    ...category,
                    nodes: botNodes // Use ONLY real bots (or maybe keep standard ones too?)
                };
            }
            return category;
        });
    }, [nodeCategories, realUserBots]);

    // Update filter logic to use dynamic list
    const filteredCategories = useMemo(() => {
        const cats = dynamicNodeCategories; // Use the dynamic one
        if (!searchQuery) return cats;

        return cats.map(cat => ({
            ...cat,
            nodes: cat.nodes.filter(node =>
                node.label.toLowerCase().includes(searchQuery.toLowerCase()) ||
                node.description.toLowerCase().includes(searchQuery.toLowerCase())
            )
        })).filter(cat => cat.nodes.length > 0);
    }, [searchQuery, dynamicNodeCategories]);

    // Load workflow if editing existing one
    useEffect(() => {
        if (workflowId && workflowId !== 'new') {
            loadWorkflow(workflowId);
        }
    }, [workflowId]);

    // Track changes
    useEffect(() => {
        if (nodes.length > 0 || edges.length > 0) {
            setHasUnsavedChanges(true);
        }
    }, [nodes, edges]);

    // Edge styling
    const defaultEdgeOptions = useMemo(() => ({
        type: 'smoothstep',
        animated: true,
        style: {
            stroke: isDark ? '#8b5cf6' : '#7c3aed',
            strokeWidth: 2,
        },
        markerEnd: {
            type: MarkerType.ArrowClosed,
            color: isDark ? '#8b5cf6' : '#7c3aed',
        },
    }), [isDark]);

    // Load workflow from API
    const loadWorkflow = async (id: string) => {
        setLoading(true);
        try {
            const response = await fetch(`/api/workflows/${id}`);
            if (response.ok) {
                const { workflow } = await response.json();
                setWorkflowName(workflow.name || 'Untitled Workflow');
                setWorkflowDescription(workflow.description || '');
                setCurrentWorkflowId(workflow.id);

                // Load nodes and edges from configuration
                if (workflow.configuration) {
                    if (workflow.configuration.nodes) {
                        setNodes(workflow.configuration.nodes);
                    }
                    if (workflow.configuration.edges) {
                        setEdges(workflow.configuration.edges);
                    }
                }
                setHasUnsavedChanges(false);
                toast.success('Workflow loaded');
            } else {
                toast.error('Failed to load workflow');
            }
        } catch (error) {
            console.error('Error loading workflow:', error);
            toast.error('Failed to load workflow');
        } finally {
            setLoading(false);
        }
    };

    // Save workflow to API
    const saveWorkflow = async () => {
        setSaving(true);
        try {
            const configuration = { nodes, edges };

            if (currentWorkflowId && currentWorkflowId !== 'new') {
                // Update existing workflow
                const response = await fetch(`/api/workflows/${currentWorkflowId}`, {
                    method: 'PATCH',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        name: workflowName,
                        description: workflowDescription,
                        configuration,
                    }),
                });

                if (response.ok) {
                    setHasUnsavedChanges(false);
                    toast.success('Workflow saved!');
                } else {
                    throw new Error('Failed to save');
                }
            } else {
                // Create new workflow
                const response = await fetch('/api/workflows', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        name: workflowName,
                        description: workflowDescription || 'Created in Visual Builder',
                        configuration,
                    }),
                });

                if (response.ok) {
                    const { workflow } = await response.json();
                    setCurrentWorkflowId(workflow.id);
                    setHasUnsavedChanges(false);
                    toast.success('Workflow created!');
                } else {
                    throw new Error('Failed to create');
                }
            }
        } catch (error) {
            console.error('Error saving workflow:', error);
            toast.error('Failed to save swarm');
        } finally {
            setSaving(false);
        }
    };



    // Drag handlers
    const onDragStart = (event: React.DragEvent, nodeData: any, category: string) => {
        event.dataTransfer.setData('application/reactflow', JSON.stringify({ ...nodeData, category }));
        event.dataTransfer.effectAllowed = 'move';
    };

    const onDragOver = useCallback((event: React.DragEvent) => {
        event.preventDefault();
        event.dataTransfer.dropEffect = 'move';
    }, []);

    const onDrop = useCallback((event: React.DragEvent) => {
        event.preventDefault();

        if (!wrapperRef.current) return;

        const bounds = wrapperRef.current.getBoundingClientRect();
        const dataStr = event.dataTransfer.getData('application/reactflow');

        if (!dataStr) return;

        const nodeData = JSON.parse(dataStr);
        const position = reactFlowInstance.screenToFlowPosition({
            x: event.clientX - bounds.left,
            y: event.clientY - bounds.top,
        });

        const newNode: Node = {
            id: `${nodeData.id}-${Date.now()}`,
            type: 'workflowNode',
            position,
            data: nodeData,
        };

        setNodes((nds) => [...nds, newNode]);
        toast.success(`Added ${nodeData.label}`);
    }, [reactFlowInstance, setNodes]);

    const onConnect = useCallback((params: Connection) => {
        setEdges((eds) => addEdge({ ...params, ...defaultEdgeOptions }, eds));
    }, [setEdges, defaultEdgeOptions]);

    const runWorkflow = async () => {
        if (nodes.length === 0) {
            toast.error('Add some nodes first!');
            return;
        }

        // Save first if there are unsaved changes
        if (hasUnsavedChanges) {
            await saveWorkflow();
        }

        toast.success('Swarm simulation started! Logic is flowing...');

        try {
            const response = await fetch('/api/swarm/execute', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include', // Include authentication cookies
                body: JSON.stringify({ nodes, edges }),
            });

            const data = await response.json();

            if (response.ok) {
                toast.success(`Execution Complete: ${data.executionTime}`);
                data.logs.forEach((log: string, index: number) => {
                    setTimeout(() => toast.message(log), index * 500);
                });
            } else {
                toast.error('Execution failed');
            }
        } catch (error) {
            console.error('Execution error:', error);
            toast.error('Failed to contact Swarm Engine');
        }
    };

    if (loading) {
        return (
            <div className={cn(
                "flex h-[calc(100vh-140px)] w-full items-center justify-center rounded-2xl border",
                isDark ? "bg-[#08080c] border-white/[0.06]" : "bg-gray-50 border-black/[0.06]"
            )}>
                <div className="text-center">
                    <Loader2 className={cn("w-8 h-8 animate-spin mx-auto mb-4", isDark ? "text-white/50" : "text-black/50")} />
                    <p className={isDark ? "text-white/50" : "text-black/50"}>Loading swarm...</p>
                </div>
            </div>
        );
    }

    return (
        <div className={cn(
            "flex h-[calc(100vh-140px)] w-full gap-0 rounded-2xl overflow-hidden border",
            isDark ? "bg-[#08080c] border-white/[0.06]" : "bg-gray-50 border-black/[0.06]"
        )}>
            {/* ================================================================ */}
            {/* SIDEBAR - Node Palette */}
            {/* ================================================================ */}
            <AnimatePresence mode="wait">
                {sidebarOpen && (
                    <motion.aside
                        initial={{ width: 0, opacity: 0 }}
                        animate={{ width: 320, opacity: 1 }}
                        exit={{ width: 0, opacity: 0 }}
                        transition={{ type: "spring", stiffness: 300, damping: 30 }}
                        className={cn(
                            "flex-shrink-0 flex flex-col border-r overflow-hidden",
                            isDark ? "bg-[#0a0a0f] border-white/[0.06]" : "bg-white border-black/[0.06]"
                        )}
                    >
                        {/* Sidebar Header */}
                        <div className={cn(
                            "p-4 border-b",
                            isDark ? "border-white/[0.06]" : "border-black/[0.06]"
                        )}>
                            <div className="flex items-center justify-between mb-4">
                                <div className="flex items-center gap-2">
                                    <div className={cn(
                                        "p-1.5 rounded-lg",
                                        isDark ? "bg-violet-500/10" : "bg-violet-50"
                                    )}>
                                        <Layers className="w-4 h-4 text-violet-500" />
                                    </div>
                                    <span className={cn("font-semibold text-sm", isDark ? "text-white" : "text-black")}>
                                        Components
                                    </span>
                                </div>
                                <button
                                    onClick={() => setSidebarOpen(false)}
                                    className={cn(
                                        "p-1.5 rounded-lg transition-colors",
                                        isDark ? "hover:bg-white/5 text-white/50" : "hover:bg-black/5 text-black/50"
                                    )}
                                >
                                    <X className="w-4 h-4" />
                                </button>
                            </div>

                            {/* Search */}
                            <div className="relative">
                                <Search className={cn(
                                    "absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4",
                                    isDark ? "text-white/40" : "text-black/40"
                                )} />
                                <input
                                    type="text"
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    placeholder="Search components..."
                                    className={cn(
                                        "w-full pl-9 pr-3 py-2.5 rounded-xl text-sm outline-none transition-all border",
                                        isDark
                                            ? "bg-white/[0.03] border-white/[0.08] text-white placeholder:text-white/40 focus:border-violet-500/50"
                                            : "bg-black/[0.02] border-black/[0.08] text-black placeholder:text-black/40 focus:border-violet-500"
                                    )}
                                />
                            </div>
                        </div>

                        {/* Category Tabs */}
                        <div className={cn(
                            "flex gap-1 p-2 border-b overflow-x-auto scrollbar-hide",
                            isDark ? "border-white/[0.06]" : "border-black/[0.06]"
                        )}>
                            {nodeCategories.map((cat) => {
                                const CatIcon = getIcon(cat.icon);
                                return (
                                    <button
                                        key={cat.id}
                                        onClick={() => setActiveCategory(cat.id)}
                                        className={cn(
                                            "flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium whitespace-nowrap transition-all",
                                            activeCategory === cat.id
                                                ? isDark
                                                    ? "bg-violet-500/15 text-violet-400"
                                                    : "bg-violet-100 text-violet-700"
                                                : isDark
                                                    ? "text-white/50 hover:text-white/70 hover:bg-white/5"
                                                    : "text-black/50 hover:text-black/70 hover:bg-black/5"
                                        )}
                                    >
                                        <CatIcon className="w-3.5 h-3.5" />
                                        {cat.name}
                                    </button>
                                );
                            })}
                        </div>

                        {/* Nodes List */}
                        <div className="flex-1 overflow-y-auto p-3 space-y-2">
                            {/* Drag hint */}
                            <div className={cn(
                                "flex items-center gap-2 px-3 py-2 rounded-lg text-xs mb-3",
                                isDark ? "bg-white/[0.03] text-white/50" : "bg-black/[0.02] text-black/50"
                            )}>
                                <MousePointer2 className="w-3.5 h-3.5" />
                                <span>Drag components to canvas</span>
                            </div>

                            {(searchQuery ? filteredCategories : dynamicNodeCategories.filter(c => c.id === activeCategory)).map((category) => (
                                <div key={category.id}>
                                    {searchQuery && (
                                        <div className={cn(
                                            "text-xs font-medium mb-2 px-1",
                                            isDark ? "text-white/50" : "text-black/50"
                                        )}>
                                            {category.name}
                                        </div>
                                    )}
                                    <div className="grid grid-cols-2 gap-2">
                                        {category.nodes.map((node) => {
                                            const NodeIcon = getIcon(node.icon);
                                            return (
                                                <motion.div
                                                    key={node.id}
                                                    draggable
                                                    onDragStart={(e) => onDragStart(e as any, node, category.name)}
                                                    whileHover={{ scale: 1.02, y: -2 }}
                                                    whileTap={{ scale: 0.98 }}
                                                    className={cn(
                                                        "relative p-3 rounded-xl border cursor-grab active:cursor-grabbing transition-all group",
                                                        isDark
                                                            ? "bg-white/[0.02] border-white/[0.08] hover:border-violet-500/30 hover:bg-white/[0.04]"
                                                            : "bg-white border-black/[0.06] hover:border-violet-500/30 hover:shadow-md shadow-sm"
                                                    )}
                                                >
                                                    {/* Icon */}
                                                    <div className={cn(
                                                        "w-9 h-9 rounded-lg flex items-center justify-center mb-2.5 bg-gradient-to-br shadow-lg group-hover:scale-105 transition-transform",
                                                        node.gradient
                                                    )}>
                                                        <NodeIcon className="w-4 h-4 text-white" />
                                                    </div>

                                                    {/* Text */}
                                                    <div className={cn(
                                                        "text-xs font-semibold truncate",
                                                        isDark ? "text-white" : "text-black"
                                                    )}>
                                                        <div className="flex items-center gap-1.5">
                                                            {node.label}
                                                            {category.id === 'integrations' && connectedIntegrations.includes(node.id.toLowerCase()) && (
                                                                <div className="w-1.5 h-1.5 rounded-full bg-emerald-500 animate-pulse" title="Connected" />
                                                            )}
                                                        </div>
                                                    </div>
                                                    <div className={cn(
                                                        "text-[10px] truncate mt-0.5",
                                                        isDark ? "text-white/50" : "text-black/50"
                                                    )}>
                                                        {node.description}
                                                    </div>
                                                </motion.div>
                                            );
                                        })}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </motion.aside>
                )}
            </AnimatePresence>

            {/* ================================================================ */}
            {/* CANVAS AREA */}
            {/* ================================================================ */}
            <div
                ref={wrapperRef}
                className="flex-1 relative"
                onDragOver={onDragOver}
                onDrop={onDrop}
            >
                {/* Subtle gradient background */}
                <div className={cn(
                    "absolute inset-0 pointer-events-none",
                    isDark
                        ? "bg-[radial-gradient(circle_at_center,rgba(139,92,246,0.03)_0%,transparent_70%)]"
                        : "bg-[radial-gradient(circle_at_center,rgba(139,92,246,0.04)_0%,transparent_70%)]"
                )} />

                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    nodeTypes={nodeTypes}
                    defaultEdgeOptions={defaultEdgeOptions}
                    fitView
                    className="!bg-transparent"
                    proOptions={{ hideAttribution: true }}
                >
                    <Background
                        variant={BackgroundVariant.Dots}
                        gap={20}
                        size={1}
                        color={isDark ? 'rgba(255,255,255,0.05)' : 'rgba(0,0,0,0.06)'}
                    />

                    <Controls
                        className={cn(
                            "!border !rounded-xl !overflow-hidden !shadow-lg",
                            isDark
                                ? "!bg-[#0f0f17] !border-white/[0.08] [&>button]:!bg-transparent [&>button]:!border-white/[0.08] [&>button]:!text-white/60 [&>button:hover]:!bg-white/5"
                                : "!bg-white !border-black/[0.08] [&>button]:!bg-transparent [&>button]:!border-black/[0.08] [&>button]:!text-black/60 [&>button:hover]:!bg-black/5"
                        )}
                    />

                    <MiniMap
                        className={cn(
                            "!border !rounded-xl !overflow-hidden !shadow-lg",
                            isDark ? "!bg-[#0f0f17] !border-white/[0.08]" : "!bg-white !border-black/[0.08]"
                        )}
                        nodeColor={isDark ? '#8b5cf6' : '#7c3aed'}
                        maskColor={isDark ? 'rgba(0,0,0,0.8)' : 'rgba(255,255,255,0.8)'}
                    />

                    {/* ============================================================ */}
                    {/* TOP TOOLBAR */}
                    {/* ============================================================ */}
                    <Panel position="top-center" className="!m-4">
                        <motion.div
                            initial={{ y: -20, opacity: 0 }}
                            animate={{ y: 0, opacity: 1 }}
                            className={cn(
                                "flex items-center gap-2 p-2 rounded-2xl border backdrop-blur-xl shadow-2xl",
                                isDark
                                    ? "bg-[#0a0a0f]/80 border-white/[0.08]"
                                    : "bg-white/80 border-black/[0.08]"
                            )}
                        >
                            {/* Toggle Sidebar */}
                            {!sidebarOpen && (
                                <button
                                    onClick={() => setSidebarOpen(true)}
                                    className={cn(
                                        "p-2 rounded-xl transition-all border-r mr-1",
                                        isDark
                                            ? "hover:bg-white/5 text-white/60 border-white/[0.08]"
                                            : "hover:bg-black/5 text-black/60 border-black/[0.08]"
                                    )}
                                >
                                    <Layers className="w-4 h-4" />
                                </button>
                            )}

                            {/* Workflow Name Input */}
                            <input
                                type="text"
                                value={workflowName}
                                onChange={(e) => setWorkflowName(e.target.value)}
                                className={cn(
                                    "px-3 py-1.5 rounded-lg text-sm font-medium bg-transparent outline-none w-48 transition-all border border-transparent",
                                    isDark
                                        ? "text-white hover:bg-white/5 focus:bg-white/5 focus:border-violet-500/30"
                                        : "text-black hover:bg-black/5 focus:bg-black/5 focus:border-violet-500/50"
                                )}
                            />

                            {/* Unsaved indicator */}
                            {hasUnsavedChanges && (
                                <div className={cn(
                                    "flex items-center gap-1.5 px-2 py-1 rounded-lg text-xs",
                                    isDark ? "bg-amber-500/10 text-amber-400" : "bg-amber-50 text-amber-600"
                                )}>
                                    <AlertCircle className="w-3 h-3" />
                                    Unsaved
                                </div>
                            )}

                            <div className={cn(
                                "w-px h-6",
                                isDark ? "bg-white/[0.08]" : "bg-black/[0.08]"
                            )} />

                            {/* Save Button */}
                            <motion.button
                                whileHover={{ scale: 1.02 }}
                                whileTap={{ scale: 0.98 }}
                                onClick={saveWorkflow}
                                disabled={saving}
                                className={cn(
                                    "flex items-center gap-2 px-4 py-2 rounded-xl text-sm font-medium transition-all",
                                    isDark
                                        ? "bg-white text-black hover:bg-white/90"
                                        : "bg-black text-white hover:bg-black/90",
                                    saving && "opacity-50"
                                )}
                            >
                                {saving ? (
                                    <Loader2 className="w-4 h-4 animate-spin" />
                                ) : (
                                    <Save className="w-4 h-4" />
                                )}
                                {saving ? 'Saving...' : 'Save'}
                            </motion.button>

                            {/* Run Button */}
                            <motion.button
                                whileHover={{ scale: 1.02 }}
                                whileTap={{ scale: 0.98 }}
                                onClick={runWorkflow}
                                className={cn(
                                    "flex items-center gap-2 px-4 py-2 rounded-xl text-sm font-medium transition-all border",
                                    isDark
                                        ? "border-emerald-500/30 text-emerald-400 hover:bg-emerald-500/10"
                                        : "border-emerald-500/50 text-emerald-600 hover:bg-emerald-50"
                                )}
                            >
                                <Play className="w-4 h-4" />
                                Test Run
                            </motion.button>

                            {/* Settings */}
                            <button className={cn(
                                "p-2 rounded-xl transition-all",
                                isDark
                                    ? "hover:bg-white/5 text-white/50"
                                    : "hover:bg-black/5 text-black/50"
                            )}>
                                <Settings className="w-4 h-4" />
                            </button>
                        </motion.div>
                    </Panel>

                    {/* Empty State / Autopilot */}
                    {nodes.length === 0 && (
                        <Panel position="bottom-center" className="pointer-events-none mb-10">
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                className="text-center pointer-events-auto"
                            >
                                <div className={cn(
                                    "w-20 h-20 rounded-3xl mx-auto mb-6 flex items-center justify-center backdrop-blur-xl border shadow-xl",
                                    isDark ? "bg-[#0f0f17]/80 border-white/[0.08]" : "bg-white/80 border-black/[0.08]"
                                )}>
                                    <Sparkles className={cn(
                                        "w-10 h-10",
                                        isDark ? "text-violet-400" : "text-violet-600"
                                    )} />
                                </div>
                                <h3 className={cn(
                                    "text-xl font-semibold mb-2",
                                    isDark ? "text-white" : "text-black"
                                )}>
                                    What should the swarm do?
                                </h3>
                                <p className={cn(
                                    "text-sm mb-6 max-w-sm mx-auto",
                                    isDark ? "text-white/50" : "text-black/50"
                                )}>
                                    Describe a goal and I'll draft the workflow for you.
                                </p>

                                {/* Autopilot Input */}
                                <div className="relative max-w-md mx-auto group">
                                    <div className={cn(
                                        "absolute -inset-0.5 rounded-xl opacity-75 blur transition duration-1000 group-hover:duration-200",
                                        "bg-gradient-to-r from-violet-600 to-indigo-600"
                                    )} />
                                    <div className="relative flex items-center">
                                        <input
                                            type="text"
                                            placeholder="e.g. Monitor prices and slack me..."
                                            className={cn(
                                                "w-full px-4 py-3.5 rounded-xl outline-none text-sm transition-all shadow-xl",
                                                isDark
                                                    ? "bg-[#0f0f17] text-white placeholder:text-white/30"
                                                    : "bg-white text-black placeholder:text-black/30"
                                            )}
                                            onKeyDown={async (e) => {
                                                if (e.key === 'Enter') {
                                                    const val = e.currentTarget.value;
                                                    if (!val) return;

                                                    const toastId = toast.loading("Autopilot is thinking...");

                                                    try {
                                                        const res = await fetch('/api/swarm/autopilot', {
                                                            method: 'POST',
                                                            headers: { 'Content-Type': 'application/json' },
                                                            credentials: 'include', // Include authentication cookies
                                                            body: JSON.stringify({
                                                                goal: val,
                                                                availableBots: realUserBots,
                                                                availableIntegrations: connectedIntegrations
                                                            })
                                                        });

                                                        if (!res.ok) throw new Error("Failed to generate");

                                                        const { blueprint } = await res.json();

                                                        if (blueprint && blueprint.nodes && blueprint.edges) {
                                                            setNodes(blueprint.nodes);
                                                            setEdges(blueprint.edges);
                                                            toast.success("Swarm constructed!", { id: toastId });
                                                        } else {
                                                            toast.error("AI returned invalid structure", { id: toastId });
                                                        }
                                                    } catch (err) {
                                                        console.error(err);
                                                        toast.error("Autopilot failed to connect", { id: toastId });
                                                    }
                                                }
                                            }}
                                        />
                                        <button className="absolute right-2 p-1.5 rounded-lg bg-violet-600 text-white hover:bg-violet-700 transition-colors">
                                            <Sparkles className="w-4 h-4" />
                                        </button>
                                    </div>
                                </div>
                            </motion.div>
                        </Panel>
                    )}
                </ReactFlow>
            </div>
        </div>
    );
}

"use client";

import React, { useState, useCallback, useEffect } from 'react';
import ReactFlow, {
    addEdge,
    Background,
    useNodesState,
    useEdgesState,
    Node,
    Connection,
    BackgroundVariant,
    ReactFlowProvider,
    Handle,
    Position,
    useReactFlow,
    MarkerType
} from 'reactflow';
import 'reactflow/dist/style.css';
import Editor from "@monaco-editor/react";
import { motion, AnimatePresence } from 'framer-motion';
import { Bot, Play, Plus, Cpu, Zap, Box, Layers, Terminal, Code, LayoutGrid, Network } from 'lucide-react';
import { cn } from '@/lib/utils';
import { toast } from 'sonner';

// ============================================================================
// PARSER (Regex) - Same as before
// ============================================================================
const parseAgentsFromCode = (code: string) => {
    const agents: { name: string; content: string }[] = [];
    const agentRegex = /agent\s+([A-Za-z0-9_]+)\s*([\s\S]*?)\s*end/g;
    let match;
    while ((match = agentRegex.exec(code)) !== null) {
        agents.push({ name: match[1], content: match[2].trim() });
    }
    return agents;
};

// ============================================================================
// TECH NODE COMPONENT
// ============================================================================
const TechNode = ({ data, selected }: { data: any; selected: boolean }) => {
    return (
        <div className="relative group">
            {selected && (
                <div className="absolute -inset-[2px] bg-cyan-500 rounded-lg blur-[2px] opacity-100" />
            )}

            <div className={cn(
                "relative min-w-[240px] bg-[#09090b] border border-[#27272a] rounded-lg overflow-hidden shadow-2xl transition-all",
                selected ? "border-cyan-500/50" : "hover:border-white/20"
            )}>
                <div className="flex items-center justify-between px-3 py-2 bg-[#18181b] border-b border-[#27272a]">
                    <div className="flex items-center gap-2">
                        <div className={cn("w-2 h-2 rounded-full", data.isActive ? "bg-emerald-500 shadow-[0_0_8px_#10b981]" : "bg-zinc-600")} />
                        <span className="text-[11px] font-mono text-zinc-400 uppercase tracking-wider">ID: {data.id?.substring(0, 4)}</span>
                    </div>
                    <Bot className="w-3 h-3 text-zinc-500" />
                </div>
                <div className="p-4">
                    <div className="flex items-start gap-4 mb-3">
                        <div className={cn("p-2.5 rounded bg-zinc-900 border border-zinc-800", data.color || "text-cyan-400")}>
                            <Cpu className="w-5 h-5" />
                        </div>
                        <div>
                            <h3 className="text-sm font-bold text-white font-mono tracking-tight">{data.label}</h3>
                            <p className="text-[10px] text-zinc-500 mt-1 uppercase tracking-wide">Autonomous Agent</p>
                        </div>
                    </div>
                    <div className="bg-[#000] border border-zinc-800 rounded p-2 font-mono text-[10px] text-zinc-400 h-[60px] overflow-hidden leading-relaxed opacity-80 relative">
                        <div className="absolute top-0 right-0 p-1 opacity-20"><Terminal className="w-3 h-3" /></div>
                        <span className="text-purple-400">agent</span> <span className="text-yellow-200">{data.label}</span>
                        <div className="opacity-70 mt-1">{data.codeSnippet?.substring(0, 60)}...</div>
                    </div>
                </div>
                <div className="px-3 py-1.5 bg-[#000] border-t border-[#27272a] flex items-center justify-between">
                    <span className="text-[9px] text-zinc-600 font-mono">STATUS: {data.isActive ? "EXECUTING" : "IDLE"}</span>
                    {data.isActive && <Zap className="w-3 h-3 text-yellow-400 fill-yellow-400 animate-pulse" />}
                </div>
                <Handle type="target" position={Position.Top} className="!w-2 !h-2 !bg-cyan-500 !border-none !rounded-none" />
                <Handle type="source" position={Position.Bottom} className="!w-2 !h-2 !bg-cyan-500 !border-none !rounded-none" />
            </div>
        </div>
    );
};

const nodeTypes = { agentNode: TechNode };

interface SwarmComposerProps {
    value: string;
    onChange: (value: string) => void;
}

export default function SwarmComposer({ value, onChange }: SwarmComposerProps) {
    return (
        <ReactFlowProvider>
            <SwarmComposerInner value={value} onChange={onChange} />
        </ReactFlowProvider>
    );
}

function SwarmComposerInner({ value, onChange }: SwarmComposerProps) {
    // Props replace local state for 'code'
    const code = value;
    const setCode = onChange;

    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [isSimulating, setIsSimulating] = useState(false);
    const [viewMode, setViewMode] = useState<'visual' | 'code' | 'architect'>('architect');

    // Sync Code to Nodes
    useEffect(() => {
        const agents = parseAgentsFromCode(code);

        const newNodes = agents.map((agent, index) => {
            const existingNode = nodes.find(n => n.id === agent.name);
            return {
                id: agent.name,
                type: 'agentNode',
                position: existingNode?.position || { x: 50 + (index * 280), y: 150 + (index % 2 === 0 ? 0 : 100) },
                data: {
                    id: agent.name,
                    label: agent.name,
                    codeSnippet: agent.content,
                    isActive: false,
                    color: index === 0 ? "text-cyan-400" : index === 1 ? "text-purple-400" : "text-emerald-400"
                }
            };
        });

        // Main Bot Node assumption based on standard HiveLang 'bot Name'
        // We'll use a generic "Swarm Orchestrator" node if we can't find the bot name, 
        // or just rely on the 'bot' declaration line.
        const botNameMatch = code.match(/bot\s+([A-Za-z0-9_]+)/);
        const botName = botNameMatch ? botNameMatch[1] : "HiveSwarm";

        if (!newNodes.find(n => n.id === botName)) {
            newNodes.unshift({
                id: botName,
                type: 'agentNode',
                position: { x: 350, y: 0 },
                data: { id: botName, label: botName, role: 'Orchestrator', codeSnippet: 'on input...', color: "text-amber-400" }
            });
        }

        // Simple check to avoid infinite loop
        if (newNodes.length !== nodes.length || newNodes.some((n, i) => n.id !== nodes[i]?.id)) {
            setNodes(newNodes);
            const newEdges = agents.map(a => ({
                id: `e-${botName}-${a.name}`,
                source: botName,
                target: a.name,
                animated: true,
                style: { stroke: '#06b6d4', strokeWidth: 1.5 },
                type: 'smoothstep',
                markerEnd: { type: MarkerType.ArrowClosed, color: '#06b6d4' }
            }));
            setEdges(newEdges);
        }
    }, [code]);

    const addNewAgent = () => {
        const newAgentName = `Agent_${Math.floor(Math.random() * 1000)}`;
        const newBlock = `\n  agent ${newAgentName}\n    on input\n      say "I am ready."\n    end\n  end`;
        const lastEndIndex = code.lastIndexOf('end');
        if (lastEndIndex !== -1) {
            // Basic insertion logic
            const newCode = code.slice(0, lastEndIndex) + newBlock + "\n" + code.slice(lastEndIndex);
            setCode(newCode);
            toast.success("Agent Module Initialized");
        } else {
            // Fallback if structure is broken
            setCode(code + newBlock);
        }
    };

    const runSimulation = async () => {
        setIsSimulating(true);
        // Sequential simulation
        const botNameMatch = code.match(/bot\s+([A-Za-z0-9_]+)/);
        const botName = botNameMatch ? botNameMatch[1] : "HiveSwarm";

        const sequence = [botName, ...nodes.filter(n => n.id !== botName).map(n => n.id)];

        for (const id of sequence) {
            setNodes(nds => nds.map(n => n.id === id ? { ...n, data: { ...n.data, isActive: true } } : { ...n, data: { ...n.data, isActive: false } }));
            await new Promise(r => setTimeout(r, 600));
        }
        // Reset
        setNodes(nds => nds.map(n => ({ ...n, data: { ...n.data, isActive: false } })));
        setIsSimulating(false);
    };

    const agentsList = parseAgentsFromCode(code);
    const botNameMatch = code.match(/bot\s+([A-Za-z0-9_]+)/);
    const botName = botNameMatch ? botNameMatch[1] : "HiveSwarm";

    return (
        <div className="flex flex-col h-full w-full bg-[#0a0a0f] text-white font-sans overflow-hidden border border-white/10 rounded-2xl">
            {/* Swarm Toolbar */}
            <div className="h-12 border-b border-white/10 bg-[#0a0a0f] flex items-center justify-between px-4">
                <div className="flex items-center gap-2 text-sm font-medium text-white/50">
                    <Layers className="w-4 h-4 text-cyan-500" />
                    <span>Swarm Composer</span>
                </div>

                <div className="flex bg-white/5 p-1 rounded-lg">
                    <button onClick={() => setViewMode('architect')} className={cn("flex items-center gap-2 px-3 py-1 rounded text-xs font-medium transition-all", viewMode === 'architect' ? "bg-zinc-800 text-white shadow-sm" : "text-zinc-400 hover:text-white")}>
                        <LayoutGrid className="w-3 h-3" /> Architect
                    </button>
                    <button onClick={() => setViewMode('visual')} className={cn("flex items-center gap-2 px-3 py-1 rounded text-xs font-medium transition-all", viewMode === 'visual' ? "bg-zinc-800 text-white shadow-sm" : "text-zinc-400 hover:text-white")}>
                        <Network className="w-3 h-3" /> Graph
                    </button>
                    <button onClick={() => setViewMode('code')} className={cn("flex items-center gap-2 px-3 py-1 rounded text-xs font-medium transition-all", viewMode === 'code' ? "bg-zinc-800 text-white shadow-sm" : "text-zinc-400 hover:text-white")}>
                        <Code className="w-3 h-3" /> Code
                    </button>
                </div>

                <button onClick={runSimulation} className="text-[10px] font-mono text-emerald-400 flex items-center gap-1.5 hover:bg-emerald-500/10 px-2 py-1 rounded border border-transparent hover:border-emerald-500/30 transition-all">
                    {isSimulating ? <Zap className="w-3 h-3 animate-spin" /> : <Play className="w-3 h-3" />}
                    {isSimulating ? "RUNNING..." : "TEST SWARM"}
                </button>
            </div>

            <div className="flex-1 overflow-hidden relative bg-[#0a0a0f]">
                {/* MODE 1: ARCHITECT */}
                {viewMode === 'architect' && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="h-full overflow-y-auto p-6 scrollbar-thin scrollbar-thumb-zinc-800">
                        <div className="flex items-center justify-between mb-6">
                            <div>
                                <h3 className="text-xl font-light text-white">Active Agents</h3>
                                <p className="text-white/40 text-xs">Manage the specialized units in your swarm.</p>
                            </div>
                            <button onClick={addNewAgent} className="flex items-center gap-2 px-3 py-1.5 bg-white text-black rounded-lg text-xs font-bold hover:bg-zinc-200 transition-colors uppercase tracking-wide">
                                <Plus className="w-3 h-3" /> Add Unit
                            </button>
                        </div>

                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                            {/* Queen Bee Card */}
                            <div className="p-5 rounded-xl bg-gradient-to-br from-amber-500/5 to-transparent border border-amber-500/20 hover:border-amber-500/40 transition-all group">
                                <div className="flex items-center justify-between mb-3">
                                    <div className="flex items-center gap-3">
                                        <div className="p-1.5 bg-amber-500/10 rounded text-amber-500"><Layers className="w-4 h-4" /></div>
                                        <span className="font-semibold text-white tracking-tight">{botName}</span>
                                    </div>
                                    <span className="text-[9px] uppercase tracking-wider text-amber-500 font-mono">Orchestrator</span>
                                </div>
                                <div className="h-px w-full bg-amber-500/10 my-3" />
                                <div className="space-y-2">
                                    <div className="flex justify-between text-xs text-zinc-400">
                                        <span>Delegates to:</span>
                                        <span className="text-white">{agentsList.length} Agents</span>
                                    </div>
                                </div>
                            </div>

                            {/* Agents Cards */}
                            {agentsList.map((agent, i) => (
                                <div key={i} className="p-5 rounded-xl bg-zinc-900/40 border border-white/5 hover:border-cyan-500/30 hover:bg-zinc-900/60 transition-all group">
                                    <div className="flex items-center justify-between mb-3">
                                        <div className="flex items-center gap-3">
                                            <div className="p-1.5 bg-zinc-800 rounded text-zinc-400 group-hover:text-cyan-400 transition-colors"><Bot className="w-4 h-4" /></div>
                                            <span className="font-semibold text-white tracking-tight">{agent.name}</span>
                                        </div>
                                        <span className="text-[9px] uppercase tracking-wider text-zinc-500 font-mono bg-zinc-900 px-2 py-1 rounded">Agent</span>
                                    </div>
                                    <div className="h-px w-full bg-white/5 my-3" />
                                    <div className="text-[10px] font-mono text-zinc-500 truncate bg-black/40 p-2 rounded border border-white/5">
                                        {agent.content.split('\n')[0].substring(0, 40)}...
                                    </div>
                                </div>
                            ))}

                            {/* Add Button */}
                            <button onClick={addNewAgent} className="p-5 rounded-xl border border-dashed border-zinc-800 hover:border-zinc-700 hover:bg-zinc-900/20 transition-all flex flex-col items-center justify-center gap-2 text-zinc-500 hover:text-zinc-300">
                                <Plus className="w-5 h-5 opacity-50" />
                                <span className="text-xs font-medium">Add New Agent Module</span>
                            </button>
                        </div>
                    </motion.div>
                )}

                {/* MODE 2: VISUAL */}
                <div className={cn("absolute inset-0 transition-opacity duration-300", viewMode === 'visual' ? "opacity-100 z-10" : "opacity-0 -z-10")}>
                    <ReactFlow
                        nodes={nodes}
                        edges={edges}
                        onNodesChange={onNodesChange}
                        onEdgesChange={onEdgesChange}
                        nodeTypes={nodeTypes}
                        fitView
                        className="bg-[#0a0a0f]"
                        defaultEdgeOptions={{ type: 'smoothstep', animated: true }}
                    >
                        <Background variant={BackgroundVariant.Dots} gap={24} size={1} color="#3f3f46" />
                    </ReactFlow>
                </div>

                {/* MODE 3: CODE */}
                <div className={cn("absolute inset-0 bg-[#0a0a0f] transition-opacity duration-300", viewMode === 'code' ? "opacity-100 z-10" : "opacity-0 -z-10")}>
                    <Editor
                        height="100%"
                        defaultLanguage="ruby"
                        value={code}
                        onChange={(val) => setCode(val || "")}
                        theme="vs-dark"
                        options={{
                            minimap: { enabled: true },
                            fontSize: 13,
                            fontFamily: "'JetBrains Mono', monospace",
                            padding: { top: 24 }
                        }}
                    />
                </div>
            </div>
        </div>
    );
}

