                  ðŸ’­ { step.thought?.substring(0, 60) } {
                    step.thought?.length \u003e 60 ?\"...\" : \"\"}
                    \u003c / div\u003e
                    \u003c / div\u003e
            ))
                }
                {
                    testReactSteps.length \u003e 3 && (
                    \u003cdiv className =\"text-[10px] text-white/40\"\u003e
                        + { testReactSteps.length - 3 } more steps...
                    \u003c / div\u003e
            )
                }
                \u003c / div\u003e
                \u003c / div\u003e
      )
