"use client";

import React, { useState } from "react";
import { motion } from "framer-motion";
import { Mail, MessageSquare, MapPin, Phone, Send, ArrowRight, Clock, CheckCircle } from "lucide-react";
import Navbar2 from "@/components/Navbar2";
import Footer from "@/components/Footer";

const CONTACT_METHODS = [
  {
    icon: Mail,
    title: "Email us",
    description: "Our team typically responds within 24 hours.",
    contact: "hello@bothive.io",
    action: "mailto:hello@bothive.io",
  },
  {
    icon: MessageSquare,
    title: "Live chat",
    description: "Available Mon-Fri, 9am-6pm WAT.",
    contact: "Start a conversation",
    action: "#chat",
  },
  {
    icon: MapPin,
    title: "Office",
    description: "Visit us at our headquarters.",
    contact: "Lagos, Nigeria",
    action: "#",
  },
];

export default function ContactPage() {
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1500));

    setIsLoading(false);
    setIsSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-[#fafafa] dark:bg-[#030014] text-[#0a0a0f] dark:text-white">
      <Navbar2 />

      {/* Hero */}
      <section className="pt-32 pb-16 px-6">
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <span className="inline-flex items-center gap-2 px-3 py-1.5 rounded-full border border-violet-500/20 bg-violet-500/5 text-xs font-medium text-violet-600 dark:text-violet-400 mb-4">
              Contact
            </span>
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-semibold tracking-tight mb-4">
              Get in touch
            </h1>
            <p className="text-lg text-black/50 dark:text-white/50 max-w-xl mx-auto">
              Have questions about BotHive? We'd love to hear from you. Send us a message and we'll respond as soon as possible.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Contact Methods */}
      <section className="pb-16 px-6">
        <div className="max-w-4xl mx-auto">
          <div className="grid md:grid-cols-3 gap-6">
            {CONTACT_METHODS.map((method, i) => (
              <motion.a
                key={method.title}
                href={method.action}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: i * 0.1 }}
                className="group p-6 rounded-2xl border border-black/5 dark:border-white/5 bg-white dark:bg-white/[0.02] hover:border-violet-500/20 transition-colors"
              >
                <div className="w-12 h-12 rounded-xl bg-violet-500/10 flex items-center justify-center mb-4 group-hover:bg-violet-500/20 transition-colors">
                  <method.icon className="w-5 h-5 text-violet-600 dark:text-violet-400" />
                </div>
                <h3 className="font-semibold mb-1">{method.title}</h3>
                <p className="text-sm text-black/50 dark:text-white/50 mb-3">{method.description}</p>
                <span className="text-sm font-medium text-violet-600 dark:text-violet-400 flex items-center gap-1 group-hover:gap-2 transition-all">
                  {method.contact} <ArrowRight className="w-3 h-3" />
                </span>
              </motion.a>
            ))}
          </div>
        </div>
      </section>

      {/* Contact Form */}
      <section className="pb-24 px-6">
        <div className="max-w-2xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="p-8 rounded-2xl border border-black/5 dark:border-white/5 bg-white dark:bg-white/[0.02]"
          >
            {isSubmitted ? (
              <div className="text-center py-12">
                <div className="w-16 h-16 rounded-full bg-emerald-500/10 flex items-center justify-center mx-auto mb-4">
                  <CheckCircle className="w-8 h-8 text-emerald-500" />
                </div>
                <h3 className="text-xl font-semibold mb-2">Message sent!</h3>
                <p className="text-black/50 dark:text-white/50">
                  Thank you for reaching out. We'll get back to you within 24 hours.
                </p>
              </div>
            ) : (
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium mb-2">Name</label>
                    <input
                      type="text"
                      required
                      value={formState.name}
                      onChange={(e) => setFormState({ ...formState, name: e.target.value })}
                      className="w-full px-4 py-3 rounded-xl border border-black/10 dark:border-white/10 bg-transparent text-sm focus:outline-none focus:ring-2 focus:ring-violet-500"
                      placeholder="Your name"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Email</label>
                    <input
                      type="email"
                      required
                      value={formState.email}
                      onChange={(e) => setFormState({ ...formState, email: e.target.value })}
                      className="w-full px-4 py-3 rounded-xl border border-black/10 dark:border-white/10 bg-transparent text-sm focus:outline-none focus:ring-2 focus:ring-violet-500"
                      placeholder="you@example.com"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Subject</label>
                  <select
                    required
                    value={formState.subject}
                    onChange={(e) => setFormState({ ...formState, subject: e.target.value })}
                    className="w-full px-4 py-3 rounded-xl border border-black/10 dark:border-white/10 bg-transparent text-sm focus:outline-none focus:ring-2 focus:ring-violet-500"
                  >
                    <option value="">Select a topic</option>
                    <option value="general">General Inquiry</option>
                    <option value="sales">Sales & Pricing</option>
                    <option value="support">Technical Support</option>
                    <option value="partnership">Partnership</option>
                    <option value="press">Press & Media</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Message</label>
                  <textarea
                    required
                    rows={5}
                    value={formState.message}
                    onChange={(e) => setFormState({ ...formState, message: e.target.value })}
                    className="w-full px-4 py-3 rounded-xl border border-black/10 dark:border-white/10 bg-transparent text-sm focus:outline-none focus:ring-2 focus:ring-violet-500 resize-none"
                    placeholder="How can we help?"
                  />
                </div>

                <button
                  type="submit"
                  disabled={isLoading}
                  className="w-full flex items-center justify-center gap-2 px-6 py-4 rounded-xl bg-[#0a0a0f] dark:bg-white text-white dark:text-black text-sm font-medium hover:opacity-90 transition-opacity disabled:opacity-50"
                >
                  {isLoading ? (
                    <div className="w-5 h-5 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                  ) : (
                    <>
                      Send message
                      <Send className="w-4 h-4" />
                    </>
                  )}
                </button>
              </form>
            )}
          </motion.div>
        </div>
      </section>

      {/* FAQ Link */}
      <section className="pb-24 px-6">
        <div className="max-w-4xl mx-auto text-center">
          <p className="text-black/50 dark:text-white/50 mb-4">
            Looking for quick answers?
          </p>
          <a
            href="/#faq"
            className="inline-flex items-center gap-2 text-violet-600 dark:text-violet-400 font-medium hover:underline"
          >
            Check our FAQ <ArrowRight className="w-4 h-4" />
          </a>
        </div>
      </section>

      <Footer />
    </div>
  );
}

"use client";

import { useEffect, useState } from "react";
import { useTheme } from "@/lib/theme-context";
import { DashboardPageShell } from "@/components/DashboardPageShell";
import { createClientComponentClient } from "@supabase/auth-helpers-nextjs";
import { Loader2, CheckCircle2, XCircle, Clock, Bot, User, Plug } from "lucide-react";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

interface PendingItem {
    id: string;
    name: string;
    description: string;
    status: string;
    created_at: string;
    user_id: string;
    category?: string; // For integrations
    type?: string;     // For integrations
    author_email?: string;
    source: 'bots' | 'integrations';
}

export default function AdminApprovalsPage() {
    const { theme } = useTheme();
    const supabase = createClientComponentClient();
    const [activeTab, setActiveTab] = useState<'bots' | 'integrations'>('bots');
    const [pendingItems, setPendingItems] = useState<PendingItem[]>([]);
    const [loading, setLoading] = useState(true);
    const [actionLoading, setActionLoading] = useState<string | null>(null);

    // Fetch pending items (bots or integrations)
    const fetchPendingItems = async () => {
        try {
            setLoading(true);
            setPendingItems([]); // Clear current while fetching

            let items: any[] = [];
            let table = activeTab === 'bots' ? 'bots' : 'integrations';

            const query = supabase
                .from(table)
                .select('*')
                .order('created_at', { ascending: false });

            // Handle different schemas for bots vs integrations
            if (activeTab === 'bots') {
                // New schema uses approval_status
                query.eq('approval_status', 'pending_review');
            } else {
                // Integrations might still use the old status
                query.in('status', ['pending', 'beta']);
            }

            console.log(`[Admin] Fetching ${table} pending items...`);
            const { data, error } = await query;

            if (error) {
                console.error("[Admin] Supabase error:", error);
                throw error;
            }

            console.log(`[Admin] Fetched ${data?.length} items from ${table}`, data);

            if (!data) return;

            items = data.map(item => ({ ...item, source: activeTab }));

            // Fetch authors
            if (items.length > 0) {
                const userIds = Array.from(new Set(items.map(b => b.user_id)));
                const { data: users } = await supabase
                    .from('users')
                    .select('id, email')
                    .in('id', userIds);

                // Map authors to items
                const userMap = new Map(users?.map(u => [u.id, u]) || []);
                items = items.map(item => ({
                    ...item,
                    author_email: userMap.get(item.user_id)?.email || 'Unknown'
                }));
            }

            setPendingItems(items);
        } catch (error) {
            console.error("Error fetching pending items:", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPendingItems();
    }, [activeTab]);

    const handleApproval = async (itemId: string, approve: boolean) => {
        try {
            setActionLoading(itemId);
            const table = activeTab === 'bots' ? 'bots' : 'integrations';
            
            // Prepare update payload
            // Constraint valid_status: pending, approved, rejected
            // RLS requires: status = 'approved' AND is_published = true
            const updates = approve 
                ? { 
                    approval_status: 'approved', 
                    status: 'approved',
                    is_published: true,
                    approved_at: new Date().toISOString()
                  }
                : { 
                    approval_status: 'rejected', 
                    status: 'rejected',
                    is_published: false,
                    rejection_reason: "Does not meet guidelines"
                  };

            const { error } = await supabase
                .from(table)
                .update(updates)
                .eq('id', itemId);

            if (error) throw error;

            // Remove from list
            setPendingItems(prev => prev.filter(item => item.id !== itemId));

        } catch (error: any) {
            console.error(`Error ${approve ? 'approving' : 'rejecting'} item:`, error);
            alert(`Failed to update status: ${error.message || "Unknown error"}`);
        } finally {
            setActionLoading(null);
        }
    };

    const EmptyState = () => (
        <div className="flex flex-col items-center justify-center p-12 text-center rounded-2xl border border-dashed border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900/50">
            <div className="w-16 h-16 bg-neutral-100 dark:bg-neutral-800 rounded-full flex items-center justify-center mb-4 text-neutral-400">
                {activeTab === 'bots' ? <Bot className="w-8 h-8" /> : <Plug className="w-8 h-8" />}
            </div>
            <h3 className="text-xl font-medium text-neutral-900 dark:text-white mb-2">
                No Pending {activeTab === 'bots' ? 'Bots' : 'Integrations'}
            </h3>
            <p className="text-neutral-500 dark:text-neutral-400 max-w-sm">
                All submissions have been reviewed.
            </p>
        </div>
    );

    return (
        <DashboardPageShell
            title="Pending Approvals"
            description="Review and approve submissions from the community."
        >
            <div className="max-w-7xl mx-auto space-y-6">

                {/* Tabs */}
                <div className="flex gap-4 border-b border-neutral-200 dark:border-neutral-800">
                    <button
                        onClick={() => setActiveTab('bots')}
                        className={cn(
                            "px-4 py-3 text-sm font-medium transition-colors relative flex items-center gap-2",
                            activeTab === 'bots' ? "text-[#6C43FF]" : "text-neutral-500 hover:text-neutral-700 dark:text-neutral-400 dark:hover:text-white"
                        )}
                    >
                        <Bot className="w-4 h-4" />
                        Bots
                        {activeTab === 'bots' && <motion.div layoutId="tab-underline" className="absolute bottom-0 left-0 right-0 h-0.5 bg-[#6C43FF]" />}
                    </button>
                    <button
                        onClick={() => setActiveTab('integrations')}
                        className={cn(
                            "px-4 py-3 text-sm font-medium transition-colors relative flex items-center gap-2",
                            activeTab === 'integrations' ? "text-[#6C43FF]" : "text-neutral-500 hover:text-neutral-700 dark:text-neutral-400 dark:hover:text-white"
                        )}
                    >
                        <Plug className="w-4 h-4" />
                        Integrations
                        {activeTab === 'integrations' && <motion.div layoutId="tab-underline" className="absolute bottom-0 left-0 right-0 h-0.5 bg-[#6C43FF]" />}
                    </button>
                </div>

                {loading ? (
                    <div className="flex items-center justify-center min-h-[400px]">
                        <Loader2 className="w-8 h-8 animate-spin text-[#6C43FF]" />
                    </div>
                ) : pendingItems.length === 0 ? (
                    <EmptyState />
                ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {pendingItems.map((item) => (
                            <div
                                key={item.id}
                                className="group relative bg-white dark:bg-neutral-900 border border-neutral-200 dark:border-neutral-800 rounded-xl overflow-hidden hover:shadow-lg transition-all duration-300"
                            >
                                {/* Status Badge */}
                                <div className="absolute top-4 right-4 z-10 px-3 py-1 rounded-full text-xs font-medium bg-yellow-500/10 text-yellow-600 dark:text-yellow-400 border border-yellow-500/20 flex items-center gap-1">
                                    <Clock className="w-3 h-3" />
                                    Pending
                                </div>

                                <div className="p-6">
                                    <div className="flex items-start justify-between mb-4">
                                        <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-[#6C43FF] to-[#8A63FF] flex items-center justify-center text-white">
                                            {item.source === 'bots' ? <Bot className="w-6 h-6" /> : <Plug className="w-6 h-6" />}
                                        </div>
                                    </div>

                                    <h3 className="text-lg font-bold text-neutral-900 dark:text-white mb-2 line-clamp-1">
                                        {item.name}
                                    </h3>
                                    <p className="text-sm text-neutral-500 dark:text-neutral-400 mb-4 line-clamp-2 h-10">
                                        {item.description || "No description provided."}
                                    </p>

                                    {item.category && (
                                        <div className="mb-4">
                                            <span className="text-xs px-2 py-1 rounded bg-neutral-100 dark:bg-neutral-800 text-neutral-600 dark:text-neutral-400">
                                                {item.category}
                                            </span>
                                        </div>
                                    )}

                                    <div className="flex items-center gap-2 text-xs text-neutral-500 dark:text-neutral-500 mb-6">
                                        <User className="w-3 h-3" />
                                        <span className="truncate">{item.author_email}</span>
                                        <span>â€¢</span>
                                        <span>{new Date(item.created_at).toLocaleDateString()}</span>
                                    </div>

                                    <div className="grid grid-cols-2 gap-3">
                                        <button
                                            onClick={() => handleApproval(item.id, false)}
                                            disabled={actionLoading === item.id}
                                            className="flex items-center justify-center gap-2 px-4 py-2 rounded-lg border border-red-200 dark:border-red-900/30 bg-red-50 dark:bg-red-900/10 text-red-600 dark:text-red-400 hover:bg-red-100 dark:hover:bg-red-900/20 transition-colors disabled:opacity-50"
                                        >
                                            {actionLoading === item.id ? <Loader2 className="w-4 h-4 animate-spin" /> : <XCircle className="w-4 h-4" />}
                                            Reject
                                        </button>
                                        <button
                                            onClick={() => handleApproval(item.id, true)}
                                            disabled={actionLoading === item.id}
                                            className="flex items-center justify-center gap-2 px-4 py-2 rounded-lg bg-green-600 text-white hover:bg-green-700 transition-colors disabled:opacity-50"
                                        >
                                            {actionLoading === item.id ? <Loader2 className="w-4 h-4 animate-spin" /> : <CheckCircle2 className="w-4 h-4" />}
                                            Approve
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </DashboardPageShell>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';
import { motion, AnimatePresence } from 'framer-motion';
import { Plus, RefreshCw, Tag, Copy, Trash2, Sparkles, AlertCircle } from 'lucide-react';
import { toast } from 'sonner';
import { GlassCard } from '@/components/ui/glass-card';
import { cn } from '@/lib/utils';

export default function CouponAdminPage() {
    const supabase = createClientComponentClient();
    const [coupons, setCoupons] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [newCode, setNewCode] = useState('');
    const [discount, setDiscount] = useState<number>(20); // Typed as number
    const [creating, setCreating] = useState(false);

    useEffect(() => {
        fetchCoupons();
    }, []);

    const fetchCoupons = async () => {
        const { data } = await supabase.from('coupons').select('*').order('created_at', { ascending: false });
        if (data) setCoupons(data);
        setLoading(false);
    };

    const generateRandomCode = () => {
        const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
        let result = '';
        const prefix = ['SUMMER', 'WINTER', 'FLASH', 'HIVE', 'PRO'];
        const randomPrefix = prefix[Math.floor(Math.random() * prefix.length)];

        for (let i = 0; i < 4; i++) {
            result += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        setNewCode(`${randomPrefix}-${result}`);
    };

    const handleCreate = async () => {
        if (!newCode || !discount || discount < 1 || discount > 100) {
            toast.error('Please enter a valid code and discount (1-100)');
            return;
        }
        setCreating(true);

        try {
            const res = await fetch('/api/coupons/create', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ code: newCode, discount_percent: discount })
            });

            if (res.ok) {
                toast.success('Coupon created successfully! ðŸŽŸï¸');
                fetchCoupons();
                setNewCode('');
            } else {
                const err = await res.json();
                toast.error(err.error || 'Failed to create');
            }
        } catch (e) {
            toast.error('Network error creating coupon');
        } finally {
            setCreating(false);
        }
    };

    return (
        <div className="p-6 md:p-12 max-w-7xl mx-auto min-h-screen">
            <div className="mb-12 relative">
                <div className="absolute -left-10 -top-10 w-32 h-32 bg-violet-500/20 rounded-full blur-3xl pointer-events-none" />
                <h1 className="text-4xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-violet-500 to-fuchsia-500 mb-2">
                    Coupon Manager
                </h1>
                <p className="text-muted-foreground text-lg">Create and manage discount codes for your growing hive.</p>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Creator Card */}
                <GlassCard className="p-8 h-fit lg:col-span-1 border-violet-500/20 bg-gradient-to-b from-card/50 to-card/10">
                    <h2 className="text-xl font-bold mb-6 flex items-center gap-2">
                        <Plus className="w-5 h-5 text-violet-500" /> Create New Coupon
                    </h2>

                    <div className="space-y-6">
                        <div>
                            <label className="text-xs font-bold uppercase text-muted-foreground mb-2 block tracking-wider">Coupon Code</label>
                            <div className="flex gap-2">
                                <div className="relative flex-1">
                                    <Tag className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                                    <input
                                        value={newCode}
                                        onChange={(e) => setNewCode(e.target.value.toUpperCase())}
                                        placeholder="CODE123"
                                        className="w-full pl-9 pr-3 py-3 bg-background/50 border border-border rounded-xl text-sm font-mono font-bold uppercase focus:ring-2 focus:ring-violet-500/50 outline-none transition-all placeholder:text-muted-foreground/50"
                                    />
                                </div>
                                <button
                                    onClick={generateRandomCode}
                                    className="p-3 bg-background/50 border border-border hover:bg-violet-500/10 hover:border-violet-500/30 rounded-xl transition-all group"
                                    title="Generate Random"
                                >
                                    <RefreshCw className="w-4 h-4 text-muted-foreground group-hover:text-violet-500 transition-colors" />
                                </button>
                            </div>
                        </div>

                        <div>
                            <label className="text-xs font-bold uppercase text-muted-foreground mb-2 block tracking-wider">Discount Percentage</label>
                            <div className="relative">
                                <input
                                    type="number"
                                    min="1"
                                    max="100"
                                    value={discount}
                                    onChange={(e) => {
                                        // Fix NaN issue by defaulting empty string to 0 or handling value directly
                                        const val = e.target.value === '' ? 0 : parseInt(e.target.value);
                                        setDiscount(val);
                                    }}
                                    className="w-full bg-background/50 border border-border rounded-xl px-4 py-3 text-sm font-bold focus:ring-2 focus:ring-violet-500/50 outline-none transition-all"
                                />
                                <span className="absolute right-4 top-1/2 -translate-y-1/2 text-muted-foreground font-bold">%</span>
                            </div>
                            <div className="flex justify-between text-xs text-muted-foreground mt-2 px-1">
                                <span>1%</span>
                                <span>50%</span>
                                <span>100%</span>
                            </div>
                            <input
                                type="range"
                                min="1"
                                max="100"
                                value={discount || 0}
                                onChange={(e) => setDiscount(parseInt(e.target.value))}
                                className="w-full mt-2 accent-violet-500 h-1.5 bg-muted rounded-lg appearance-none cursor-pointer"
                            />
                        </div>

                        <button
                            onClick={handleCreate}
                            disabled={creating || !newCode || !discount}
                            className={cn(
                                "w-full py-4 rounded-xl font-bold text-white shadow-lg flex items-center justify-center gap-2 transition-all mt-4",
                                creating ? "bg-muted cursor-not-allowed text-muted-foreground" : "bg-gradient-to-r from-violet-600 to-fuchsia-600 hover:shadow-violet-600/25 active:scale-95"
                            )}
                        >
                            {creating ? 'Creating...' : 'Create Coupon'} <Sparkles className="w-4 h-4" />
                        </button>
                    </div>
                </GlassCard>

                {/* List */}
                <div className="lg:col-span-2 space-y-6">
                    <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
                        <Tag className="w-5 h-5 text-violet-500" /> Active Coupons
                    </h2>

                    {loading ? (
                        <div className="grid gap-4">
                            {[1, 2, 3].map(i => (
                                <div key={i} className="h-20 bg-muted/20 animate-pulse rounded-xl" />
                            ))}
                        </div>
                    ) : coupons.length === 0 ? (
                        <div className="flex flex-col items-center justify-center py-20 border-2 border-dashed border-border/50 rounded-2xl bg-muted/5">
                            <Tag className="w-12 h-12 text-muted-foreground/30 mb-4" />
                            <p className="text-muted-foreground font-medium">No active coupons yet.</p>
                            <p className="text-sm text-muted-foreground/60">Create your first one to get started.</p>
                        </div>
                    ) : (
                        <div className="grid gap-4">
                            <AnimatePresence>
                                {coupons.map((coupon, i) => (
                                    <motion.div
                                        key={coupon.code}
                                        initial={{ opacity: 0, x: 20 }}
                                        animate={{ opacity: 1, x: 0 }}
                                        transition={{ delay: i * 0.05 }}
                                        className="relative group p-1 rounded-2xl bg-gradient-to-r from-border/50 to-border/10 hover:from-violet-500/20 hover:to-fuchsia-500/20 transition-all duration-300"
                                    >
                                        <div className="flex items-center justify-between p-5 rounded-xl bg-card border border-border/50 group-hover:border-transparent transition-all">
                                            <div className="flex items-center gap-5">
                                                <div className={cn(
                                                    "h-14 w-14 rounded-2xl flex flex-col items-center justify-center font-bold shadow-inner",
                                                    coupon.discount_percent >= 50
                                                        ? "bg-gradient-to-br from-red-500/20 to-orange-500/20 text-red-500"
                                                        : "bg-gradient-to-br from-emerald-500/20 to-green-500/20 text-emerald-500"
                                                )}>
                                                    <span className="text-lg leading-none">{coupon.discount_percent}</span>
                                                    <span className="text-[10px] opacity-80">%</span>
                                                </div>
                                                <div>
                                                    <div className="font-mono font-bold text-xl tracking-wider text-foreground group-hover:text-violet-500 transition-colors">
                                                        {coupon.code}
                                                    </div>
                                                    <div className="text-xs text-muted-foreground font-medium mt-1 flex items-center gap-2">
                                                        <span>Created {new Date(coupon.created_at).toLocaleDateString()}</span>
                                                        <span className="w-1 h-1 rounded-full bg-muted-foreground/50" />
                                                        <span className="text-green-500 flex items-center gap-1">
                                                            Active <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
                                                        </span>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="flex items-center gap-2">
                                                <button
                                                    onClick={() => {
                                                        navigator.clipboard.writeText(coupon.code);
                                                        toast.success('Code copied to clipboard');
                                                    }}
                                                    className="p-3 hover:bg-muted rounded-xl transition-all text-muted-foreground hover:text-foreground"
                                                    title="Copy Code"
                                                >
                                                    <Copy className="w-5 h-5" />
                                                </button>
                                                {/* Delete functionality would go here */}
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { supabase } from "@/lib/supabase";
import { useAppSession } from "@/lib/app-session-context";
import { useTheme } from "@/lib/theme-context";
import ProfessionalAlert from "@/components/ui/game-alert";
import DashboardBackground from "@/components/DashboardBackground";
import ThemeToggle from "@/components/ThemeToggle";
import AIChatInterface from "@/components/AIChatInterface";
import AIVoiceIntegration from "@/components/AIVoiceIntegration";
import { 
  Brain, 
  TrendingUp, 
  Users, 
  MessageSquare, 
  Zap, 
  Target,
  Clock,
  BarChart3,
  PieChart,
  Activity,
  Lightbulb,
  AlertTriangle,
  CheckCircle2,
  ArrowUpRight,
  ArrowDownRight,
  Sparkles,
  Bot,
  Cpu,
  Database,
  RefreshCw,
  Settings,
  Play,
  Pause,
  Calendar,
  Filter,
  Download,
  Upload,
  Eye,
  EyeOff,
  ChevronDown,
  ChevronUp,
  Globe,
  Wifi,
  Server,
  Shield,
  Layers,
  Grid3X3,
  Command,
  Terminal,
  Code,
  FileText,
  Mail,
  Bell,
  Search,
  Plus
} from "lucide-react";
import { cn } from "@/lib/utils";

interface AIMetrics {
  totalInteractions: number;
  averageResponseTime: number;
  userSatisfaction: number;
  intentAccuracy: number;
  learningProgress: number;
  activeConversations: number;
  topIntents: Array<{ type: string; count: number }>;
  sentimentAnalysis: {
    positive: number;
    neutral: number;
    negative: number;
  };
  performanceTrends: Array<{
    date: string;
    satisfaction: number;
    responseTime: number;
    interactions: number;
  }>;
  systemHealth: {
    cpu: number;
    memory: number;
    uptime: number;
  };
}

interface CronJob {
  id: string;
  name: string;
  description: string;
  schedule: string;
  status: 'active' | 'paused' | 'error';
  lastRun: string;
  nextRun: string;
  executionTime: number;
  successRate: number;
  enabled: boolean;
}

interface Alert {
  type: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
  autoClose?: boolean;
}

export default function AIIntelligenceDashboard() {
  const { profile } = useAppSession();
  const { theme } = useTheme();
  const isDark = theme === "dark";
  
  const [metrics, setMetrics] = useState<AIMetrics | null>(null);
  const [cronJobs, setCronJobs] = useState<CronJob[]>([]);
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState<'day' | 'week' | 'month'>('week');
  const [selectedMetric, setSelectedMetric] = useState<string>('overview');
  const [showDetails, setShowDetails] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<'all' | 'active' | 'paused' | 'error'>('all');
  const [showChat, setShowChat] = useState(false);
  const [alert, setAlert] = useState<Alert | null>(null);

  useEffect(() => {
    if (profile) {
      fetchAIMetrics();
    }
  }, [profile, timeRange]);

  const fetchAIMetrics = async () => {
    if (!profile) return;

    setLoading(true);
    try {
      // For now, use mock data since database isn't set up yet
      // TODO: Replace with real database queries once tables are created
      const mockMetrics: AIMetrics = {
        totalInteractions: 127,
        averageResponseTime: 1850,
        userSatisfaction: 87.3,
        intentAccuracy: 91.2,
        learningProgress: 73.5,
        activeConversations: 8,
        topIntents: [
          { type: "question", count: 45 },
          { type: "task", count: 32 },
          { type: "request", count: 28 },
          { type: "conversation", count: 15 },
          { type: "complaint", count: 7 }
        ],
        sentimentAnalysis: {
          positive: 68,
          neutral: 24,
          negative: 8
        },
        performanceTrends: generateTrends(timeRange),
        systemHealth: {
          cpu: 42.7,
          memory: 61.3,
          uptime: 99.97
        }
      };

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setMetrics(mockMetrics);

      // Try to fetch real data (will fail gracefully until database is set up)
      try {
        const { data: conversations, error: convError } = await supabase
          .from('conversation_history')
          .select('*')
          .eq('user_id', profile.id)
          .gte('timestamp', getTimeRangeStart(timeRange))
          .order('timestamp', { ascending: false });

        if (!convError && conversations) {
          // Real data available, process it
          const processedMetrics = processMetrics(conversations, [], []);
          setMetrics(processedMetrics);
        }
      } catch (dbError) {
        console.log('Database not set up yet, using mock data');
      }

    } catch (error) {
      console.error('Error fetching AI metrics:', error);
      setAlert({
        type: "error",
        title: "Failed to load AI metrics",
        message: "Using demo data for now."
      });
    } finally {
      setLoading(false);
    }
  };

  const processMetrics = (conversations: any[], learningData: any[], feedbackData: any[]): AIMetrics => {
    // Calculate total interactions
    const totalInteractions = conversations.filter(c => c.role === 'user').length;

    // Calculate average response time
    const avgResponseTime = learningData.length > 0 
      ? learningData.reduce((sum, d) => sum + (d.resolution_time || 0), 0) / learningData.length 
      : 0;

    // Calculate user satisfaction
    const positiveFeedback = feedbackData.filter(f => f.rating >= 4).length;
    const userSatisfaction = feedbackData.length > 0 ? (positiveFeedback / feedbackData.length) * 100 : 0;

    // Calculate intent accuracy (mock data for now)
    const intentAccuracy = 85 + Math.random() * 10; // 85-95%

    // Calculate learning progress
    const learningProgress = learningData.length > 0 ? Math.min((learningData.length / 100) * 100, 100) : 0;

    // Count active conversations
    const uniqueConversations = new Set(conversations.map(c => c.conversation_id)).size;

    // Analyze top intents
    const intentCounts = conversations
      .filter(c => c.intent_data)
      .reduce((acc, c) => {
        const type = c.intent_data.type;
        acc[type] = (acc[type] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

    const topIntents = Object.entries(intentCounts)
      .sort(([,a]: [string, unknown], [,b]: [string, unknown]) => Number(b) - Number(a))
      .slice(0, 5)
      .map(([type, count]) => ({ type, count: Number(count) }));

    // Analyze sentiment
    const sentimentAnalysis = conversations
      .filter(c => c.intent_data?.sentiment)
      .reduce(
        (acc, c) => {
          const sentiment = c.intent_data.sentiment;
          acc[sentiment as keyof typeof acc]++;
          return acc;
        },
        { positive: 0, neutral: 0, negative: 0 }
      );

    // Generate performance trends (mock data)
    const performanceTrends = generateTrends(timeRange);

    return {
      totalInteractions,
      averageResponseTime: avgResponseTime,
      userSatisfaction,
      intentAccuracy,
      learningProgress,
      activeConversations: uniqueConversations,
      topIntents,
      sentimentAnalysis,
      performanceTrends,
      systemHealth: {
        cpu: 42.7,
        memory: 61.3,
        uptime: 99.97
      }
    };
  };

  const generateTrends = (range: 'day' | 'week' | 'month') => {
    const points = range === 'day' ? 24 : range === 'week' ? 7 : 30;
    const trends = [];

    for (let i = 0; i < points; i++) {
      const date = new Date();
      date.setHours(date.getHours() - (range === 'day' ? i : i * 24));

      trends.push({
        date: date.toISOString().split('T')[0],
        satisfaction: 75 + Math.random() * 20,
        responseTime: 1000 + Math.random() * 2000,
        interactions: Math.floor(Math.random() * 50)
      });
    }

    return trends.reverse();
  };

  const getTimeRangeStart = (range: 'day' | 'week' | 'month') => {
    const now = new Date();
    switch (range) {
      case 'day':
        return new Date(now.setHours(now.getHours() - 24)).toISOString();
      case 'week':
        return new Date(now.setDate(now.getDate() - 7)).toISOString();
      case 'month':
        return new Date(now.setMonth(now.getMonth() - 1)).toISOString();
    }
  };

  // Cron job functionality
  const triggerAlert = useCallback((alert: Alert) => {
    setAlert(alert);
    if (alert.autoClose !== false) {
      setTimeout(() => setAlert(null), 5000);
    }
  }, []);

  const handleCronJobToggle = async (jobId: string, enabled: boolean) => {
    setCronJobs(prev => prev.map(job => 
      job.id === jobId ? { ...job, enabled, status: enabled ? 'active' : 'paused' } : job
    ));
    
    triggerAlert({
      type: 'success',
      title: 'Cron Job Updated',
      message: `Cron job has been ${enabled ? 'enabled' : 'disabled'} successfully.`
    });
  };

  const handleCronJobRun = async (jobId: string) => {
    const job = cronJobs.find(j => j.id === jobId);
    if (job) {
      setCronJobs(prev => prev.map(j => 
        j.id === jobId ? { ...j, status: 'active' } : j
      ));
      
      triggerAlert({
        type: 'info',
        title: 'Cron Job Triggered',
        message: `${job.name} has been manually triggered.`
      });
    }
  };

  // Mock cron jobs data
  const mockCronJobs: CronJob[] = [
    {
      id: "1",
      name: "AI Model Training",
      description: "Retrain AI models with new data",
      schedule: "0 2 * * *",
      status: "active",
      lastRun: "2024-01-15 02:00:00",
      nextRun: "2024-01-16 02:00:00",
      executionTime: 1247,
      successRate: 98.5,
      enabled: true
    },
    {
      id: "2",
      name: "Data Cleanup",
      description: "Clean up old conversation logs",
      schedule: "0 3 * * 0",
      status: "active",
      lastRun: "2024-01-14 03:00:00",
      nextRun: "2024-01-21 03:00:00",
      executionTime: 342,
      successRate: 100,
      enabled: true
    },
    {
      id: "3",
      name: "Performance Analytics",
      description: "Generate daily performance reports",
      schedule: "0 1 * * *",
      status: "error",
      lastRun: "2024-01-15 01:00:00",
      nextRun: "2024-01-16 01:00:00",
      executionTime: 892,
      successRate: 92.1,
      enabled: true
    },
    {
      id: "4",
      name: "Model Backup",
      description: "Backup trained AI models",
      schedule: "0 4 * * 6",
      status: "paused",
      lastRun: "2024-01-13 04:00:00",
      nextRun: "2024-01-20 04:00:00",
      executionTime: 2156,
      successRate: 96.8,
      enabled: false
    }
  ];

  // Initialize cron jobs
  useEffect(() => {
    if (profile) {
      setCronJobs(mockCronJobs);
    }
  }, [profile]);

  const filteredCronJobs = cronJobs.filter(job => {
    const matchesSearch = job.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         job.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesFilter = filterStatus === 'all' || job.status === filterStatus;
    return matchesSearch && matchesFilter;
  });

  if (!profile) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-white">Please sign in to view AI Intelligence dashboard.</p>
      </div>
    );
  }

  // Background classes matching signup page
  const rootBackground = cn(
    "relative min-h-screen overflow-hidden transition-colors duration-500",
    isDark
      ? "bg-[#0C1024] text-white"
      : "bg-gradient-to-br from-[#0C1024] via-[#1a1f3a] to-[#0C1024] text-white"
  );

  const gridOverlayClass = cn(
    "absolute inset-0 bg-[length:64px_64px]",
    isDark
      ? "bg-[linear-gradient(to_right,rgba(255,255,255,0.06)_1px,transparent_1px),linear-gradient(to_bottom,rgba(255,255,255,0.06)_1px,transparent_1px)] opacity-40"
      : "bg-[linear-gradient(to_right,rgba(12,16,36,0.08)_1px,transparent_1px),linear-gradient(to_bottom,rgba(12,16,36,0.08)_1px,transparent_1px)] opacity-70"
  );

  const radialOverlayClass = cn(
    "absolute inset-x-0 top-[-240px] h-[520px] rounded-full",
    isDark
      ? "bg-[radial-gradient(circle_at_center,rgba(108,67,255,0.15),transparent_70%)]"
      : "bg-[radial-gradient(circle_at_center,rgba(108,67,255,0.12),transparent_70%)]"
  );

  // Text colors matching signup page
  const subduedText = isDark ? "text-white/60" : "text-white/60";
  const strongText = isDark ? "text-white" : "text-white";

  return (
    <div className={rootBackground}>
      {/* Background overlays matching signup page */}
      <div className="pointer-events-none absolute inset-0">
        <div className={radialOverlayClass} />
        <div className={gridOverlayClass} />
      </div>
      
      <div className="relative z-10">
        {/* Header */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between p-4 sm:p-6 lg:p-8 mb-6 sm:mb-8 gap-4">
          <div>
            <h1 className={cn(
              "text-3xl lg:text-4xl font-bold mb-2 bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] bg-clip-text text-transparent"
            )}>
              AI Intelligence Center
            </h1>
            <p className={cn("text-base lg:text-lg", subduedText)}>
              Advanced AI analytics, automation, and system monitoring
            </p>
          </div>
          
          <div className="flex items-center gap-4">
            {/* Time Range Selector */}
            <div className="flex gap-1 p-1 rounded-lg bg-white/10 backdrop-blur-sm border border-white/20">
              {(['day', 'week', 'month'] as const).map((range) => (
                <button
                  key={range}
                  onClick={() => setTimeRange(range)}
                  className={cn(
                    "px-3 py-2 rounded-lg text-xs sm:text-sm font-medium transition-all duration-200",
                    timeRange === range
                      ? "bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] text-white shadow-lg shadow-[#6C43FF]/25"
                      : "text-white/60 hover:text-white hover:bg-white/10"
                  )}
                >
                  {range.charAt(0).toUpperCase() + range.slice(1)}
                </button>
              ))}
            </div>
            
            {/* Metric Selector */}
            <div className="flex items-center gap-2 p-1 rounded-lg bg-white/10 backdrop-blur-sm border border-white/20">
              {['overview', 'performance', 'automation'].map((metric) => (
                <button
                  key={metric}
                  onClick={() => setSelectedMetric(metric)}
                  className={cn(
                    "px-3 py-2 rounded-lg text-xs sm:text-sm font-medium transition-all duration-200",
                    selectedMetric === metric
                      ? "bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] text-white shadow-lg shadow-[#6C43FF]/25"
                      : "text-white/60 hover:text-white hover:bg-white/10"
                  )}
                >
                  {metric.charAt(0).toUpperCase() + metric.slice(1)}
                </button>
              ))}
            </div>
            
            <ThemeToggle />
          </div>
        </div>

        {/* Alert */}
        <AnimatePresence>
          {alert && (
            <motion.div
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="px-4 sm:px-6 lg:px-8 mb-6"
            >
              <ProfessionalAlert
                variant={alert.type}
                title={alert.title}
                message={alert.message}
                open={true}
                onClose={() => setAlert(null)}
              />
            </motion.div>
          )}
        </AnimatePresence>

        {/* Main Content */}
        <div className="px-4 sm:px-6 lg:px-8">
          {loading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {[...Array(8)].map((_, i) => (
                <div key={i} className="h-32 rounded-2xl bg-white/10 backdrop-blur-sm border border-white/20 animate-pulse" />
              ))}
            </div>
          ) : (
            <div className="space-y-8">
              {/* Overview Section */}
              {selectedMetric === 'overview' && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="space-y-6"
                >
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {/* Total Interactions Card */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      className="relative group"
                    >
                      <div className="absolute inset-0 bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300 opacity-60" />
                      <div className="relative p-6 rounded-2xl border border-white/10 bg-white/[0.02] hover:border-[#6C43FF]/30 hover:bg-white/[0.05] transition-all duration-300">
                        <div className="flex items-start justify-between mb-4">
                          <div className="p-3 rounded-xl bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 border border-white/10">
                            <MessageSquare className="w-5 h-5 text-white" />
                          </div>
                          <ArrowUpRight className="w-4 h-4 text-[#6C43FF]" />
                        </div>
                        <h3 className="text-2xl font-bold text-white mb-1">{metrics?.totalInteractions.toLocaleString()}</h3>
                        <p className={subduedText + " text-sm"}>Total Interactions</p>
                      </div>
                    </motion.div>

                    {/* User Satisfaction Card */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.1 }}
                      className="relative group"
                    >
                      <div className="absolute inset-0 bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300 opacity-60" />
                      <div className="relative p-6 rounded-2xl border border-white/10 bg-white/[0.02] hover:border-[#6C43FF]/30 hover:bg-white/[0.05] transition-all duration-300">
                        <div className="flex items-start justify-between mb-4">
                          <div className="p-3 rounded-xl bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 border border-white/10">
                            <Users className="w-5 h-5 text-white" />
                          </div>
                          <ArrowUpRight className="w-4 h-4 text-[#6C43FF]" />
                        </div>
                        <h3 className="text-2xl font-bold text-white mb-1">{metrics?.userSatisfaction}%</h3>
                        <p className={subduedText + " text-sm"}>User Satisfaction</p>
                      </div>
                    </motion.div>

                    {/* Response Time Card */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.2 }}
                      className="relative group"
                    >
                      <div className="absolute inset-0 bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300 opacity-60" />
                      <div className="relative p-6 rounded-2xl border border-white/10 bg-white/[0.02] hover:border-[#6C43FF]/30 hover:bg-white/[0.05] transition-all duration-300">
                        <div className="flex items-start justify-between mb-4">
                          <div className="p-3 rounded-xl bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 border border-white/10">
                            <Clock className="w-5 h-5 text-white" />
                          </div>
                          <ArrowDownRight className="w-4 h-4 text-[#6C43FF]" />
                        </div>
                        <h3 className="text-2xl font-bold text-white mb-1">{metrics?.averageResponseTime}ms</h3>
                        <p className={subduedText + " text-sm"}>Avg Response Time</p>
                      </div>
                    </motion.div>

                    {/* Intent Accuracy Card */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.3 }}
                      className="relative group"
                    >
                      <div className="absolute inset-0 bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300 opacity-60" />
                      <div className="relative p-6 rounded-2xl border border-white/10 bg-white/[0.02] hover:border-[#6C43FF]/30 hover:bg-white/[0.05] transition-all duration-300">
                        <div className="flex items-start justify-between mb-4">
                          <div className="p-3 rounded-xl bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 border border-white/10">
                            <Target className="w-5 h-5 text-white" />
                          </div>
                          <ArrowUpRight className="w-4 h-4 text-[#6C43FF]" />
                        </div>
                        <h3 className="text-2xl font-bold text-white mb-1">{metrics?.intentAccuracy}%</h3>
                        <p className={subduedText + " text-sm"}>Intent Accuracy</p>
                      </div>
                    </motion.div>
                  </div>

                  {/* System Health and Additional Metrics */}
                  <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    {/* System Health */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.4 }}
                      className="p-6 rounded-2xl border border-white/10 bg-white/[0.02]"
                    >
                      <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                        <Cpu className="w-5 h-5 text-[#6C43FF]" />
                        System Health
                      </h3>
                      <div className="space-y-4">
                        <div>
                          <div className="flex justify-between text-sm mb-1">
                            <span className={subduedText}>CPU Usage</span>
                            <span className="text-white">{metrics?.systemHealth.cpu}%</span>
                          </div>
                          <div className="w-full bg-white/10 rounded-full h-2">
                            <div 
                              className="bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] h-2 rounded-full transition-all duration-500"
                              style={{ width: `${metrics?.systemHealth.cpu}%` }}
                            />
                          </div>
                        </div>
                        <div>
                          <div className="flex justify-between text-sm mb-1">
                            <span className={subduedText}>Memory Usage</span>
                            <span className="text-white">{metrics?.systemHealth.memory}%</span>
                          </div>
                          <div className="w-full bg-white/10 rounded-full h-2">
                            <div 
                              className="bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] h-2 rounded-full transition-all duration-500"
                              style={{ width: `${metrics?.systemHealth.memory}%` }}
                            />
                          </div>
                        </div>
                        <div>
                          <div className="flex justify-between text-sm mb-1">
                            <span className={subduedText}>Uptime</span>
                            <span className="text-white">{metrics?.systemHealth.uptime}%</span>
                          </div>
                          <div className="w-full bg-white/10 rounded-full h-2">
                            <div 
                              className="bg-gradient-to-r from-green-500 to-green-400 h-2 rounded-full transition-all duration-500"
                              style={{ width: `${metrics?.systemHealth.uptime}%` }}
                            />
                          </div>
                        </div>
                      </div>
                    </motion.div>

                    {/* Sentiment Analysis */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.5 }}
                      className="p-6 rounded-2xl border border-white/10 bg-white/[0.02]"
                    >
                      <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                        <PieChart className="w-5 h-5 text-[#6C43FF]" />
                        Sentiment Analysis
                      </h3>
                      <div className="space-y-3">
                        <div className="flex justify-between items-center">
                          <span className="text-green-400 text-sm">Positive</span>
                          <span className="text-white font-medium">{metrics?.sentimentAnalysis.positive}%</span>
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-gray-400 text-sm">Neutral</span>
                          <span className="text-white font-medium">{metrics?.sentimentAnalysis.neutral}%</span>
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-red-400 text-sm">Negative</span>
                          <span className="text-white font-medium">{metrics?.sentimentAnalysis.negative}%</span>
                        </div>
                      </div>
                    </motion.div>

                    {/* Top Intents */}
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.6 }}
                      className="p-6 rounded-2xl border border-white/10 bg-white/[0.02]"
                    >
                      <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                        <TrendingUp className="w-5 h-5 text-[#6C43FF]" />
                        Top Intents
                      </h3>
                      <div className="space-y-2">
                        {metrics?.topIntents.slice(0, 5).map((intent, index) => (
                          <div key={intent.type} className="flex justify-between items-center">
                            <span className={subduedText + " text-sm capitalize"}>{intent.type.replace('_', ' ')}</span>
                            <span className="text-white font-medium">{intent.count.toLocaleString()}</span>
                          </div>
                        ))}
                      </div>
                    </motion.div>
                  </div>
                </motion.div>
              )}

              {/* Performance Section */}
              {selectedMetric === 'performance' && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="space-y-6"
                >
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    <div className="p-6 rounded-2xl border border-white/10 bg-white/[0.02]">
                      <h3 className="text-lg font-semibold text-white mb-4">Performance Trends</h3>
                      <div className="space-y-3">
                        {metrics?.performanceTrends.map((trend) => (
                          <div key={trend.date} className="flex justify-between items-center p-3 rounded-lg bg-white/[0.02]">
                            <span className={subduedText}>{trend.date}</span>
                            <div className="flex gap-4">
                              <span className="text-green-400">{trend.satisfaction}% satisfaction</span>
                              <span className="text-blue-400">{trend.responseTime}ms response</span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                    
                    <div className="p-6 rounded-2xl border border-white/10 bg-white/[0.02]">
                      <h3 className="text-lg font-semibold text-white mb-4">Learning Progress</h3>
                      <div className="flex items-center justify-center h-32">
                        <div className="relative">
                          <div className="w-32 h-32 rounded-full border-8 border-white/10">
                            <div 
                              className="absolute inset-0 rounded-full border-8 border-transparent border-t-[#6C43FF] border-r-[#8A63FF] transition-all duration-1000"
                              style={{ 
                                transform: `rotate(${(metrics?.learningProgress || 0) * 3.6}deg)`,
                              }}
                            />
                          </div>
                          <div className="absolute inset-0 flex items-center justify-center">
                            <span className="text-2xl font-bold text-white">{metrics?.learningProgress}%</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </motion.div>
              )}

              {/* Automation Section */}
              {selectedMetric === 'automation' && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="space-y-6"
                >
                  {/* Cron Jobs Controls */}
                  <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
                    <div className="flex-1 max-w-md">
                      <div className="relative">
                        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-white/40" />
                        <input
                          type="text"
                          placeholder="Search cron jobs..."
                          value={searchQuery}
                          onChange={(e) => setSearchQuery(e.target.value)}
                          className="w-full pl-10 pr-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/40 focus:border-[#6C43FF]/50 focus:outline-none"
                        />
                      </div>
                    </div>
                    
                    <div className="flex gap-2">
                      <select
                        value={filterStatus}
                        onChange={(e) => setFilterStatus(e.target.value as any)}
                        className="px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:border-[#6C43FF]/50 focus:outline-none"
                      >
                        <option value="all">All Status</option>
                        <option value="active">Active</option>
                        <option value="paused">Paused</option>
                        <option value="error">Error</option>
                      </select>
                      
                      <button className="p-2 bg-white/10 border border-white/20 rounded-lg text-white hover:bg-white/20 transition-colors">
                        <Settings className="w-4 h-4" />
                      </button>
                    </div>
                  </div>

                  {/* Cron Jobs Grid */}
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                    {filteredCronJobs.map((job) => (
                      <motion.div
                        key={job.id}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="group"
                      >
                        <div className="p-4 rounded-xl border border-white/10 bg-white/[0.02] hover:border-[#6C43FF]/30 hover:bg-white/[0.05] transition-all duration-200">
                          <div className="flex items-start justify-between mb-3">
                            <div className="flex items-center gap-3">
                              <div className={cn(
                                "p-2 rounded-lg",
                                job.status === 'active' ? "bg-green-500/20 text-green-400" :
                                job.status === 'error' ? "bg-red-500/20 text-red-400" :
                                "bg-gray-500/20 text-gray-400"
                              )}>
                                <Terminal className="w-4 h-4" />
                              </div>
                              <div>
                                <h4 className="font-semibold text-white">{job.name}</h4>
                                <p className={subduedText + " text-xs"}>{job.description}</p>
                              </div>
                            </div>
                            <div className="flex items-center gap-2">
                              <button
                                onClick={() => handleCronJobToggle(job.id, !job.enabled)}
                                className={cn(
                                  "p-2 rounded-lg transition-colors",
                                  job.enabled ? "bg-[#6C43FF]/20 text-[#6C43FF]" : "bg-white/10 text-white/60"
                                )}
                              >
                                {job.enabled ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                              </button>
                              <button
                                onClick={() => handleCronJobRun(job.id)}
                                className="p-2 rounded-lg bg-white/10 text-white/60 hover:bg-white/20 transition-colors"
                              >
                                <RefreshCw className="w-4 h-4" />
                              </button>
                            </div>
                          </div>
                          
                          <div className="grid grid-cols-2 gap-3 text-xs">
                            <div>
                              <p className={subduedText}>Schedule</p>
                              <p className="text-white font-mono">{job.schedule}</p>
                            </div>
                            <div>
                              <p className={subduedText}>Success Rate</p>
                              <p className="text-white">{job.successRate}%</p>
                            </div>
                            <div>
                              <p className={subduedText}>Last Run</p>
                              <p className="text-white">{new Date(job.lastRun).toLocaleDateString()}</p>
                            </div>
                            <div>
                              <p className={subduedText}>Execution Time</p>
                              <p className="text-white">{job.executionTime}ms</p>
                            </div>
                          </div>
                        </div>
                      </motion.div>
                    ))}
                  </div>

                  {/* Add New Cron Job */}
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 }}
                    className="p-6 rounded-2xl border border-dashed border-white/20 bg-white/[0.02] hover:border-[#6C43FF]/30 transition-all duration-200"
                  >
                    <div className="text-center">
                      <div className="mx-auto w-12 h-12 rounded-full bg-gradient-to-r from-[#6C43FF]/20 to-[#8A63FF]/20 border border-white/10 flex items-center justify-center mb-4">
                        <Plus className="w-6 h-6 text-[#6C43FF]" />
                      </div>
                      <h3 className="text-lg font-semibold text-white mb-2">Add New Cron Job</h3>
                      <p className={subduedText + " mb-4"}>
                        Create automated tasks for AI model training, data processing, and system maintenance
                      </p>
                      <button className="px-4 py-2 bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] text-white rounded-lg hover:shadow-lg hover:shadow-[#6C43FF]/25 transition-all duration-200">
                        Create Cron Job
                      </button>
                    </div>
                  </motion.div>
                </motion.div>
              )}
            </div>
          )}
        </div>
      </div>
      
      {/* Floating Action Button */}
      <motion.div
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 1, type: "spring", stiffness: 200 }}
        className="fixed bottom-8 right-8 z-50"
      >
        <button
          onClick={() => setShowChat(!showChat)}
          className="relative group p-4 rounded-full bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] text-white shadow-lg shadow-[#6C43FF]/25 hover:shadow-xl hover:shadow-[#6C43FF]/40 transition-all duration-300 hover:scale-110"
        >
          <div className="absolute inset-0 rounded-full bg-gradient-to-r from-[#6C43FF] to-[#8A63FF] blur-lg opacity-50 group-hover:opacity-70 transition-opacity duration-300" />
          <div className="relative">
            <Bot className="w-6 h-6" />
          </div>
          <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white animate-pulse" />
        </button>
        
        {/* Tooltip */}
        <div className="absolute bottom-full right-0 mb-2 px-3 py-1 bg-black/80 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 whitespace-nowrap">
          AI Assistant
        </div>
      </motion.div>

      {/* AI Chat Interface */}
      <AnimatePresence>
        {showChat && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 20, scale: 0.95 }}
            className="fixed bottom-24 right-8 z-40 w-96 max-w-[90vw]"
          >
            <AIChatInterface />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { supabase } from "@/lib/supabase";
import { useAppSession } from "@/lib/app-session-context";
import { useTheme } from "@/lib/theme-context";
import ProfessionalAlert from "@/components/ui/game-alert";
import DashboardBackground from "@/components/DashboardBackground";
import ThemeToggle from "@/components/ThemeToggle";
import AIAnalyticsInsights from "@/components/AIAnalyticsInsights";
import {
    TrendingUp,
    TrendingDown,
    DollarSign,
    Zap,
    HardDrive,
    Calendar,
    Download,
    Settings,
    Activity,
    Target,
    Clock,
    PieChart,
    BarChart3,
    Filter,
    Brain
} from "lucide-react";

interface AnalyticsData {
    subscription: {
        plan: {
            name: string;
            max_api_calls_per_month: number;
            max_tokens_per_month: number;
            max_storage_mb: number;
        };
        status: string;
        current_period_start: string;
        current_period_end: string;
    };
    usage: {
        api_calls: {
            used: number;
            limit: number;
            percentage: number;
            cost: number;
        };
        tokens: {
            used: number;
            limit: number;
            percentage: number;
            cost: number;
        };
        storage: {
            used: number;
            limit: number;
            percentage: number;
            cost: number;
        };
    };
    usage_records: any[];
    alerts: any[];
    period: {
        start: string;
        end: string;
        type: string;
    };
}

export default function AnalyticsPage() {
    const { profile } = useAppSession();
    const { theme } = useTheme();
    const isDark = theme === "dark";
    const [analytics, setAnalytics] = useState<AnalyticsData | null>(null);
    const [loading, setLoading] = useState(true);
    const [period, setPeriod] = useState("current");
    const [selectedMetric, setSelectedMetric] = useState("overview");
    const [showAlert, setShowAlert] = useState<{
        type: "success" | "error" | "warning" | "info";
        title: string;
        message?: string;
        autoClose?: number;
    } | null>(null);
    const [showAIInsights, setShowAIInsights] = useState(false);

    useEffect(() => {
        fetchAnalytics();
    }, [period]);

    const fetchAnalytics = async () => {
        try {
            setLoading(true);
            const response = await fetch(`/api/billing/usage/track?period=${period}`);
            const data = await response.json();
            setAnalytics(data);
        } catch (error) {
            console.error("Error fetching analytics:", error);
        } finally {
            setLoading(false);
        }
    };

    const formatNumber = (num: number) => {
        if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
        if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
        return num.toString();
    };

    const formatCurrency = (cents: number) => {
        return `$${(cents / 100).toFixed(2)}`;
    };

    const getUsageColor = (percentage: number) => {
        if (percentage >= 90) return "text-red-600 bg-red-50 border-red-200";
        if (percentage >= 70) return "text-yellow-600 bg-yellow-50 border-yellow-200";
        return "text-green-600 bg-green-50 border-green-200";
    };

    const getTrendIcon = (isPositive: boolean) => {
        return isPositive ? (
            <TrendingUp className="w-4 h-4 text-green-600" />
        ) : (
            <TrendingDown className="w-4 h-4 text-red-600" />
        );
    };

    if (loading) {
        return (
            <DashboardBackground>
                <div className="container mx-auto px-4 py-8">
                    <div className="mb-8 flex items-center justify-between">
                        <div>
                            <h1 className="text-3xl font-bold mb-2" style={{ color: 'var(--text-primary)' }}>Analytics Dashboard</h1>
                            <p style={{ color: 'var(--text-secondary)' }}>Monitor your usage and performance metrics</p>
                        </div>
                        <ThemeToggle />
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                        {[...Array(9)].map((_, i) => (
                            <div key={i} className="bg-gray-50 rounded-2xl h-32 animate-pulse" />
                        ))}
                    </div>
                </div>
            </DashboardBackground>
        );
    }

    return (
        <DashboardBackground>
            <div className="container mx-auto px-4 py-8">
                <div className="mb-8 flex items-center justify-between">
                    <div>
                        <h1 className="text-3xl font-bold mb-2" style={{ color: 'var(--text-primary)' }}>Analytics Dashboard</h1>
                        <p style={{ color: 'var(--text-secondary)' }}>Monitor your usage and performance metrics</p>
                    </div>
                    <div className="flex items-center gap-3">
                        {/* AI Insights Toggle */}
                        <motion.button
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                            onClick={() => setShowAIInsights(!showAIInsights)}
                            className="flex items-center gap-2 px-4 py-2 rounded-xl font-medium transition-all duration-300"
                            style={{
                                background: showAIInsights
                                    ? 'linear-gradient(135deg, #6C43FF, #8A63FF)'
                                    : isDark
                                        ? 'rgba(255, 255, 255, 0.1)'
                                        : 'rgba(139, 92, 246, 0.1)',
                                color: showAIInsights ? 'white' : isDark ? 'white' : '#6C43FF',
                                border: `1px solid ${showAIInsights ? 'transparent' : isDark ? 'rgba(139, 92, 246, 0.3)' : 'rgba(139, 92, 246, 0.2)'}`
                            }}
                        >
                            <Brain className="w-4 h-4" />
                            {showAIInsights ? 'Hide' : 'Show'} AI
                        </motion.button>

                        <div className="flex items-center gap-2">
                            {["current", "last_month", "year"].map((periodOption) => (
                                <button
                                    key={periodOption}
                                    onClick={() => setPeriod(periodOption)}
                                    className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
                                        period === periodOption
                                            ? "bg-linear-to-r from-purple-600 to-purple-800 shadow-lg shadow-purple-500/25"
                                            : "text-gray-600 hover:text-purple-600 hover:bg-purple-50"
                                    }`}
                                >
                                    {periodOption === "current" ? "This Month" : periodOption === "last_month" ? "Last Month" : "This Year"}
                                </button>
                            ))}
                        </div>
                        <ThemeToggle />
                    </div>
                </div>

                {/* AI Insights Section */}
                <AnimatePresence>
                    {showAIInsights && (
                        <motion.div
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                            className="mb-8"
                        >
                            <div className="max-w-7xl mx-auto">
                                <div className="rounded-3xl border border-white/12 bg-white/[0.04] backdrop-blur-xl overflow-hidden">
                                    <AIAnalyticsInsights />
                                </div>
                            </div>
                        </motion.div>
                    )}
                </AnimatePresence>
                <div className="flex items-center gap-3">
                    <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => {
                            setShowAlert({
                                type: "info",
                                title: "Export Analytics",
                                message: "Analytics data will be downloaded as CSV file",
                                autoClose: 3000
                            });
                        }}
                        className="p-2.5 backdrop-blur-sm border rounded-xl hover:border-purple-500 hover:shadow-lg hover:shadow-purple-500/20 transition-all duration-300"
                        style={{
                            backgroundColor: isDark ? 'rgba(255, 255, 255, 0.8)' : 'rgba(255, 255, 255, 0.9)',
                            borderColor: isDark ? 'rgba(139, 92, 246, 0.2)' : 'rgba(139, 92, 246, 0.3)'
                        }}
                    >
                        <Download className="w-5 h-5 text-purple-600" />
                    </motion.button>
                    <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        className="p-2.5 backdrop-blur-sm border rounded-xl hover:border-purple-500 hover:shadow-lg hover:shadow-purple-500/20 transition-all duration-300"
                        style={{
                            backgroundColor: isDark ? 'rgba(255, 255, 255, 0.8)' : 'rgba(255, 255, 255, 0.9)',
                            borderColor: isDark ? 'rgba(139, 92, 246, 0.2)' : 'rgba(139, 92, 246, 0.3)'
                        }}
                    >
                        <Settings className="w-5 h-5 text-purple-600" />
                    </motion.button>
                </div>
            </div>

            {/* Subscription Status */}
            {analytics?.subscription && (
                <div className="max-w-7xl mx-auto px-4 sm:px-6 py-4 sm:py-6">
                    <div className="bg-linear-to-r from-purple-600 to-purple-800 rounded-2xl p-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <h2 className="text-xl font-bold mb-1">{analytics?.subscription?.plan?.name || 'Unknown'} Plan</h2>
                                <p className="text-purple-100">
                                    Period: {new Date(analytics?.subscription?.current_period_start || Date.now()).toLocaleDateString()} -{" "}
                                    {new Date(analytics?.subscription?.current_period_end || Date.now()).toLocaleDateString()}
                                </p>
                            </div>
                            <div className="text-right">
                                <div className="text-2xl font-bold">
                                    {formatCurrency((analytics.usage.api_calls?.cost || 0) + (analytics.usage.tokens?.cost || 0) + (analytics.usage.storage?.cost || 0))}
                                </div>
                                <div className="text-purple-100 text-sm">Current Period Cost</div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Usage Overview */}
            {analytics?.usage && (
                <div className="max-w-7xl mx-auto px-4 sm:px-6 pb-6">
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6">
                        {/* API Calls */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="bg-white border border-gray-200 rounded-2xl p-6"
                        >
                            <div className="flex items-center justify-between mb-4">
                                <div className="p-3 bg-purple-50 rounded-xl">
                                    <Zap className="w-6 h-6 text-purple-600" />
                                </div>
                                <span className={`px-3 py-1 text-xs font-medium rounded-full border ${getUsageColor(analytics.usage.api_calls.percentage)}`}>
                  {analytics.usage.api_calls.percentage}% used
                </span>
                            </div>
                            <div className="mb-4">
                                <div className="text-2xl font-bold text-black">
                                    {formatNumber(analytics.usage.api_calls.used)}
                                </div>
                                <div className="text-gray-500 text-sm">
                                    of {formatNumber(analytics.usage.api_calls.limit)} calls
                                </div>
                            </div>
                            <div className="w-full bg-gray-100 rounded-full h-2 mb-2">
                                <div
                                    className="bg-linear-to-r from-purple-500 to-purple-600 h-2 rounded-full transition-all duration-500"
                                    style={{ width: `${Math.min(analytics.usage.api_calls.percentage, 100)}%` }}
                                />
                            </div>
                            <div className="flex items-center justify-between text-sm">
                                <span className="text-gray-500">Cost: {formatCurrency(analytics.usage.api_calls.cost)}</span>
                                {getTrendIcon(true)}
                            </div>
                        </motion.div>

                        {/* Tokens */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.1 }}
                            className="bg-white border border-gray-200 rounded-2xl p-6"
                        >
                            <div className="flex items-center justify-between mb-4">
                                <div className="p-3 bg-blue-50 rounded-xl">
                                    <Activity className="w-6 h-6 text-blue-600" />
                                </div>
                                <span className={`px-3 py-1 text-xs font-medium rounded-full border ${getUsageColor(analytics.usage.tokens.percentage)}`}>
                  {analytics.usage.tokens.percentage}% used
                </span>
                            </div>
                            <div className="mb-4">
                                <div className="text-2xl font-bold text-black">
                                    {formatNumber(analytics.usage.tokens.used)}
                                </div>
                                <div className="text-gray-500 text-sm">
                                    of {formatNumber(analytics.usage.tokens.limit)} tokens
                                </div>
                            </div>
                            <div className="w-full bg-gray-100 rounded-full h-2 mb-2">
                                <div
                                    className="bg-linear-to-r from-blue-500 to-blue-600 h-2 rounded-full transition-all duration-500"
                                    style={{ width: `${Math.min(analytics.usage.tokens.percentage, 100)}%` }}
                                />
                            </div>
                            <div className="flex items-center justify-between text-sm">
                                <span className="text-gray-500">Cost: {formatCurrency(analytics.usage.tokens.cost)}</span>
                                {getTrendIcon(true)}
                            </div>
                        </motion.div>

                        {/* Storage */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.2 }}
                            className="bg-white border border-gray-200 rounded-2xl p-6"
                        >
                            <div className="flex items-center justify-between mb-4">
                                <div className="p-3 bg-green-50 rounded-xl">
                                    <BarChart3 className="w-6 h-6 text-green-600" />
                                </div>
                                <span className={`px-3 py-1 text-xs font-medium rounded-full border ${getUsageColor(analytics.usage.storage.percentage)}`}>
                  {analytics.usage.storage.percentage}% used
                </span>
                            </div>
                            <div className="mb-4">
                                <div className="text-2xl font-bold text-black">
                                    {analytics.usage.storage.used} MB
                                </div>
                                <div className="text-gray-500 text-sm">
                                    of {analytics.usage.storage.limit} MB
                                </div>
                            </div>
                            <div className="w-full bg-gray-100 rounded-full h-2 mb-2">
                                <div
                                    className="bg-linear-to-r from-green-500 to-green-600 h-2 rounded-full transition-all duration-500"
                                    style={{ width: `${Math.min(analytics.usage.storage.percentage, 100)}%` }}
                                />
                            </div>
                            <div className="flex items-center justify-between text-sm">
                                <span className="text-gray-500">Cost: {formatCurrency(analytics.usage.storage.cost)}</span>
                                {getTrendIcon(false)}
                            </div>
                        </motion.div>
                    </div>
                </div>
            )}

            {/* Detailed Analytics */}
            <div className="max-w-7xl mx-auto px-4 sm:px-6 pb-12">
                <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
                    {/* Usage Chart */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="lg:col-span-2 bg-white border border-gray-200 rounded-2xl p-6"
                    >
                        <div className="flex items-center justify-between mb-6">
                            <h3 className="text-lg font-semibold text-black">Usage Trends</h3>
                            <div className="flex items-center gap-2">
                                <button className="px-3 py-1.5 text-sm bg-purple-50 text-purple-600 rounded-lg font-medium">
                                    Daily
                                </button>
                                <button className="px-3 py-1.5 text-sm text-gray-600 hover:bg-gray-50 rounded-lg transition-colors">
                                    Weekly
                                </button>
                                <button className="px-3 py-1.5 text-sm text-gray-600 hover:bg-gray-50 rounded-lg transition-colors">
                                    Monthly
                                </button>
                            </div>
                        </div>

                        {/* Mock Chart */}
                        <div className="h-64 bg-gray-50 rounded-xl flex items-center justify-center">
                            <div className="text-center">
                                <PieChart className="w-12 h-12 text-gray-400 mx-auto mb-3" />
                                <p className="text-gray-500">Usage chart will appear here</p>
                                <p className="text-gray-400 text-sm mt-1">Integration with chart library needed</p>
                            </div>
                        </div>
                    </motion.div>

                    {/* Recent Activity */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3 }}
                        className="bg-white border border-gray-200 rounded-2xl p-6"
                    >
                        <h3 className="text-lg font-semibold text-black mb-4">Recent Activity</h3>

                        <div className="space-y-3">
                            {analytics?.usage_records?.slice(0, 5).map((record, index) => (
                                <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-xl">
                                    <div className="flex items-center gap-3">
                                        <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
                                            <Zap className="w-4 h-4 text-purple-600" />
                                        </div>
                                        <div>
                                            <div className="font-medium text-black text-sm">
                                                {record.usage_type.replace('_', ' ').charAt(0).toUpperCase() + record.usage_type.slice(1)}
                                            </div>
                                            <div className="text-xs text-gray-500">
                                                {new Date(record.created_at).toLocaleDateString()}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <div className="font-medium text-black text-sm">
                                            {formatNumber(record.quantity)}
                                        </div>
                                        <div className="text-xs text-gray-500">
                                            {formatCurrency(record.total_cost_cents)}
                                        </div>
                                    </div>
                                </div>
                            )) || (
                                <div className="text-center py-8">
                                    <Activity className="w-12 h-12 text-gray-400 mx-auto mb-3" />
                                    <p className="text-gray-500">No recent activity</p>
                                </div>
                            )}
                        </div>
                    </motion.div>
                </div>

                {/* Alerts */}
                {analytics?.alerts && analytics.alerts.length > 0 && (
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.4 }}
                        className="mt-6 bg-white border border-gray-200 rounded-2xl p-6"
                    >
                        <h3 className="text-lg font-semibold text-black mb-4">Usage Alerts</h3>

                        <div className="space-y-3">
                            {analytics.alerts.map((alert, index) => (
                                <div key={index} className="flex items-center justify-between p-4 bg-yellow-50 border border-yellow-200 rounded-xl">
                                    <div className="flex items-center gap-3">
                                        <div className="w-8 h-8 bg-yellow-100 rounded-lg flex items-center justify-center">
                                            <Filter className="w-4 h-4 text-yellow-600" />
                                        </div>
                                        <div>
                                            <div className="font-medium text-black">
                                                {alert.metric_type.charAt(0).toUpperCase() + alert.metric_type.slice(1)} Alert
                                            </div>
                                            <div className="text-sm text-gray-600">
                                                Threshold: {alert.threshold_percent}%
                                            </div>
                                        </div>
                                    </div>
                                    <button className="text-sm text-yellow-600 hover:text-yellow-700 font-medium">
                                        Configure
                                    </button>
                                </div>
                            ))}
                        </div>
                    </motion.div>
                )}
            </div>

            {/* Alert Component */}
            <AnimatePresence>
                {showAlert && (
                    <ProfessionalAlert
                        open={!!showAlert}
                        title={showAlert?.title || ""}
                        message={showAlert?.message}
                        onClose={() => setShowAlert(null)}
                        variant={showAlert?.type || "info"}
                        autoClose={showAlert?.autoClose}
                    />
                )}
            </AnimatePresence>
        </DashboardBackground>
    );
}

