const MRR_SERIES: Record<TimeframeOption, { label: string; value: number }[]> = {
  "12 months": [
    { label: "Jan", value: 15400 },
    { label: "Feb", value: 15840 },
    { label: "Mar", value: 16280 },
    { label: "Apr", value: 16720 },
