                    initial={{ opacity: 0, y: 12 }}
{{ ... }
                    transition={{ delay: 0.3, duration: 0.35 }}
                    className="rounded-3xl border border-black/10 bg-white/70 p-6 backdrop-blur-lg dark:border-white/10 dark:bg-white/5"
                  >
