        totalIntegrations: 0,
        activeInstalls: 0,
        totalRevenue: 0,
        pendingEarnings: 0,
    });
    const [integrations, setIntegrations] = useState<Integration[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDeveloperData();
    }, []);

    const fetchDeveloperData = async () => {
        try {
