                            Submit Integration
                        </Link>
                        <ThemeToggle />
                    </motion.div>
                </div>

                {/* Payout Settings Banner */}
                {!isConnected && (
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="mb-8"
                    >
                        <GlassCard className="p-6 border-yellow-500/30 bg-yellow-500/5">
                            <div className="flex items-start gap-4">
                                <div className="w-12 h-12 rounded-xl bg-yellow-500/20 border border-yellow-500/30 flex items-center justify-center flex-shrink-0">
                                    <DollarSign className="w-6 h-6 text-yellow-500" />
                                </div>
                                <div className="flex-1">
                                    <h3 className="font-semibold text-foreground mb-1">Set Up Payouts</h3>
                                    <p className="text-sm text-muted-foreground mb-4">
                                        Add your PayPal or Wise details to receive your earnings. Payouts are processed monthly.
                                    </p>
                                    <Link
                                        href="/developer/settings/payouts"
                                        className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-yellow-500/20 border border-yellow-500/30 text-yellow-500 hover:bg-yellow-500/30 transition-colors text-sm font-medium"
                                    >
                                        <Settings className="w-4 h-4" />
                                        Configure Payouts
                                    </Link>
                                </div>
                            </div>
                        </GlassCard>
                    </motion.div>
                )}

                {/* Stats Grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
                    {[
                        { icon: Package, label: 'Integrations', value: stats.totalIntegrations, color: 'text-blue-500' },
                        { icon: Users, label: 'Active Installs', value: stats.activeInstalls, color: 'text-purple-500' },
                        { icon: DollarSign, label: 'Total Revenue', value: `$${stats.totalRevenue.toFixed(2)}`, color: 'text-emerald-500' },
                        { icon: TrendingUp, label: 'Pending', value: `$${stats.pendingEarnings.toFixed(2)}`, color: 'text-yellow-500' },
                    ].map((stat, index) => (
                        <motion.div
                            key={index}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.1 * index }}
                        >
                            <GlassCard className="p-5">
                                <div className="flex items-center gap-3">
                                    <div className={cn('w-12 h-12 rounded-xl flex items-center justify-center bg-gradient-to-br from-primary/20 to-primary/5 border border-primary/20')}>
                                        <stat.icon className={cn('w-6 h-6', stat.color)} />
                                    </div>
                                    <div>
                                        <div className="text-2xl font-bold text-foreground">{stat.value}</div>
                                        <div className="text-xs text-muted-foreground">{stat.label}</div>
                                    </div>
                                </div>
                            </GlassCard>
                        </motion.div>
                    ))}
                </div>

                {/* Integrations List */}
                <div>
                    <h2 className="text-2xl font-bold text-foreground mb-4">Your Integrations</h2>

                    {integrations.length === 0 ? (
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="text-center py-20"
                        >
                            <Package className="w-16 h-16 mx-auto mb-4 text-muted-foreground opacity-50" />
                            <h3 className="text-xl font-semibold text-foreground mb-2">No integrations yet</h3>
                            <p className="text-muted-foreground mb-6">
                                Start building your first integration and earn revenue
                            </p>
                            <Link
                                href="/developer/submit"
                                className="inline-flex items-center gap-2 px-6 py-3 rounded-xl bg-primary text-primary-foreground hover:bg-primary/90 transition-colors font-medium"
                            >
                                <Plus className="w-5 h-5" />
                                Submit Integration
                            </Link>
                        </motion.div>
                    ) : (
                        <div className="space-y-3">
                            {integrations.map((integration, index) => (
                                <motion.div
                                    key={integration.id}
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: index * 0.05 }}
                                >
                                    <GlassCard className="p-6">
                                        <div className="flex items-center justify-between">
                                            <div className="flex items-center gap-4 flex-1">
                                                <div>
                                                    <h3 className="text-lg font-semibold text-foreground mb-1">{integration.name}</h3>
                                                    <div className="flex items-center gap-4 text-sm text-muted-foreground">
                                                        <span>{integration.active_installs} installs</span>
                                                        <span>â€¢</span>
                                                        <span>${integration.total_revenue.toFixed(2)} revenue</span>
                                                        <span>â€¢</span>
                                                        <span>â­ {integration.rating.toFixed(1)}</span>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="flex items-center gap-3">
                                                <span className={cn('flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium border', getStatusColor(integration.status))}>
                                                    {getStatusIcon(integration.status)}
                                                    {integration.status}
                                                </span>
                                                <Link
                                                    href={`/developer/integrations/${integration.id}`}
                                                    className="p-2 rounded-lg hover:bg-muted transition-colors"
                                                >
                                                    <Settings className="w-5 h-5 text-muted-foreground" />
                                                </Link>
                                            </div>
                                        </div>
                                    </GlassCard>
                                </motion.div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </HeroBackground>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import {
    Download, Star, Shield, Zap, Globe, CheckCircle,
    MessageSquare, ArrowLeft, Share2, ExternalLink,
    CreditCard, Lock
} from 'lucide-react';
import { HeroBackground } from '@/components/HeroBackground';
import { GlassCard } from '@/components/ui/GlassCard';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';
import { PaystackService } from '@/lib/paystack-service';
import { cn } from '@/lib/utils';
import Link from 'next/link';

interface Integration {
    id: string;
    name: string;
    slug: string;
    short_description: string;
    long_description: string;
    icon_url: string;
    cover_image_url: string;
    screenshots: string[];
    pricing_model: 'free' | 'one_time' | 'subscription';
    price: number;
    currency: string;
    paystack_plan_code: string;
    developer_id: string;
    rating: number;
    review_count: number;
    total_installs: number;
    capabilities: string[];
    updated_at: string;
}

export default function IntegrationDetailsPage() {
    const params = useParams();
    const router = useRouter();
    const supabase = createClientComponentClient();
    const [integration, setIntegration] = useState<Integration | null>(null);
    const [loading, setLoading] = useState(true);
    const [purchasing, setPurchasing] = useState(false);
    const [user, setUser] = useState<any>(null);

    useEffect(() => {
        fetchIntegration();
        checkUser();
    }, []);

    const checkUser = async () => {
        const { data: { user } } = await supabase.auth.getUser();
        setUser(user);
    };

    const fetchIntegration = async () => {
        try {
            const { data, error } = await supabase
                .from('marketplace_integrations')
                .select('*')
                .eq('slug', params.slug)
                .single();

            if (error) throw error;
            setIntegration(data);
        } catch (error) {
            console.error('Error fetching integration:', error);
        } finally {
            setLoading(false);
        }
    };

    const handlePurchase = async () => {
        if (!user) {
            router.push('/login');
            return;
        }

        if (!integration) return;

        setPurchasing(true);
        try {
            // 1. Initialize Paystack Transaction
            const response = await fetch('/api/integrations/purchase', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    integrationId: integration.id,
                    email: user.email,
                }),
            });

            const data = await response.json();

            if (data.authorization_url) {
                // Redirect to Paystack
                window.location.href = data.authorization_url;
            } else if (data.success) {
                // Free installation success
                router.push('/integrations?success=true');
            }
        } catch (error) {
            console.error('Purchase failed:', error);
            // TODO: Show error toast
        } finally {
            setPurchasing(false);
        }
    };

    if (loading) {
        return (
            <HeroBackground className="min-h-screen flex items-center justify-center">
                <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin" />
            </HeroBackground>
        );
    }

    if (!integration) {
        return (
            <HeroBackground className="min-h-screen flex flex-col items-center justify-center">
                <h1 className="text-2xl font-bold mb-4">Integration not found</h1>
                <Link href="/hivetools" className="text-primary hover:underline">
                    Back to Marketplace
                </Link>
            </HeroBackground>
        );
    }

    return (
        <HeroBackground className="min-h-screen w-full overflow-hidden pb-12 pt-20">
            <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                {/* Back Button */}
                <Link
                    href="/hivetools"
                    className="inline-flex items-center gap-2 text-muted-foreground hover:text-foreground transition-colors mb-8"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Marketplace
                </Link>

                <div className="grid lg:grid-cols-3 gap-8">
                    {/* Main Content */}
                    <div className="lg:col-span-2 space-y-8">
                        {/* Header Card */}
                        <GlassCard className="p-8 relative overflow-hidden">
                            {/* Background Blur/Gradient */}
                            <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-transparent" />

                            <div className="relative flex gap-6">
                                <div className="w-24 h-24 rounded-2xl bg-background/50 border border-white/10 flex items-center justify-center overflow-hidden shadow-xl shrink-0">
                                    {integration.icon_url ? (
                                        <img src={integration.icon_url} alt={integration.name} className="w-full h-full object-cover" />
                                    ) : (
                                        <Zap className="w-10 h-10 text-primary" />
                                    )}
                                </div>

                                <div className="flex-1">
                                    <div className="flex items-start justify-between mb-2">
                                        <div>
                                            <h1 className="text-3xl font-bold text-foreground mb-2">{integration.name}</h1>
                                            <div className="flex items-center gap-4 text-sm text-muted-foreground">
                                                <span className="flex items-center gap-1">
                                                    <Star className="w-4 h-4 text-yellow-500 fill-yellow-500" />
                                                    {integration.rating.toFixed(1)} ({integration.review_count} reviews)
                                                </span>
                                                <span className="flex items-center gap-1">
                                                    <Download className="w-4 h-4" />
                                                    {integration.total_installs.toLocaleString()} installs
                                                </span>
                                            </div>
                                        </div>
                                        <button className="p-2 rounded-full hover:bg-white/5 transition-colors text-muted-foreground hover:text-foreground">
                                            <Share2 className="w-5 h-5" />
                                        </button>
                                    </div>
                                    <p className="text-lg text-muted-foreground leading-relaxed">
                                        {integration.short_description}
                                    </p>
                                </div>
                            </div>
                        </GlassCard>

                        {/* Screenshots Carousel */}
                        {integration.screenshots && integration.screenshots.length > 0 && (
                            <div className="space-y-4">
                                <h2 className="text-xl font-semibold text-foreground">Preview</h2>
                                <div className="flex gap-4 overflow-x-auto pb-4 scrollbar-hide">
                                    {integration.screenshots.map((url, i) => (
                                        <div key={i} className="w-[400px] aspect-video rounded-xl overflow-hidden border border-white/10 shrink-0 shadow-lg">
                                            <img src={url} alt={`Screenshot ${i + 1}`} className="w-full h-full object-cover" />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Description & Capabilities */}
                        <div className="grid md:grid-cols-2 gap-8">
                            <div className="space-y-4">
                                <h2 className="text-xl font-semibold text-foreground">About this integration</h2>
                                <div className="prose prose-invert max-w-none text-muted-foreground">
                                    <p>{integration.long_description || integration.short_description}</p>
                                </div>
                            </div>

                            <div className="space-y-4">
                                <h2 className="text-xl font-semibold text-foreground">Capabilities</h2>
                                <div className="space-y-3">
                                    {integration.capabilities?.map((cap, i) => (
                                        <div key={i} className="flex items-start gap-3">
                                            <CheckCircle className="w-5 h-5 text-primary shrink-0 mt-0.5" />
                                            <span className="text-muted-foreground">{cap}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Sidebar - Pricing & Actions */}
                    <div className="space-y-6">
                        <GlassCard className="p-6 sticky top-24">
                            <div className="mb-6">
                                <div className="text-sm text-muted-foreground mb-1">Price</div>
                                <div className="flex items-baseline gap-2">
                                    {integration.pricing_model === 'free' ? (
                                        <span className="text-3xl font-bold text-foreground">Free</span>
                                    ) : (
                                        <>
                                            <span className="text-3xl font-bold text-foreground">
                                                {integration.currency === 'NGN' ? 'â‚¦' : '$'}
                                                {integration.price.toLocaleString()}
                                            </span>
                                            {integration.pricing_model === 'subscription' && (
                                                <span className="text-muted-foreground">/month</span>
                                            )}
                                        </>
                                    )}
                                </div>
                            </div>

                            <button
                                onClick={handlePurchase}
                                disabled={purchasing}
                                className="w-full py-4 rounded-xl bg-primary text-primary-foreground font-semibold text-lg hover:bg-primary/90 transition-all shadow-lg shadow-primary/20 mb-4 flex items-center justify-center gap-2 disabled:opacity-70 disabled:cursor-not-allowed"
                            >
                                {purchasing ? (
                                    <div className="w-5 h-5 border-2 border-current border-t-transparent rounded-full animate-spin" />
                                ) : (
                                    <>
                                        {integration.pricing_model === 'free' ? (
                                            <>
                                                <Download className="w-5 h-5" />
                                                Install Now
                                            </>
                                        ) : (
                                            <>
                                                <CreditCard className="w-5 h-5" />
                                                Purchase Now
                                            </>
                                        )}
                                    </>
                                )}
                            </button>

                            <div className="space-y-4 pt-6 border-t border-white/10">
                                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                    <Shield className="w-4 h-4 text-emerald-500" />
                                    <span>Verified by BotHive</span>
                                </div>
                                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                    <Lock className="w-4 h-4 text-blue-500" />
                                    <span>Secure Payment via Paystack</span>
                                </div>
                                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                    <Globe className="w-4 h-4" />
                                    <span>Works globally</span>
                                </div>
                            </div>
                        </GlassCard>

                        <GlassCard className="p-6">
                            <h3 className="font-semibold text-foreground mb-4">Developer</h3>
                            <div className="flex items-center gap-3 mb-4">
                                <div className="w-10 h-10 rounded-full bg-muted flex items-center justify-center">
                                    <span className="font-bold text-muted-foreground">D</span>
                                </div>
                                <div>
                                    <div className="font-medium text-foreground">Developer Name</div>
                                    <div className="text-xs text-muted-foreground">Joined 2024</div>
                                </div>
                            </div>
                            <button className="w-full py-2 rounded-lg border border-white/10 hover:bg-white/5 transition-colors text-sm font-medium flex items-center justify-center gap-2">
                                <MessageSquare className="w-4 h-4" />
                                Contact Developer
                            </button>
                        </GlassCard>
                    </div>
                </div>
            </div>
        </HeroBackground>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Search, Star, Download, ArrowRight, Sparkles, Zap, Globe, Code, Database, MessageSquare, Calendar, Mail, Github, Video, Music, FileText, Table, Lock } from 'lucide-react';
import { HeroBackground } from '@/components/HeroBackground';
import { GlassCard } from '@/components/ui/GlassCard';
import ThemeToggle from '@/components/ThemeToggle';
import { useRouter } from 'next/navigation';
import Link from 'next/link';

interface Integration {
    id: string;
    name: string;
    slug: string;
    short_description: string;
    category: string;
    icon_url: string;
    rating: number;
    total_installs: number;
    pricing_model: 'free' | 'one_time' | 'subscription';
    price: number;
    currency: string;
    tags: string[];
    developer_id: string; // To check for "Official" status if needed
}

interface HiveStats {
    totalHiveTools: number;
    officialTools: number;
    communityTools: number;
    categories: Array<{ category: string; count: number; icon: string }>;
}

const categoryIcons: Record<string, any> = {
    communication: MessageSquare,
    productivity: Calendar,
    data: Database,
    ai: Sparkles,
    social: Globe,
    finance: Database,
    developer: Code,
    entertainment: Video,
    other: Zap,
};

export default function HiveToolsPage() {
    const [integrations, setIntegrations] = useState<Integration[]>([]);
    const [stats, setStats] = useState<HiveStats | null>(null);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

    useEffect(() => {
        fetchIntegrations();
    }, [selectedCategory]);

    const fetchIntegrations = async () => {
        try {
            const params = new URLSearchParams();
            if (selectedCategory) params.append('category', selectedCategory);

            const response = await fetch(`/api/hivetools?${params}`);
            const data = await response.json();

            setIntegrations(data.hivetools || []);
            setStats(data.stats);
        } catch (error) {
            console.error('Failed to fetch integrations:', error);
        } finally {
            setLoading(false);
        }
    };

    const filteredTools = integrations.filter(tool => {
        if (!searchQuery) return true;
        const query = searchQuery.toLowerCase();
        return (
            tool.name.toLowerCase().includes(query) ||
            tool.short_description.toLowerCase().includes(query) ||
            tool.tags?.some(tag => tag.toLowerCase().includes(query))
        );
    });

    return (
        <HeroBackground className="min-h-screen w-full min-h-screen overflow-hidden pb-6 pt-16 sm:pt-24 mb-10 transition-colors duration-300">
            <div className="relative z-10">
                {/* Header / Hero Section */}
                <div className="border-b border-border/40 bg-background/80 backdrop-blur-md">
                    <div className="max-w-6xl mx-auto px-6 py-6 flex justify-between items-start">
                        <div className="pt-14">
                            <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-[#5B21B6]/20 border border-[#5B21B6]/30 text-[#A78BFA] text-sm mb-6 shadow-sm">
                                <Sparkles className="w-4 h-4" />
                                <span>The Integration Marketplace</span>
                            </div>

                            <h1 className="text-6xl font-bold text-foreground mb-4 tracking-tight">
                                HiveTools
                            </h1>
                            <p className="text-xl text-muted-foreground max-w-2xl">
                                Connect your favorite tools. Install with one click and automate everything.
                            </p>

                            {/* Stats */}
                            {stats && (
                                <div className="flex items-center gap-8 mt-8 text-sm">
                                    <div>
                                        <span className="text-foreground font-semibold">{stats.totalHiveTools}</span>
                                        <span className="text-muted-foreground ml-2">integrations</span>
                                    </div>
                                    <div>
                                        <span className="text-foreground font-semibold">{stats.officialTools}</span>
                                        <span className="text-muted-foreground ml-2">official</span>
                                    </div>
                                    <div>
                                        <span className="text-foreground font-semibold">{stats.communityTools}</span>
                                        <span className="text-muted-foreground ml-2">community</span>
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Theme Toggle */}
                        <div className="pt-4">
                            <ThemeToggle />
                        </div>
                    </div>
                </div>

                {/* Search */}
                <div className="border-b border-border/40 bg-muted/30 backdrop-blur-sm">
                    <div className="max-w-6xl mx-auto px-6 py-8">
                        <div className="relative group">
                            <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground group-focus-within:text-[#6D28D9] transition-colors" />
                            <input
                                type="text"
                                placeholder="Search integrations..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="w-full pl-12 pr-4 py-3 bg-background/50 border border-border/50 rounded-xl text-foreground placeholder-muted-foreground focus:outline-none focus:ring-2 focus:ring-[#6D28D9]/50 focus:bg-background transition-all"
                            />
                        </div>
                    </div>
                </div>

                {/* Categories */}
                <div className="border-b border-border/40 bg-muted/10">
                    <div className="max-w-6xl mx-auto px-6 py-6">
                        <div className="flex items-center gap-2 overflow-x-auto pb-2 scrollbar-hide">
                            <CategoryButton
                                icon={Zap}
                                label="All"
                                active={!selectedCategory}
                                onClick={() => setSelectedCategory(null)}
                            />
                            {stats?.categories.map((cat) => {
                                const Icon = categoryIcons[cat.category] || Zap;
                                return (
                                    <CategoryButton
                                        key={cat.category}
                                        icon={Icon}
                                        label={cat.category}
                                        active={selectedCategory === cat.category}
                                        onClick={() => setSelectedCategory(cat.category)}
                                    />
                                );
                            })}
                        </div>
                    </div>
                </div>

                {/* Tools Grid */}
                <div className="max-w-6xl mx-auto px-6 py-12">
                    {loading ? (
                        <div className="flex items-center justify-center py-20">
                            <div className="w-8 h-8 border-2 border-[#6D28D9] border-t-transparent rounded-full animate-spin" />
                        </div>
                    ) : filteredTools.length === 0 ? (
                        <div className="text-center py-20">
                            <p className="text-muted-foreground">No integrations found</p>
                        </div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                            {filteredTools.map((tool) => (
                                <Link key={tool.id} href={`/hivetools/${tool.slug}`}>
                                    <ToolCard tool={tool} />
                                </Link>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </HeroBackground>
    );
}

function CategoryButton({ icon: Icon, label, active, onClick }: any) {
    return (
        <button
            onClick={onClick}
            className={`flex items-center gap-2 px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all ${active
                ? 'bg-[#6D28D9] text-white shadow-md shadow-[#6D28D9]/20'
                : 'bg-background/50 text-muted-foreground hover:bg-background hover:text-foreground border border-border/50'
                }`}
        >
            <Icon className="w-4 h-4" />
            <span className="capitalize">{label}</span>
        </button>
    );
}

function ToolCard({ tool }: { tool: Integration }) {
    return (
        <GlassCard className="group p-6 cursor-pointer hover:shadow-lg hover:shadow-[#6D28D9]/10 hover:border-[#6D28D9]/30 h-full flex flex-col">
            {/* Gradient overlay on hover */}
            <div className="absolute inset-0 bg-gradient-to-br from-[#6D28D9]/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500" />

            <div className="relative z-10 flex-1">
                {/* Icon & Badge */}
                <div className="flex items-start justify-between mb-4">
                    <div className="w-14 h-14 bg-[#6D28D9]/10 border border-[#6D28D9]/20 rounded-xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300 shadow-sm overflow-hidden">
                        {tool.icon_url ? (
                            <img src={tool.icon_url} alt={tool.name} className="w-full h-full object-cover" />
                        ) : (
                            <Zap className="w-7 h-7 text-[#A78BFA]" />
                        )}
                    </div>
                    {/* TODO: Add logic for 'Official' badge based on developer_id or a flag */}
                    {tool.pricing_model !== 'free' && (
                        <span className="px-2.5 py-1 bg-[#6D28D9]/10 border border-[#6D28D9]/20 text-[#A78BFA] text-xs font-medium rounded-full">
                            {tool.currency === 'NGN' ? 'â‚¦' : '$'}{tool.price}
                        </span>
                    )}
                </div>

                {/* Content */}
                <h3 className="text-lg font-semibold text-foreground mb-2 group-hover:text-[#A78BFA] transition-colors">
                    {tool.name}
                </h3>
                <p className="text-sm text-muted-foreground mb-4 line-clamp-2 leading-relaxed">
                    {tool.short_description}
                </p>

                {/* Tags */}
                {tool.tags && tool.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1.5 mb-4">
                        {tool.tags.slice(0, 3).map((tag) => (
                            <span key={tag} className="px-2 py-0.5 bg-muted border border-border/50 text-muted-foreground text-xs rounded">
                                {tag}
                            </span>
                        ))}
                    </div>
                )}
            </div>

            {/* Footer */}
            <div className="relative z-10 flex items-center justify-between pt-4 border-t border-border/40 mt-auto">
                <div className="flex items-center gap-4 text-xs text-muted-foreground">
                    <div className="flex items-center gap-1">
                        <Star className="w-3.5 h-3.5 fill-yellow-500 text-yellow-500" />
                        <span className="font-medium text-foreground">{tool.rating.toFixed(1)}</span>
                    </div>
                    <div className="flex items-center gap-1">
                        <Download className="w-3.5 h-3.5" />
                        <span className="text-foreground">
                            {tool.total_installs > 1000
                                ? `${(tool.total_installs / 1000).toFixed(1)}k`
                                : tool.total_installs}
                        </span>
                    </div>
                </div>

                <ArrowRight className="w-4 h-4 text-muted-foreground group-hover:text-[#6D28D9] group-hover:translate-x-1 transition-all" />
            </div>
        </GlassCard>
    );
}

"use client";

import { useState, useEffect } from "react";
import { useParams, useRouter } from "next/navigation";
import Link from "next/link";
import { useTheme } from "@/lib/theme-context";
import { cn } from "@/lib/utils";
import {
    ArrowLeft,
    Code,
    Globe,
    Zap,
    Star,
    Copy,
    Check,
    Play,
    Settings,
    ExternalLink,
    Calendar,
    User,
    Box,
    Shield,
    Sparkles,
    Download,
    Loader2,
    CheckCircle,
} from "lucide-react";
import { IntegrationTestRunner } from "@/components/IntegrationTestRunner";

interface Integration {
    id: string;
    name: string;
    slug: string;
    description: string;
    icon_url: string;
    banner_url?: string;
    category: string;
    auth_type: string;
    is_official: boolean;
    is_verified: boolean;
    install_count: number;
    status: string;
    hivelang_code: string;
    created_at?: string;
    capabilities: Array<{
        name: string;
        description?: string;
        params?: string[];
    }>;
    documentation_url?: string;
    developer_id?: string;
}

export default function IntegrationDetailPage() {
    const params = useParams();
    const router = useRouter();
    const { isDark } = useTheme();
    const [integration, setIntegration] = useState<Integration | null>(null);
    const [loading, setLoading] = useState(true);
    const [copied, setCopied] = useState(false);
    const [showTestRunner, setShowTestRunner] = useState(false);
    const [installing, setInstalling] = useState(false);
    const [installed, setInstalled] = useState(false);

    useEffect(() => {
        if (params.id) {
            fetchIntegration(params.id as string);
        }
    }, [params.id]);

    async function fetchIntegration(id: string) {
        try {
            setLoading(true);
            const res = await fetch(`/api/integrations/${id}`);
            if (!res.ok) {
                router.push("/integrations");
                return;
            }
            const data = await res.json();
            setIntegration(data.integration);
        } catch (error) {
            console.error("Error fetching integration:", error);
            router.push("/integrations");
        } finally {
            setLoading(false);
        }
    }

    const copyCode = () => {
        if (integration?.hivelang_code) {
            navigator.clipboard.writeText(integration.hivelang_code);
            setCopied(true);
            setTimeout(() => setCopied(false), 2000);
        }
    };

    const handleInstall = async () => {
        if (!integration || installing || installed) return;

        setInstalling(true);
        try {
            const res = await fetch(`/api/integrations/${integration.id}/install`, {
                method: "POST",
            });

            if (res.ok) {
                setInstalled(true);
                // Update local install count
                setIntegration(prev => prev ? {
                    ...prev,
                    install_count: prev.install_count + 1
                } : null);
            }
        } catch (error) {
            console.error("Error installing integration:", error);
        } finally {
            setInstalling(false);
        }
    };

    const textPrimary = isDark ? "text-white" : "text-gray-900";
    const textSecondary = isDark ? "text-gray-400" : "text-gray-600";
    const textTertiary = isDark ? "text-gray-500" : "text-gray-500";
    const bgCard = isDark ? "bg-white/5 border-white/10" : "bg-white border-gray-200";

    if (loading) {
        return (
            <div className={cn("min-h-screen", isDark ? "bg-[#0A0A0A]" : "bg-gray-50")}>
                <div className="max-w-4xl mx-auto px-6 py-20">
                    <div className="animate-pulse space-y-6">
                        <div className={cn("h-48 rounded-2xl", isDark ? "bg-white/5" : "bg-gray-100")} />
                        <div className={cn("h-64 rounded-2xl", isDark ? "bg-white/5" : "bg-gray-100")} />
                    </div>
                </div>
            </div>
        );
    }

    if (!integration) {
        return null;
    }

    const createdDate = integration.created_at
        ? new Date(integration.created_at).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        })
        : 'Recently';

    return (
        <div className={cn("min-h-screen relative overflow-hidden", isDark ? "bg-[#0A0A0A]" : "bg-gray-50")}>
            {/* Background gradient */}
            {isDark && (
                <>
                    <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[1200px] h-[600px] bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-purple-600/30 via-purple-900/20 to-transparent pointer-events-none blur-3xl" />
                    <div className="absolute top-40 right-0 w-[600px] h-[400px] bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-blue-600/15 via-indigo-800/10 to-transparent pointer-events-none blur-2xl" />
                </>
            )}

            {/* Banner */}
            <div className="relative h-48 md:h-64 overflow-hidden">
                {integration.banner_url ? (
                    <img
                        src={integration.banner_url}
                        alt={`${integration.name} banner`}
                        className="w-full h-full object-cover"
                    />
                ) : (
                    <div className={cn(
                        "w-full h-full",
                        "bg-gradient-to-br from-purple-600/40 via-indigo-600/30 to-blue-600/20"
                    )} />
                )}
                {/* Overlay gradient */}
                <div className={cn(
                    "absolute inset-0 bg-gradient-to-t",
                    isDark ? "from-[#0A0A0A] via-transparent to-transparent" : "from-gray-50 via-transparent to-transparent"
                )} />

                {/* Back button on banner */}
                <Link
                    href="/integrations"
                    className={cn(
                        "absolute top-6 left-6 flex items-center gap-2 px-4 py-2 rounded-xl backdrop-blur-md transition-all",
                        isDark
                            ? "bg-black/40 text-white/80 hover:bg-black/60"
                            : "bg-white/40 text-gray-800 hover:bg-white/60"
                    )}
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back
                </Link>
            </div>

            <div className="relative max-w-4xl mx-auto px-6 -mt-20">
                {/* Receipt-style Header Card */}
                <div className={cn(
                    "relative p-8 rounded-3xl border backdrop-blur-xl shadow-2xl",
                    isDark
                        ? "bg-[#0C1323]/90 border-white/10"
                        : "bg-white/95 border-gray-200"
                )}>
                    {/* Decorative top bar */}
                    <div className="absolute top-0 left-1/2 -translate-x-1/2 w-32 h-1 rounded-b-full bg-gradient-to-r from-purple-500 via-indigo-500 to-blue-500" />

                    <div className="flex flex-col md:flex-row items-start gap-6">
                        {/* Icon */}
                        <div className={cn(
                            "relative w-24 h-24 rounded-2xl flex items-center justify-center overflow-hidden",
                            "bg-gradient-to-br from-purple-500/20 via-indigo-500/10 to-blue-500/20",
                            "border-2",
                            isDark ? "border-white/10" : "border-gray-100"
                        )}>
                            {integration.icon_url ? (
                                <img
                                    src={integration.icon_url}
                                    alt={integration.name}
                                    className="w-full h-full object-cover"
                                />
                            ) : (
                                <Zap className={cn("w-10 h-10 text-purple-500")} />
                            )}
                            {/* Shine effect */}
                            <div className="absolute inset-0 bg-gradient-to-tr from-transparent via-white/10 to-transparent" />
                        </div>

                        {/* Info */}
                        <div className="flex-1">
                            <div className="flex flex-wrap items-center gap-3 mb-2">
                                <h1 className={cn("text-3xl font-bold", textPrimary)}>
                                    {integration.name}
                                </h1>
                                {integration.is_official && (
                                    <span className="flex items-center gap-1.5 px-3 py-1 rounded-full bg-gradient-to-r from-purple-500/20 to-indigo-500/20 border border-purple-500/30">
                                        <Star className="w-3.5 h-3.5 text-purple-400 fill-purple-400" />
                                        <span className="text-xs font-semibold text-purple-400">Official</span>
                                    </span>
                                )}
                                <span className={cn(
                                    "px-3 py-1 rounded-full text-xs font-semibold capitalize",
                                    integration.status === "active"
                                        ? "bg-emerald-500/10 text-emerald-400 border border-emerald-500/20"
                                        : "bg-amber-500/10 text-amber-400 border border-amber-500/20"
                                )}>
                                    {integration.status}
                                </span>
                            </div>
                            <p className={cn("text-lg mb-4", textSecondary)}>{integration.description}</p>

                            {/* Stats row */}
                            <div className="flex flex-wrap gap-6 text-sm">
                                <div className="flex items-center gap-2">
                                    <Box className={cn("w-4 h-4", textTertiary)} />
                                    <span className={textSecondary}>
                                        <span className={cn("font-semibold", textPrimary)}>{integration.install_count}</span> installs
                                    </span>
                                </div>
                                <div className="flex items-center gap-2">
                                    <Shield className={cn("w-4 h-4", textTertiary)} />
                                    <span className={cn("capitalize", textSecondary)}>
                                        {integration.auth_type.replace("_", " ")} auth
                                    </span>
                                </div>
                                <div className="flex items-center gap-2">
                                    <Globe className={cn("w-4 h-4", textTertiary)} />
                                    <span className={cn("capitalize", textSecondary)}>{integration.category}</span>
                                </div>
                                <div className="flex items-center gap-2">
                                    <Calendar className={cn("w-4 h-4", textTertiary)} />
                                    <span className={textSecondary}>{createdDate}</span>
                                </div>
                            </div>

                            {/* Install Button */}
                            <div className="mt-6 pt-6 border-t border-white/5">
                                <button
                                    onClick={handleInstall}
                                    disabled={installing || installed}
                                    className={cn(
                                        "w-full flex items-center justify-center gap-2 px-6 py-3 rounded-xl font-semibold transition-all",
                                        installed
                                            ? "bg-emerald-500/20 text-emerald-400 border border-emerald-500/30 cursor-default"
                                            : "bg-gradient-to-r from-purple-500 to-indigo-500 text-white shadow-lg shadow-purple-500/25 hover:shadow-xl hover:shadow-purple-500/30 hover:scale-[1.02]",
                                        installing && "opacity-70 cursor-wait"
                                    )}
                                >
                                    {installing ? (
                                        <>
                                            <Loader2 className="w-5 h-5 animate-spin" />
                                            Installing...
                                        </>
                                    ) : installed ? (
                                        <>
                                            <CheckCircle className="w-5 h-5" />
                                            Installed
                                        </>
                                    ) : (
                                        <>
                                            <Download className="w-5 h-5" />
                                            Install Integration
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>

                    {/* Receipt-style divider */}
                    <div className="my-8 border-t border-dashed opacity-20" style={{
                        borderImage: isDark
                            ? 'repeating-linear-gradient(90deg, white 0, white 8px, transparent 8px, transparent 16px) 1'
                            : 'repeating-linear-gradient(90deg, #666 0, #666 8px, transparent 8px, transparent 16px) 1'
                    }} />

                    {/* Integration ID (like a receipt number) */}
                    <div className="flex items-center justify-between">
                        <div>
                            <p className={cn("text-xs uppercase tracking-wider mb-1", textTertiary)}>Integration ID</p>
                            <p className={cn("font-mono text-sm", textSecondary)}>{integration.id}</p>
                        </div>
                        <div className="text-right">
                            <p className={cn("text-xs uppercase tracking-wider mb-1", textTertiary)}>Slug</p>
                            <p className={cn("font-mono text-sm", textSecondary)}>{integration.slug}</p>
                        </div>
                    </div>
                </div>

                {/* Capabilities */}
                {integration.capabilities && integration.capabilities.length > 0 && (
                    <div className={cn("mt-8 p-6 rounded-2xl border", bgCard)}>
                        <h2 className={cn("text-lg font-semibold mb-4 flex items-center gap-2", textPrimary)}>
                            <Sparkles className="w-5 h-5 text-purple-500" />
                            Capabilities
                        </h2>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                            {integration.capabilities.map((cap, idx) => (
                                <div key={idx} className={cn(
                                    "p-4 rounded-xl border transition-all hover:scale-[1.02]",
                                    isDark
                                        ? "bg-white/5 border-white/5 hover:border-purple-500/30"
                                        : "bg-gray-50 border-gray-100 hover:border-purple-500/30"
                                )}>
                                    <div className="flex items-center gap-2 mb-1">
                                        <Code className={cn("w-4 h-4 text-purple-500")} />
                                        <span className={cn("font-semibold", textPrimary)}>{cap.name}</span>
                                    </div>
                                    {cap.description && (
                                        <p className={cn("text-sm", textSecondary)}>{cap.description}</p>
                                    )}
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* HiveLang Code */}
                {integration.hivelang_code && (
                    <div className={cn("mt-8 p-6 rounded-2xl border", bgCard)}>
                        <div className="flex items-center justify-between mb-4">
                            <h2 className={cn("text-lg font-semibold flex items-center gap-2", textPrimary)}>
                                <Code className="w-5 h-5 text-purple-500" />
                                HiveLang Code
                            </h2>
                            <div className="flex items-center gap-2">
                                <button
                                    onClick={() => setShowTestRunner(!showTestRunner)}
                                    className={cn(
                                        "flex items-center gap-2 px-4 py-2 rounded-xl text-sm font-medium transition-all",
                                        showTestRunner
                                            ? "bg-purple-500/20 text-purple-400 border border-purple-500/30"
                                            : cn(
                                                isDark ? "bg-white/5 text-white/70" : "bg-gray-100 text-gray-700",
                                                "hover:opacity-80"
                                            )
                                    )}
                                >
                                    <Play className="w-4 h-4" />
                                    Test
                                </button>
                                <button
                                    onClick={copyCode}
                                    className={cn(
                                        "flex items-center gap-2 px-4 py-2 rounded-xl text-sm font-medium",
                                        isDark ? "bg-white/5 text-white/70" : "bg-gray-100 text-gray-700",
                                        "hover:opacity-80 transition-opacity"
                                    )}
                                >
                                    {copied ? <Check className="w-4 h-4 text-green-500" /> : <Copy className="w-4 h-4" />}
                                    {copied ? "Copied!" : "Copy"}
                                </button>
                            </div>
                        </div>

                        <pre className={cn(
                            "p-4 rounded-xl overflow-x-auto text-sm font-mono",
                            isDark ? "bg-black/50" : "bg-gray-100"
                        )}>
                            <code className={textSecondary}>{integration.hivelang_code}</code>
                        </pre>

                        {/* Test Runner */}
                        {showTestRunner && (
                            <div className="mt-6 pt-6 border-t border-white/10">
                                <IntegrationTestRunner hiveLangCode={integration.hivelang_code} />
                            </div>
                        )}
                    </div>
                )}

                {/* Documentation Link */}
                {integration.documentation_url && (
                    <Link
                        href={integration.documentation_url}
                        target="_blank"
                        className={cn(
                            "mt-8 flex items-center justify-center gap-2 p-4 rounded-xl border transition-all",
                            bgCard,
                            "hover:border-purple-500/50"
                        )}
                    >
                        <ExternalLink className={cn("w-4 h-4", textSecondary)} />
                        <span className={textSecondary}>View Documentation</span>
                    </Link>
                )}

                {/* Bottom spacer */}
                <div className="h-20" />
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { useTheme } from "@/lib/theme-context";
import { cn } from "@/lib/utils";
import Link from "next/link";
import {
    Search,
    Plus,
    Zap,
    Globe,
    Mail,
    Calendar,
    FileText,
    MessageSquare,
    Image,
    Code,
    ChevronRight,
    Filter,
    Star,
} from "lucide-react";

interface Integration {
    id: string;
    name: string;
    slug: string;
    description: string;
    icon_url: string;
    category: string;
    auth_type: string;
    is_official: boolean;
    is_verified: boolean;
    install_count: number;
    status: string;
    capabilities: Array<{
        name: string;
        description: string;
    }>;
}

const CATEGORIES = [
    { value: "all", label: "All", icon: Globe },
    { value: "productivity", label: "Productivity", icon: Zap },
    { value: "communication", label: "Communication", icon: MessageSquare },
    { value: "media", label: "Media", icon: Image },
    { value: "ai", label: "AI", icon: Code },
    { value: "social", label: "Social", icon: Mail },
] as const;

const CATEGORY_ICONS: Record<string, any> = {
    productivity: Zap,
    communication: MessageSquare,
    media: Image,
    ai: Code,
    social: Mail,
    education: FileText,
    other: Globe,
};

export default function IntegrationsPage() {
    const { isDark } = useTheme();
    const [integrations, setIntegrations] = useState<Integration[]>([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("all");
    const [showOfficial, setShowOfficial] = useState(false);

    useEffect(() => {
        fetchIntegrations();
    }, [selectedCategory, showOfficial]);

    async function fetchIntegrations() {
        try {
            setLoading(true);
            const params = new URLSearchParams();
            if (selectedCategory !== "all") params.set("category", selectedCategory);
            if (showOfficial) params.set("official", "true");

            const res = await fetch(`/api/integrations?${params}`);
            const data = await res.json();
            setIntegrations(data.integrations || []);
        } catch (error) {
            console.error("Error fetching integrations:", error);
        } finally {
            setLoading(false);
        }
    }

    const filteredIntegrations = integrations.filter((int) =>
        search
            ? int.name.toLowerCase().includes(search.toLowerCase()) ||
            int.description?.toLowerCase().includes(search.toLowerCase())
            : true
    );

    const textPrimary = isDark ? "text-white" : "text-gray-900";
    const textSecondary = isDark ? "text-gray-400" : "text-gray-600";
    const textTertiary = isDark ? "text-gray-500" : "text-gray-500";
    const bgCard = isDark
        ? "bg-white/5 border-white/10"
        : "bg-white border-gray-200";
    const bgHover = isDark ? "hover:bg-white/10" : "hover:bg-gray-50";

    return (
        <div className={cn("min-h-screen relative overflow-hidden", isDark ? "bg-[#0A0A0A]" : "bg-gray-50")}>
            {/* Enhanced Purple Gradient */}
            {isDark && (
                <>
                    {/* Main top glow */}
                    <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[1200px] h-[600px] bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-purple-600/30 via-purple-900/20 to-transparent pointer-events-none blur-3xl" />
                    
                    {/* Secondary accent glow */}
                    <div className="absolute top-40 right-0 w-[800px] h-[400px] bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-blue-600/20 via-purple-800/10 to-transparent pointer-events-none blur-2xl" />
                    
                    {/* Subtle left accent */}
                    <div className="absolute top-60 left-0 w-[600px] h-[300px] bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-purple-700/15 to-transparent pointer-events-none blur-2xl" />
                </>
            )}

            <div className="relative max-w-7xl mx-auto px-6 py-20">
                {/* Header */}
                <div className="mb-12">
                    <div className="flex items-center justify-between mb-6">
                        <div>
                            <h1
                                className={cn(
                                    "text-4xl font-bold mb-2 tracking-tight",
                                    textPrimary
                                )}
                            >
                                Integrations
                            </h1>
                            <p className={cn("text-lg", textSecondary)}>
                                Connect your bots to any service
                            </p>
                        </div>

                        <Link
                            href="/integrations/new"
                            className={cn(
                                "flex items-center gap-2 px-6 py-3 rounded-xl font-medium transition-all",
                                isDark
                                    ? "bg-white text-black hover:bg-gray-100"
                                    : "bg-black text-white hover:bg-gray-800"
                            )}
                        >
                            <Plus className="w-4 h-4" />
                            Create Integration
                        </Link>
                    </div>

                    {/* Search & Filters */}
                    <div className="flex flex-col sm:flex-row gap-4">
                        {/* Search */}
                        <div className="flex-1 relative">
                            <Search
                                className={cn(
                                    "absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5",
                                    textTertiary
                                )}
                            />
                            <input
                                type="text"
                                placeholder="Search integrations..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                className={cn(
                                    "w-full pl-12 pr-4 py-3 rounded-xl border transition-colors",
                                    isDark
                                        ? "bg-white/5 border-white/10 text-white placeholder:text-gray-500"
                                        : "bg-white border-gray-200 text-gray-900 placeholder:text-gray-400",
                                    "focus:outline-none focus:ring-2 focus:ring-purple-500/50"
                                )}
                            />
                        </div>

                        {/* Official Filter */}
                        <button
                            onClick={() => setShowOfficial(!showOfficial)}
                            className={cn(
                                "flex items-center gap-2 px-4 py-3 rounded-xl border transition-all",
                                showOfficial
                                    ? isDark
                                        ? "bg-white/10 border-white/20"
                                        : "bg-gray-100 border-gray-300"
                                    : bgCard,
                                textSecondary,
                                "hover:border-purple-500/50"
                            )}
                        >
                            <Filter className="w-4 h-4" />
                            {showOfficial ? "Official Only" : "All Integrations"}
                        </button>
                    </div>

                    {/* Categories */}
                    <div className="flex gap-2 mt-4 overflow-x-auto pb-2">
                        {CATEGORIES.map((cat) => {
                            const Icon = cat.icon;
                            const isSelected = selectedCategory === cat.value;

                            return (
                                <button
                                    key={cat.value}
                                    onClick={() => setSelectedCategory(cat.value)}
                                    className={cn(
                                        "flex items-center gap-2 px-4 py-2 rounded-lg border transition-all whitespace-nowrap",
                                        isSelected
                                            ? isDark
                                                ? "bg-white/10 border-white/20 text-white"
                                                : "bg-black text-white border-black"
                                            : cn(bgCard, textSecondary, bgHover)
                                    )}
                                >
                                    <Icon className="w-4 h-4" />
                                    {cat.label}
                                </button>
                            );
                        })}
                    </div>
                </div>

                {/* Integrations Grid */}
                {loading ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {[1, 2, 3, 4, 5, 6].map((i) => (
                            <div
                                key={i}
                                className={cn(
                                    "h-64 rounded-2xl border animate-pulse",
                                    isDark ? "bg-white/5 border-white/10" : "bg-gray-100 border-gray-200"
                                )}
                            />
                        ))}
                    </div>
                ) : filteredIntegrations.length === 0 ? (
                    <div className="text-center py-20">
                        <Globe className={cn("w-16 h-16 mx-auto mb-4", textTertiary)} />
                        <h3 className={cn("text-xl font-semibold mb-2", textPrimary)}>
                            No integrations found
                        </h3>
                        <p className={cn("mb-6", textSecondary)}>
                            Try adjusting your search or filters
                        </p>
                        <Link
                            href="/integrations/new"
                            className={cn(
                                "inline-flex items-center gap-2 px-6 py-3 rounded-xl font-medium",
                                isDark
                                    ? "bg-white text-black hover:bg-gray-100"
                                    : "bg-black text-white hover:bg-gray-800"
                            )}
                        >
                            <Plus className="w-4 h-4" />
                            Create First Integration
                        </Link>
                    </div>
                ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {filteredIntegrations.map((integration) => {
                            const Icon = CATEGORY_ICONS[integration.category] || Globe;

                            return (
                                <Link
                                    key={integration.id}
                                    href={`/integrations/${integration.id}`}
                                    className={cn(
                                        "group relative p-6 rounded-2xl border transition-all",
                                        bgCard,
                                        bgHover,
                                        "hover:shadow-lg hover:scale-[1.02]"
                                    )}
                                >
                                    {/* Official Badge */}
                                    {integration.is_official && (
                                        <div className="absolute top-4 right-4 flex items-center gap-1 px-2 py-1 rounded-md bg-purple-500/10 border border-purple-500/20">
                                            <Star className="w-3 h-3 text-purple-500 fill-purple-500" />
                                            <span className="text-xs font-medium text-purple-500">
                                                Official
                                            </span>
                                        </div>
                                    )}

                                    {/* Icon */}
                                    <div
                                        className={cn(
                                            "w-12 h-12 rounded-xl flex items-center justify-center mb-4",
                                            isDark
                                                ? "bg-white/10 border border-white/10"
                                                : "bg-gray-100 border border-gray-200"
                                        )}
                                    >
                                        <Icon className={cn("w-6 h-6", textPrimary)} />
                                    </div>

                                    {/* Content */}
                                    <h3 className={cn("text-lg font-semibold mb-2", textPrimary)}>
                                        {integration.name}
                                    </h3>
                                    <p className={cn("text-sm mb-4 line-clamp-2", textSecondary)}>
                                        {integration.description}
                                    </p>

                                    {/* Stats */}
                                    <div className="flex items-center justify-between">
                                        <div className="flex items-center gap-4 text-xs">
                                            <span className={textTertiary}>
                                                {integration.install_count} installs
                                            </span>
                                            <span className={cn("capitalize", textTertiary)}>
                                                {integration.auth_type.replace("_", " ")}
                                            </span>
                                        </div>

                                        <ChevronRight
                                            className={cn(
                                                "w-4 h-4 transition-transform group-hover:translate-x-1",
                                                textTertiary
                                            )}
                                        />
                                    </div>

                                    {/* Capabilities count */}
                                    {integration.capabilities && integration.capabilities.length > 0 && (
                                        <div className="mt-4 pt-4 border-t border-white/5">
                                            <span className={cn("text-xs", textTertiary)}>
                                                {integration.capabilities.length} capabilities
                                            </span>
                                        </div>
                                    )}
                                </Link>
                            );
                        })}
                    </div>
                )}
            </div>
        </div>
    );
}

