"use client";

import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { ArrowRight, ArrowLeft, Check, Sparkles, Bot, MessageSquare, Zap, Globe2 } from "lucide-react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { useAppSession } from "@/lib/app-session-context";
import { supabase } from "@/lib/supabase";
import { cn } from "@/lib/utils";
import { SignUpProfileModal, type SignUpProfileValues } from "@/components/SignUpProfileModal";

type OnboardingStep = "welcome" | "profile" | "preferences" | "first-bot" | "integrations" | "complete";

interface OnboardingData {
  firstName: string;
  lastName: string;
  company: string;
  role: string;
  useCase: string;
  experience: string;
  botType: string;
  integrations: string[];
  notifications: boolean;
  newsletter: boolean;
  preferredName: string;
  pronouns: string;
  teamName: string;
  agentStyle: "technical" | "playful" | "balanced";
}

const steps = [
  { id: "welcome", title: "Welcome to Bothive!", description: "Let's set up your AI automation platform" },
  { id: "profile", title: "Tell us about yourself", description: "Help us personalize your experience" },
  { id: "preferences", title: "Your preferences", description: "Configure your workspace" },
  { id: "first-bot", title: "Create your first bot", description: "Choose a template to get started" },
  { id: "integrations", title: "Connect your tools", description: "Integrate with your existing workflow" },
  { id: "complete", title: "You're all set!", description: "Start building amazing bots" }
];

export default function OnboardingPage() {
  const router = useRouter();
  const { session } = useAppSession();
  const [currentStep, setCurrentStep] = useState<OnboardingStep>("welcome");
  const [isLoading, setIsLoading] = useState(false);
  const [onboardingData, setOnboardingData] = useState<OnboardingData>({
    firstName: "",
    lastName: "",
    company: "",
    role: "",
    useCase: "",
    experience: "",
    botType: "",
    integrations: [],
    notifications: true,
    newsletter: true,
    preferredName: "",
    pronouns: "",
    teamName: "",
    agentStyle: "balanced",
  });
  const [showProfileModal, setShowProfileModal] = useState(true);

  const stepIndex = steps.findIndex(step => step.id === currentStep);
  const progress = ((stepIndex + 1) / steps.length) * 100;

  useEffect(() => {
    // Check if user is logged in
    if (!session) {
      router.push("/auth");
      return;
    }

    // Check if user already completed onboarding
    checkOnboardingStatus();
  }, [session, router]);

  const checkOnboardingStatus = async () => {
    if (!session?.userId) return;

    try {
      const { data, error } = await supabase
        .from("user_profiles")
        .select("onboarding_completed")
        .eq("user_id", session.userId)
        .single();

      if (data?.onboarding_completed) {
        router.push("/dashboard");
      }
    } catch (error) {
      console.error("Error checking onboarding status:", error);
    }
  };

  const handleProfileModalSubmit = async (values: SignUpProfileValues) => {
    setOnboardingData((prev) => ({
      ...prev,
      preferredName: values.preferredName,
      pronouns: values.pronouns,
      teamName: values.teamName,
      agentStyle: values.favoriteAgentStyle,
    }));
    setShowProfileModal(false);
  };

  const handleProfileModalDismiss = () => {
    setShowProfileModal(false);
  };

  const handleNext = async () => {
    if (currentStep === "complete") {
      await completeOnboarding();
      return;
    }

    const nextStepIndex = stepIndex + 1;
    if (nextStepIndex < steps.length) {
      setCurrentStep(steps[nextStepIndex].id as OnboardingStep);
    }
  };

  const handlePrevious = () => {
    const prevStepIndex = stepIndex - 1;
    if (prevStepIndex >= 0) {
      setCurrentStep(steps[prevStepIndex].id as OnboardingStep);
    }
  };

  const completeOnboarding = async () => {
    setIsLoading(true);
    
    try {
      if (!session?.userId) return;

      // Save onboarding data
      const { error } = await supabase
        .from("user_profiles")
        .upsert({
          user_id: session.userId,
          first_name: onboardingData.firstName,
          last_name: onboardingData.lastName,
          company: onboardingData.company,
          role: onboardingData.role,
          use_case: onboardingData.useCase,
          experience_level: onboardingData.experience,
          preferred_bot_type: onboardingData.botType,
          integrations: onboardingData.integrations,
          notifications_enabled: onboardingData.notifications,
          newsletter_subscribed: onboardingData.newsletter,
          preferred_name: onboardingData.preferredName || onboardingData.firstName,
          preferred_pronouns: onboardingData.pronouns,
          team_name: onboardingData.teamName,
          agent_style: onboardingData.agentStyle,
          onboarding_completed: true,
          onboarding_completed_at: new Date().toISOString()
        });

      if (error) throw error;

      // Route based on role and admin email
      const userEmail = session.user.email;
      const userRole = onboardingData.role;
      
      if (userEmail === "akinlorinjeremiah@gmail.com") {
        // Admin routing
        router.push("/dashboard/admin");
      } else if (userRole === "developer") {
        // Developer routing
        router.push("/dashboard/developer");
      } else {
        // Regular user routing
        router.push("/dashboard");
      }
    } catch (error) {
      console.error("Error completing onboarding:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case "welcome":
        return <WelcomeStep data={onboardingData} setData={setOnboardingData} />;
      case "profile":
        return <ProfileStep data={onboardingData} setData={setOnboardingData} />;
      case "preferences":
        return <PreferencesStep data={onboardingData} setData={setOnboardingData} />;
      case "first-bot":
        return <FirstBotStep data={onboardingData} setData={setOnboardingData} />;
      case "integrations":
        return <IntegrationsStep data={onboardingData} setData={setOnboardingData} />;
      case "complete":
        return <CompleteStep data={onboardingData} />;
      default:
        return null;
    }
  };

  return (
    <>
      <SignUpProfileModal
        open={showProfileModal}
        initialValues={{
          preferredName: onboardingData.preferredName,
          pronouns: onboardingData.pronouns,
          teamName: onboardingData.teamName,
          favoriteAgentStyle: onboardingData.agentStyle,
        }}
        onSubmit={handleProfileModalSubmit}
        onClose={handleProfileModalDismiss}
      />
      <div className="min-h-screen bg-gradient-to-br from-[#05030c] via-[#0b0a18] to-[#11111b] text-white">
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_top,rgba(99,92,255,0.15),transparent_70%)]" />
      
      <div className="relative z-10 flex min-h-screen">
        {/* Progress Sidebar */}
        <div className="w-80 bg-black/30 backdrop-blur-xl border-r border-white/10 p-8">
          <div className="mb-12">
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              Bothive
            </h1>
            <p className="text-sm text-white/60 mt-2">AI Automation Platform</p>
          </div>

          <div className="space-y-4">
            {steps.map((step, index) => {
              const isCompleted = index < stepIndex;
              const isCurrent = index === stepIndex;
              
              return (
                <div
                  key={step.id}
                  className={cn(
                    "flex items-center gap-3 p-3 rounded-lg transition-all",
                    isCurrent ? "bg-white/10 border border-white/20" : "opacity-60"
                  )}
                >
                  <div
                    className={cn(
                      "w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium",
                      isCompleted 
                        ? "bg-green-500 text-white" 
                        : isCurrent 
                        ? "bg-blue-500 text-white" 
                        : "bg-white/10 text-white/60"
                    )}
                  >
                    {isCompleted ? <Check className="w-4 h-4" /> : index + 1}
                  </div>
                  <div className="flex-1">
                    <p className="text-sm font-medium">{step.title}</p>
                    <p className="text-xs text-white/60">{step.description}</p>
                  </div>
                </div>
              );
            })}
          </div>

          <div className="mt-8">
            <div className="text-xs text-white/60 mb-2">Progress</div>
            <div className="w-full bg-white/10 rounded-full h-2">
              <div 
                className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              />
            </div>
            <div className="text-xs text-white/60 mt-1">{Math.round(progress)}% Complete</div>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          <div className="flex-1 flex items-center justify-center p-8">
            <motion.div
              key={currentStep}
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              transition={{ duration: 0.3 }}
              className="w-full max-w-2xl"
            >
              {renderStepContent()}
            </motion.div>
          </div>

          {/* Navigation */}
          <div className="border-t border-white/10 p-8">
            <div className="flex items-center justify-between">
              <button
                onClick={handlePrevious}
                disabled={stepIndex === 0}
                className="flex items-center gap-2 px-4 py-2 text-white/60 hover:text-white transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <ArrowLeft className="w-4 h-4" />
                Previous
              </button>

              <div className="flex items-center gap-2 text-sm text-white/60">
                Step {stepIndex + 1} of {steps.length}
              </div>

              <button
                onClick={handleNext}
                disabled={isLoading}
                className="flex items-center gap-2 px-6 py-2 bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-lg hover:from-blue-600 hover:to-purple-600 transition-all disabled:opacity-50"
              >
                {isLoading ? (
                  "Saving..."
                ) : currentStep === "complete" ? (
                  <>
                    Go to Dashboard
                    <ArrowRight className="w-4 h-4" />
                  </>
                ) : (
                  <>
                    Next
                    <ArrowRight className="w-4 h-4" />
                  </>
                )}
              </button>
            </div>
          </div>
        </div>
      </div>
      </div>
    </>
  );
}

// Step Components
function WelcomeStep({ data, setData }: { data: OnboardingData; setData: (data: OnboardingData) => void }) {
  return (
    <div className="text-center space-y-8">
      <div className="w-20 h-20 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center mx-auto">
        <Sparkles className="w-10 h-10 text-white" />
      </div>
      
      <div>
        <h2 className="text-4xl font-bold mb-4">Welcome to Bothive! ðŸŽ‰</h2>
        <p className="text-xl text-white/80">
          Let's get you set up with AI-powered automation in just a few steps
        </p>
      </div>

      <div className="grid grid-cols-3 gap-6 mt-12">
        <div className="bg-white/5 rounded-xl p-6 border border-white/10">
          <Bot className="w-8 h-8 text-blue-400 mb-4" />
          <h3 className="font-semibold mb-2">Build Bots</h3>
          <p className="text-sm text-white/60">Create custom AI bots with our visual builder</p>
        </div>
        
        <div className="bg-white/5 rounded-xl p-6 border border-white/10">
          <Zap className="w-8 h-8 text-purple-400 mb-4" />
          <h3 className="font-semibold mb-2">Automate Workflows</h3>
          <p className="text-sm text-white/60">Connect your tools and automate repetitive tasks</p>
        </div>
        
        <div className="bg-white/5 rounded-xl p-6 border border-white/10">
          <Globe2 className="w-8 h-8 text-green-400 mb-4" />
          <h3 className="font-semibold mb-2">Scale Easily</h3>
          <p className="text-sm text-white/60">Deploy and manage bots across your organization</p>
        </div>
      </div>
    </div>
  );
}

function ProfileStep({ data, setData }: { data: OnboardingData; setData: (data: OnboardingData) => void }) {
  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold mb-2">Tell us about yourself</h2>
        <p className="text-white/60">Help us personalize your Bothive experience</p>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-2">First Name</label>
          <input
            type="text"
            value={data.firstName}
            onChange={(e) => setData({ ...data, firstName: e.target.value })}
            className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
            placeholder="John"
          />
        </div>
        
        <div>
          <label className="block text-sm font-medium mb-2">Last Name</label>
          <input
            type="text"
            value={data.lastName}
            onChange={(e) => setData({ ...data, lastName: e.target.value })}
            className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
            placeholder="Doe"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Company</label>
        <input
          type="text"
          value={data.company}
          onChange={(e) => setData({ ...data, company: e.target.value })}
          className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
          placeholder="Acme Corp"
        />
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Role</label>
        <select
          value={data.role}
          onChange={(e) => setData({ ...data, role: e.target.value })}
          className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
        >
          <option value="">Select your role</option>
          <option value="developer">Developer</option>
          <option value="product">Product Manager</option>
          <option value="marketing">Marketing</option>
          <option value="sales">Sales</option>
          <option value="support">Customer Support</option>
          <option value="founder">Founder/Owner</option>
          <option value="other">Other</option>
        </select>
      </div>
    </div>
  );
}

function PreferencesStep({ data, setData }: { data: OnboardingData; setData: (data: OnboardingData) => void }) {
  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold mb-2">Your preferences</h2>
        <p className="text-white/60">Configure your workspace to match your needs</p>
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">What's your primary use case?</label>
        <select
          value={data.useCase}
          onChange={(e) => setData({ ...data, useCase: e.target.value })}
          className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
        >
          <option value="">Select use case</option>
          <option value="customer-support">Customer Support</option>
          <option value="marketing">Marketing Automation</option>
          <option value="sales">Sales Assistant</option>
          <option value="development">Development Helper</option>
          <option value="personal">Personal Assistant</option>
          <option value="education">Education/Tutoring</option>
          <option value="other">Other</option>
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Experience with AI/Automation</label>
        <select
          value={data.experience}
          onChange={(e) => setData({ ...data, experience: e.target.value })}
          className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
        >
          <option value="">Select experience level</option>
          <option value="beginner">Just getting started</option>
          <option value="intermediate">Some experience</option>
          <option value="advanced">Experienced user</option>
          <option value="expert">Expert/Developer</option>
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">What type of bot do you want to build first?</label>
        <select
          value={data.botType}
          onChange={(e) => setData({ ...data, botType: e.target.value })}
          className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg focus:border-blue-500 focus:outline-none"
        >
          <option value="">Choose a bot type</option>
          <option value="communication">Email/Messaging Bot</option>
          <option value="coding">Code Assistant</option>
          <option value="social">Social Media Assistant</option>
          <option value="task">Task Manager</option>
          <option value="custom">Custom Workflow</option>
        </select>
      </div>

      <div className="space-y-4">
        <label className="flex items-center gap-3 cursor-pointer">
          <input
            type="checkbox"
            checked={data.notifications}
            onChange={(e) => setData({ ...data, notifications: e.target.checked })}
            className="w-4 h-4 text-blue-500 bg-white/5 border-white/10 rounded focus:ring-blue-500"
          />
          <span className="text-sm">Enable email notifications</span>
        </label>

        <label className="flex items-center gap-3 cursor-pointer">
          <input
            type="checkbox"
            checked={data.newsletter}
            onChange={(e) => setData({ ...data, newsletter: e.target.checked })}
            className="w-4 h-4 text-blue-500 bg-white/5 border-white/10 rounded focus:ring-blue-500"
          />
          <span className="text-sm">Subscribe to product updates and tips</span>
        </label>
      </div>
    </div>
  );
}

function FirstBotStep({ data, setData }: { data: OnboardingData; setData: (data: OnboardingData) => void }) {
  const botTemplates = [
    {
      id: "communication",
      name: "Communication Assistant",
      description: "Handle emails, messages, and customer support",
      icon: MessageSquare,
      color: "from-blue-500 to-cyan-500"
    },
    {
      id: "coding",
      name: "Code Assistant", 
      description: "Generate, review, and debug code",
      icon: Bot,
      color: "from-purple-500 to-pink-500"
    },
    {
      id: "social",
      name: "Social Media Bot",
      description: "Automate posts and engagement",
      icon: Globe2,
      color: "from-green-500 to-emerald-500"
    }
  ];

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold mb-2">Choose your first bot</h2>
        <p className="text-white/60">Select a template to get started quickly</p>
      </div>

      <div className="grid gap-4">
        {botTemplates.map((template) => {
          const Icon = template.icon;
          const isSelected = data.botType === template.id;
          
          return (
            <div
              key={template.id}
              onClick={() => setData({ ...data, botType: template.id })}
              className={cn(
                "p-6 rounded-xl border cursor-pointer transition-all",
                isSelected 
                  ? "bg-white/10 border-blue-500" 
                  : "bg-white/5 border-white/10 hover:bg-white/10"
              )}
            >
              <div className="flex items-start gap-4">
                <div className={`w-12 h-12 bg-gradient-to-r ${template.color} rounded-lg flex items-center justify-center`}>
                  <Icon className="w-6 h-6 text-white" />
                </div>
                
                <div className="flex-1">
                  <h3 className="font-semibold text-lg mb-1">{template.name}</h3>
                  <p className="text-sm text-white/60">{template.description}</p>
