                {isSelected && (
                  <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
                    <Check className="w-4 h-4 text-white" />
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      <div className="bg-blue-500/10 border border-blue-500/20 rounded-lg p-4">
        <p className="text-sm text-blue-300">
          ðŸ’¡ Don't worry! You can always create more bots and customize this one later. This is just to get you started.
        </p>
      </div>
    </div>
  );
}

function IntegrationsStep({ data, setData }: { data: OnboardingData; setData: (data: OnboardingData) => void }) {
  const integrations = [
    { id: "slack", name: "Slack", description: "Team communication" },
    { id: "discord", name: "Discord", description: "Community management" },
    { id: "email", name: "Email", description: "Email automation" },
    { id: "whatsapp", name: "WhatsApp", description: "Customer messaging" },
    { id: "twitter", name: "Twitter/X", description: "Social media posting" },
    { id: "notion", name: "Notion", description: "Documentation & tasks" }
  ];

  const toggleIntegration = (integrationId: string) => {
    if (data.integrations.includes(integrationId)) {
      setData({
        ...data,
        integrations: data.integrations.filter(id => id !== integrationId)
      });
    } else {
      setData({
        ...data,
        integrations: [...data.integrations, integrationId]
      });
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold mb-2">Connect your tools</h2>
        <p className="text-white/60">Choose the platforms you want to integrate with</p>
      </div>

      <div className="grid grid-cols-2 gap-4">
        {integrations.map((integration) => {
          const isSelected = data.integrations.includes(integration.id);
          
          return (
            <div
              key={integration.id}
              onClick={() => toggleIntegration(integration.id)}
              className={cn(
                "p-4 rounded-lg border cursor-pointer transition-all",
                isSelected 
                  ? "bg-white/10 border-blue-500" 
                  : "bg-white/5 border-white/10 hover:bg-white/10"
              )}
            >
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="font-medium">{integration.name}</h4>
                  <p className="text-xs text-white/60">{integration.description}</p>
                </div>
                
                <div className={cn(
                  "w-5 h-5 rounded border-2 flex items-center justify-center",
                  isSelected 
                    ? "bg-blue-500 border-blue-500" 
                    : "border-white/30"
                )}>
                  {isSelected && <Check className="w-3 h-3 text-white" />}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className="bg-white/5 border border-white/10 rounded-lg p-4">
        <p className="text-sm text-white/60">
          ðŸ”— You can connect these integrations later in your dashboard settings. This just helps us recommend the best templates for you.
        </p>
      </div>
    </div>
  );
}

function CompleteStep({ data }: { data: OnboardingData }) {
  return (
    <div className="text-center space-y-8">
      <div className="w-20 h-20 bg-gradient-to-r from-green-500 to-emerald-500 rounded-full flex items-center justify-center mx-auto">
        <Check className="w-10 h-10 text-white" />
      </div>
      
      <div>
        <h2 className="text-4xl font-bold mb-4">You're all set! ðŸŽ‰</h2>
        <p className="text-xl text-white/80 mb-6">
          Welcome aboard, {data.firstName}! Your Bothive workspace is ready.
        </p>
        
        <div className="bg-white/5 border border-white/10 rounded-xl p-6 text-left max-w-md mx-auto">
          <h3 className="font-semibold mb-4">What's next:</h3>
          <ul className="space-y-2 text-sm text-white/80">
            <li className="flex items-center gap-2">
              <div className="w-1.5 h-1.5 bg-blue-400 rounded-full" />
              Explore your dashboard and create your first bot
            </li>
            <li className="flex items-center gap-2">
              <div className="w-1.5 h-1.5 bg-blue-400 rounded-full" />
              Connect your favorite tools and integrations
            </li>
            <li className="flex items-center gap-2">
              <div className="w-1.5 h-1.5 bg-blue-400 rounded-full" />
              Check out our templates and tutorials
            </li>
          </ul>
        </div>
