"use client";

import { useEffect, useMemo, useRef, useState } from "react";
import { motion } from "motion/react";
import DottedMap from "dotted-map";

import { useTheme } from "@/lib/theme-context";

interface MapProps {
    dots?: Array<{
        start: { lat: number; lng: number; label?: string };
        end: { lat: number; lng: number; label?: string };
    }>;
    lineColor?: string;
}

export function WorldMap({
                             dots = [],
                             lineColor = "#0ea5e9",
                         }: MapProps) {
    const svgRef = useRef<SVGSVGElement>(null);

    const { theme } = useTheme();
    const isDark = theme === "dark";
    const [svgMap, setSvgMap] = useState<string | null>(null);

    const map = useMemo(() => new DottedMap({ height: 100, grid: "diagonal" }), []);

    useEffect(() => {
        const generated = map.getSVG({
            radius: 0.22,
            color: isDark ? "#FFFFFF40" : "#00000040",
            shape: "circle",
            backgroundColor: isDark ? "black" : "white",
        });
        setSvgMap(generated);
    }, [map, isDark]);

    const projectPoint = (lat: number, lng: number) => {
        const x = (lng + 180) * (800 / 360);
        const y = (90 - lat) * (400 / 180);
        return { x, y };
    };

    const createCurvedPath = (
        start: { x: number; y: number },
        end: { x: number; y: number }
    ) => {
        const midX = (start.x + end.x) / 2;
        const midY = Math.min(start.y, end.y) - 50;
        return `M ${start.x} ${start.y} Q ${midX} ${midY} ${end.x} ${end.y}`;
    };

    return (
        <div className="w-full aspect-[2/1] dark:bg-black bg-white rounded-lg relative font-sans overflow-hidden">
            {svgMap && (
                <img
                    src={`data:image/svg+xml;utf8,${encodeURIComponent(svgMap)}`}
                    className="h-full w-full [mask-image:linear-gradient(to_bottom,transparent,white_10%,white_90%,transparent)] pointer-events-none select-none transition-colors duration-300"
                    alt="world map"
                    height="495"
                    width="1056"
                    draggable={false}
                />
            )}
            <svg
                ref={svgRef}
                viewBox="0 0 800 400"
                className="w-full h-full absolute inset-0 pointer-events-none select-none"
            >
                {dots.map((dot, i) => {
                    const startPoint = projectPoint(dot.start.lat, dot.start.lng);
                    const endPoint = projectPoint(dot.end.lat, dot.end.lng);
                    return (
                        <g key={`path-group-${i}`}>
                            <motion.path
                                d={createCurvedPath(startPoint, endPoint)}
                                fill="none"
                                stroke="url(#path-gradient)"
                                strokeWidth="1"
                                initial={{
                                    pathLength: 0,
                                }}
                                animate={{
                                    pathLength: 1,
                                }}
                                transition={{
                                    duration: 1,
                                    delay: 0.5 * i,
                                    ease: "easeOut",
                                }}
                                key={`start-upper-${i}`}
                            ></motion.path>
                        </g>
                    );
                })}

                <defs>
                    <linearGradient id="path-gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                        <stop offset="0%" stopColor="white" stopOpacity="0" />
                        <stop offset="5%" stopColor={lineColor} stopOpacity="1" />
                        <stop offset="95%" stopColor={lineColor} stopOpacity="1" />
                        <stop offset="100%" stopColor="white" stopOpacity="0" />
                    </linearGradient>
                </defs>

                {dots.map((dot, i) => (
                    <g key={`points-group-${i}`}>
                        <g key={`start-${i}`}>
                            <circle
                                cx={projectPoint(dot.start.lat, dot.start.lng).x}
                                cy={projectPoint(dot.start.lat, dot.start.lng).y}
                                r="2"
                                fill={lineColor}
                            />
                            <circle
                                cx={projectPoint(dot.start.lat, dot.start.lng).x}
                                cy={projectPoint(dot.start.lat, dot.start.lng).y}
                                r="2"
                                fill={lineColor}
                                opacity="0.5"
                            >
                                <animate
                                    attributeName="r"
                                    from="2"
                                    to="8"
                                    dur="1.5s"
                                    begin="0s"
                                    repeatCount="indefinite"
                                />
                                <animate
                                    attributeName="opacity"
                                    from="0.5"
                                    to="0"
                                    dur="1.5s"
                                    begin="0s"
                                    repeatCount="indefinite"
                                />
                            </circle>
                        </g>
                        <g key={`end-${i}`}>
                            <circle
                                cx={projectPoint(dot.end.lat, dot.end.lng).x}
                                cy={projectPoint(dot.end.lat, dot.end.lng).y}
                                r="2"
                                fill={lineColor}
                            />
                            <circle
                                cx={projectPoint(dot.end.lat, dot.end.lng).x}
                                cy={projectPoint(dot.end.lat, dot.end.lng).y}
                                r="2"
                                fill={lineColor}
                                opacity="0.5"
                            >
                                <animate
                                    attributeName="r"
                                    from="2"
                                    to="8"
                                    dur="1.5s"
                                    begin="0s"
                                    repeatCount="indefinite"
                                />
                                <animate
                                    attributeName="opacity"
                                    from="0.5"
                                    to="0"
                                    dur="1.5s"
                                    begin="0s"
                                    repeatCount="indefinite"
                                />
                            </circle>
                        </g>
                    </g>
                ))}
            </svg>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";

const screens = {
    sm: "640px",
    md: "768px",
    lg: "1024px",
    xl: "1280px",
    "2xl": "1536px",
};

/**
 * Checks whether a particular Tailwind CSS viewport size applies.
 *
 * @param size The size to check, which must either be included in Tailwind CSS's
 * list of default screen sizes, or added to the Tailwind CSS config file.
 *
 * @returns A boolean indicating whether the viewport size applies.
 */
export const useBreakpoint = (size: "sm" | "md" | "lg" | "xl" | "2xl") => {
    const [matches, setMatches] = useState(typeof window !== "undefined" ? window.matchMedia(`(min-width: ${screens[size]})`).matches : true);

    useEffect(() => {
        const breakpoint = window.matchMedia(`(min-width: ${screens[size]})`);

        setMatches(breakpoint.matches);

        const handleChange = (value: MediaQueryListEvent) => setMatches(value.matches);

        breakpoint.addEventListener("change", handleChange);
        return () => breakpoint.removeEventListener("change", handleChange);
    }, [size]);

    return matches;
};

/**
 * React hooks for bot management
 * Provides real-time data from the API
 */

import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';

export interface Bot {
    id: string;
    user_id: string;
    workspace_id: string | null;
    name: string;
    description: string | null;
    slug: string;
    icon_url: string | null;
    tags: string[];
    category: string;
    deployment_status: 'draft' | 'deploying' | 'active' | 'paused' | 'failed' | 'archived';
    total_runs: number;
    successful_runs: number;
    failed_runs: number;
    avg_execution_time_ms: number | null;
    is_public: boolean;
    rating: number | null;
    created_at: string;
    updated_at: string;
}

/**
 * Hook to fetch all bots
 */
export function useBots(options?: {
    category?: string;
    is_public?: boolean;
    limit?: number;
}) {
    const [bots, setBots] = useState<Bot[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        fetchBots();
    }, [options?.category, options?.is_public, options?.limit]);

    const fetchBots = async () => {
        try {
            setLoading(true);
            const params = new URLSearchParams();

            if (options?.category) params.append('category', options.category);
            if (options?.is_public !== undefined) params.append('is_public', String(options.is_public));
            if (options?.limit) params.append('limit', String(options.limit));

            const response = await fetch(`/api/bots?${params.toString()}`);

            if (!response.ok) {
                throw new Error('Failed to fetch bots');
            }

            const data = await response.json();
            setBots(data.bots);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    const createBot = async (botData: {
        name: string;
        code: string;
        description?: string;
        category?: string;
        tags?: string[];
        icon_url?: string;
    }) => {
        try {
            const response = await fetch('/api/bots', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(botData),
            });

            if (!response.ok) {
                throw new Error('Failed to create bot');
            }

            const data = await response.json();
            await fetchBots(); // Refresh list
            return data.bot;
        } catch (err) {
            throw err instanceof Error ? err : new Error('Unknown error');
        }
    };

    const updateBot = async (id: string, updates: Partial<Bot>) => {
        try {
            const response = await fetch('/api/bots', {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id, ...updates }),
            });

            if (!response.ok) {
                throw new Error('Failed to update bot');
            }

            const data = await response.json();
            await fetchBots(); // Refresh list
            return data.bot;
        } catch (err) {
            throw err instanceof Error ? err : new Error('Unknown error');
        }
    };

    const deleteBot = async (id: string) => {
        try {
            const response = await fetch('/api/bots', {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id }),
            });

            if (!response.ok) {
                throw new Error('Failed to delete bot');
            }

            await fetchBots(); // Refresh list
        } catch (err) {
            throw err instanceof Error ? err : new Error('Unknown error');
        }
    };

    return {
        bots,
        loading,
        error,
        refetch: fetchBots,
        createBot,
        updateBot,
        deleteBot,
    };
}

/**
 * Hook to fetch a single bot by ID
 */
export function useBot(botId: string | null) {
    const [bot, setBot] = useState<Bot | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        if (!botId) {
            setBot(null);
            setLoading(false);
            return;
        }

        fetchBot();
    }, [botId]);

    const fetchBot = async () => {
        if (!botId) return;

        try {
            setLoading(true);
            const { data, error: supabaseError } = await supabase
                .from('bots')
                .select('*')
                .eq('id', botId)
                .single();

            if (supabaseError) throw supabaseError;

            setBot(data);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    return { bot, loading, error, refetch: fetchBot };
}

/**
 * Hook to subscribe to real-time bot updates
 */
export function useRealtimeBots() {
    const { bots, loading, error, refetch } = useBots();
    const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

    useEffect(() => {
        // Subscribe to real-time changes
        const channel = supabase
            .channel('bots-changes')
            .on(
                'postgres_changes',
                {
                    event: '*',
                    schema: 'public',
                    table: 'bots',
                },
                (payload) => {
                    console.log('Bot change detected:', payload);
                    setLastUpdate(new Date());
                    refetch();
                }
            )
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, [refetch]);

    return { bots, loading, error, lastUpdate, refetch };
}

/**
 * React hooks for bot executions
 * Provides execution history and real-time status
 */

import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';

export interface BotExecution {
    id: string;
    bot_id: string;
    user_id: string | null;
    status: 'queued' | 'running' | 'completed' | 'failed' | 'cancelled' | 'timeout';
    input_data: any;
    output_data: any;
    error_data: any;
    execution_time_ms: number | null;
    tokens_used: number;
    estimated_cost_usd: number | null;
    created_at: string;
    completed_at: string | null;
}

/**
 * Hook to execute a bot
 */
export function useExecuteBot() {
    const [executing, setExecuting] = useState(false);
    const [execution, setExecution] = useState<BotExecution | null>(null);
    const [error, setError] = useState<Error | null>(null);

    const execute = async (botId: string, inputData?: any) => {
        try {
            setExecuting(true);
            setError(null);

            const response = await fetch('/api/execute', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    bot_id: botId,
                    input_data: inputData,
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to execute bot');
            }

            const data = await response.json();
            setExecution(data.execution);
            return data.execution;
        } catch (err) {
            const error = err instanceof Error ? err : new Error('Unknown error');
            setError(error);
            throw error;
        } finally {
            setExecuting(false);
        }
    };

    return { execute, executing, execution, error };
}

/**
 * Hook to fetch execution details
 */
export function useExecution(executionId: string | null) {
    const [execution, setExecution] = useState<BotExecution | null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        if (!executionId) {
            setExecution(null);
            return;
        }

        fetchExecution();
    }, [executionId]);

    const fetchExecution = async () => {
        if (!executionId) return;

        try {
            setLoading(true);
            const response = await fetch(`/api/execute?id=${executionId}`);

            if (!response.ok) {
                throw new Error('Failed to fetch execution');
            }

            const data = await response.json();
            setExecution(data.execution);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    return { execution, loading, error, refetch: fetchExecution };
}

/**
 * Hook to fetch recent executions
 */
export function useRecentExecutions(limit: number = 10) {
    const [executions, setExecutions] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        fetchExecutions();
    }, [limit]);

    const fetchExecutions = async () => {
        try {
            setLoading(true);
            const { data, error: supabaseError } = await supabase
                .from('bot_executions')
                .select('*, bots(name, slug)')
                .order('created_at', { ascending: false })
                .limit(limit);

            if (supabaseError) throw supabaseError;

            setExecutions(data || []);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    return { executions, loading, error, refetch: fetchExecutions };
}

/**
 * Hook to subscribe to real-time execution updates
 */
export function useRealtimeExecution(executionId: string | null) {
    const { execution, loading, error, refetch } = useExecution(executionId);
    const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

    useEffect(() => {
        if (!executionId) return;

        // Subscribe to real-time changes for this execution
        const channel = supabase
            .channel(`execution-${executionId}`)
            .on(
                'postgres_changes',
                {
                    event: '*',
                    schema: 'public',
                    table: 'bot_executions',
                    filter: `id=eq.${executionId}`,
                },
                (payload) => {
                    console.log('Execution update:', payload);
                    setLastUpdate(new Date());
                    refetch();
                }
            )
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, [executionId, refetch]);

    return { execution, loading, error, lastUpdate, refetch };
}

/**
 * Hook to subscribe to all executions in real-time
 */
export function useRealtimeExecutions(limit: number = 10) {
    const { executions, loading, error, refetch } = useRecentExecutions(limit);
    const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

    useEffect(() => {
        // Subscribe to real-time changes
        const channel = supabase
            .channel('executions-changes')
            .on(
                'postgres_changes',
                {
                    event: '*',
                    schema: 'public',
                    table: 'bot_executions',
                },
                (payload) => {
                    console.log('Executions update:', payload);
                    setLastUpdate(new Date());
                    refetch();
                }
            )
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, [refetch]);

    return { executions, loading, error, lastUpdate, refetch };
}

/**
 * React hooks for gamification features
 * Provides XP, achievements, and leaderboard data
 */

import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';

export interface UserGamification {
    user_id: string;
    level: number;
    xp: number;
    total_xp: number;
    streak_days: number;
    longest_streak: number;
    bots_created: number;
    bots_deployed: number;
    total_executions: number;
    achievements_unlocked: number;
}

export interface Achievement {
    id: string;
    slug: string;
    name: string;
    description: string | null;
    icon_url: string | null;
    category: 'creation' | 'deployment' | 'execution' | 'social' | 'milestone' | 'special';
    xp_reward: number;
    tier: 'bronze' | 'silver' | 'gold' | 'platinum' | 'diamond';
}

export interface UserAchievement {
    id: string;
    user_id: string;
    achievement_id: string;
    unlocked_at: string;
    achievement?: Achievement;
}

/**
 * Hook to fetch user's gamification profile
 */
export function useGamification(userId?: string) {
    const [profile, setProfile] = useState<UserGamification | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);
    const [progress, setProgress] = useState({
        current_xp: 0,
        xp_to_next_level: 100,
        percentage: 0,
    });
    const [rank, setRank] = useState<number | null>(null);

    useEffect(() => {
        fetchGamification();
    }, [userId]);

    const fetchGamification = async () => {
        try {
            setLoading(true);
            const params = userId ? `?user_id=${userId}` : '';
            const response = await fetch(`/api/gamification${params}`);

            if (!response.ok) {
                throw new Error('Failed to fetch gamification data');
            }

            const data = await response.json();
            setProfile(data.profile);
            setProgress(data.progress);
            setRank(data.rank);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    const awardXP = async (xp_amount: number, reason?: string) => {
        try {
            const response = await fetch('/api/gamification', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ xp_amount, reason }),
            });

            if (!response.ok) {
                throw new Error('Failed to award XP');
            }

            const data = await response.json();
            await fetchGamification(); // Refresh profile
            return data;
        } catch (err) {
            throw err instanceof Error ? err : new Error('Unknown error');
        }
    };

    return {
        profile,
        progress,
        rank,
        loading,
        error,
        refetch: fetchGamification,
        awardXP,
    };
}

/**
 * Hook to fetch achievements
 */
export function useAchievements(userId?: string) {
    const [achievements, setAchievements] = useState<Achievement[] | UserAchievement[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        fetchAchievements();
    }, [userId]);

    const fetchAchievements = async () => {
        try {
            setLoading(true);
            const params = userId ? `?user_id=${userId}` : '';
            const response = await fetch(`/api/achievements${params}`);

            if (!response.ok) {
                throw new Error('Failed to fetch achievements');
            }

            const data = await response.json();
            setAchievements(data.achievements);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    const unlockAchievement = async (achievement_slug: string) => {
        try {
            const response = await fetch('/api/achievements', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ achievement_slug }),
            });

            if (!response.ok) {
                throw new Error('Failed to unlock achievement');
            }

            const data = await response.json();
            await fetchAchievements(); // Refresh list
            return data;
        } catch (err) {
            throw err instanceof Error ? err : new Error('Unknown error');
        }
    };

    return {
        achievements,
        loading,
        error,
        refetch: fetchAchievements,
        unlockAchievement,
    };
}

/**
 * Hook to fetch leaderboard
 */
export function useLeaderboard(limit: number = 100) {
    const [leaderboard, setLeaderboard] = useState<any[]>([]);
    const [userRank, setUserRank] = useState<number | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<Error | null>(null);

    useEffect(() => {
        fetchLeaderboard();
    }, [limit]);

    const fetchLeaderboard = async () => {
        try {
            setLoading(true);
            const response = await fetch(`/api/leaderboard?limit=${limit}`);

            if (!response.ok) {
                throw new Error('Failed to fetch leaderboard');
            }

            const data = await response.json();
            setLeaderboard(data.leaderboard);
            setUserRank(data.user_rank);
            setError(null);
        } catch (err) {
            setError(err instanceof Error ? err : new Error('Unknown error'));
        } finally {
            setLoading(false);
        }
    };

    return {
        leaderboard,
        userRank,
        loading,
        error,
        refetch: fetchLeaderboard,
    };
}

/**
 * Hook to subscribe to real-time gamification updates
 */
export function useRealtimeGamification(userId: string) {
    const { profile, loading, error, refetch } = useGamification(userId);
    const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

    useEffect(() => {
        if (!userId) return;

        // Subscribe to real-time changes
        const channel = supabase
            .channel('gamification-changes')
            .on(
                'postgres_changes',
                {
                    event: '*',
                    schema: 'public',
                    table: 'user_gamification',
                    filter: `user_id=eq.${userId}`,
                },
                (payload) => {
                    console.log('Gamification update:', payload);
                    setLastUpdate(new Date());
                    refetch();
                }
            )
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, [userId, refetch]);

    return { profile, loading, error, lastUpdate, refetch };
}

